INSERT INTO `artifactset` VALUES (31, '千岩牢固', '[4,5]', '生命值提升20%', '元素战技命中敌人后，使队伍中附近的所有角色攻击力提升20%，护盾强效提升30%，持续3秒。该效果每0.5秒至多触发一次。装备此圣遗物套装的角色处于队伍后台时，依然能触发该效果。', '[\"山脊守望\"]', '[]', '{\"Flower\":\"/ArtifactSet/勋绩之花.png\",\"Plume\":\"/ArtifactSet/昭武翎羽.png\",\"Sands\":\"/ArtifactSet/金铜时晷.png\",\"Goblet\":\"/ArtifactSet/盟誓金爵.png\",\"Circlet\":\"/ArtifactSet/将帅兜鍪.png\"}');
