INSERT INTO `breakthrough` VALUES (26, 'Jewels', '坚牢黄玉断片', '3', '/AscensionMaterial/Jewels/坚牢黄玉断片.png', '角色培养素材', '\"角色突破素材。<br>「大陆所流通的一切银钱皆是我的血肉。」\"', '/img/upgrade/gem/i_362', '[\"40级以上无相之岩掉落\",\"40级以上黄金王兽掉落\",\"40级以上遗迹巨蛇掉落\",\"合成获得\"]', '[\"无相之岩Lv 40+\",\"黄金王兽Lv 40+\",\"恒常机关阵列Lv 40+\",\"古岩龙蜥Lv 40+\",\"北风的王狼，奔狼的领主Lv 40+\",\"若陀龙王Lv 40+\"]', '[\"「伏龙树」之底\",\"盐中之谜\",\"「伏龙树」之底\",\"浮浪人藏身处\"]', '坚牢黄玉碎屑x 3', '坚牢黄玉块 = 坚牢黄玉断片x 3', '[\"ningguang\",\"noelle\",\"zhongli\",\"albedo\",\"gorou\",\"itto\",\"yunjin\"]', NULL);
INSERT INTO `breakthrough` VALUES (27, 'Jewels', '坚牢黄玉块', '4', '/AscensionMaterial/Jewels/坚牢黄玉块.png', '角色培养素材', '\"角色突破素材。<br>「大陆所流通的一切银钱皆是我的血肉。<br>「我在以这样的方式，为人类一切的汗水、智慧和未来做担保。」\"', '/img/upgrade/gem/i_363', '[\"60级以上无相之岩掉落\",\"60级以上黄金王兽掉落\",\"60级以上遗迹巨蛇掉落\",\"合成获得\"]', '[\"无相之岩Lv 60+\",\"黄金王兽Lv 60+\",\"恒常机关阵列Lv 60+\",\"古岩龙蜥Lv 60+\",\"北风的王狼，奔狼的领主Lv 60+\",\"若陀龙王Lv 60+\"]', '[\"「伏龙树」之底\"]', '坚牢黄玉断片x 3', '坚牢黄玉 = 坚牢黄玉块x 3', '[\"ningguang\",\"noelle\",\"zhongli\",\"albedo\",\"gorou\",\"itto\",\"yunjin\"]', NULL);
INSERT INTO `breakthrough` VALUES (28, 'Jewels', '坚牢黄玉', '5', '/AscensionMaterial/Jewels/坚牢黄玉.png', '角色培养素材', '\"角色突破素材。<br>「大陆所流通的一切银钱皆是我的血肉。<br>「我在以这样的方式，为人类一切的汗水、智慧和未来做担保。<br>「这是我对众生的信任，辜负了它就是玷污了我的血。」\"', '/img/upgrade/gem/i_364', '[\"75级以上无相之岩掉落\",\"75级以上黄金王兽掉落\",\"75级以上遗迹巨蛇掉落\",\"合成获得\"]', '[\"无相之岩Lv 75+\",\"黄金王兽Lv 75+\",\"恒常机关阵列Lv 75+\",\"古岩龙蜥Lv 75+\",\"北风的王狼，奔狼的领主Lv 75+\",\"若陀龙王Lv 75+\"]', '[\"「伏龙树」之底\"]', '坚牢黄玉块x 3', NULL, '[\"ningguang\",\"noelle\",\"zhongli\",\"albedo\",\"gorou\",\"itto\",\"yunjin\"]', NULL);
INSERT INTO `breakthrough` VALUES (29, 'Jewels', '生长碧翡碎屑', '2', '/AscensionMaterial/Jewels/生长碧翡碎屑.png', '角色培养素材', '\"角色突破素材。\"', '/img/upgrade/gem/i_371', '[\"测试\"]', '[]', '[]', NULL, NULL, '[]', NULL);
INSERT INTO `breakthrough` VALUES (30, 'Jewels', '生长碧翡断片', '3', '/AscensionMaterial/Jewels/生长碧翡断片.png', '角色培养素材', '\"角色突破素材。<br>「生命最后放开了手…」\"', '/img/upgrade/gem/i_372', '[\"测试\"]', '[]', '[]', NULL, NULL, '[]', NULL);
INSERT INTO `breakthrough` VALUES (31, 'Jewels', '生长碧翡块', '4', '/AscensionMaterial/Jewels/生长碧翡块.png', '角色培养素材', '\"角色突破素材。<br>「生命最后放开了手…<br>「施与众生自由。」\"', '/img/upgrade/gem/i_373', '[\"测试\"]', '[]', '[]', NULL, NULL, '[]', NULL);
INSERT INTO `breakthrough` VALUES (32, 'Jewels', '生长碧翡', '5', '/AscensionMaterial/Jewels/生长碧翡.png', '角色培养素材', '\"角色突破素材。<br>「生命最后放开了手…<br>「施与众生自由。<br>「人与动物们都能表达自己，草木却只有沉默。所以它们才需要一位代言。」\"', '/img/upgrade/gem/i_374', '[\"测试\"]', '[]', '[]', NULL, NULL, '[]', NULL);
INSERT INTO `breakthrough` VALUES (49, 'ElementalStones', '常燃火种', '4', '/AscensionMaterial/ElementalStones/常燃火种.png', '角色培养素材', '\"驱动爆炎树持续燃烧的种子。仿佛要点燃世间的一切草木般放射着火元素的热量。<br>大地深处湍流不息的能量中运载着无穷的愿望。挟着烈火凭依在植物上，使它恒常地燃烧，并且渴望用爆裂的火炎洗净一切的，又是什么样的愿望呢。\"', '/img/upgrade/gem/i_201', '[\"30级以上爆炎树掉落\"]', '[\"爆炎树Lv 30+\"]', NULL, NULL, NULL, '[\"amber\",\"bennett\",\"diluc\",\"klee\",\"xiangling\",\"xinyan\"]', NULL);
INSERT INTO `breakthrough` VALUES (50, 'ElementalStones', '净水之心', '4', '/AscensionMaterial/ElementalStones/净水之心.png', '角色培养素材', '\"纯水精灵留下的永远透彻的一掬水。<br>即使溶在了湖泊中，它的纯净也不会被稀释吧。但高洁如此的纯水精灵，又是为何渴望着地上生灵的模样，以净水模拟成生物的姿态呢，即使不需要呼吸、也不需要进食…\"', '/img/upgrade/gem/i_202', '[\"30级以上纯水精灵掉落\"]', '[\"纯水精灵Lv 30+\"]', NULL, NULL, NULL, '[\"barbara\",\"mona\",\"xingqiu\",\"tartaglia\"]', NULL);
INSERT INTO `breakthrough` VALUES (51, 'ElementalStones', '雷光棱镜', '4', '/AscensionMaterial/ElementalStones/雷光棱镜.png', '角色培养素材', '\"无相之雷用于汇聚元素，修复损伤的元素物质。其中蕴含着至纯的雷之力。<br>棱镜能将构成光的色彩区分，而雷光棱镜将奔流的能量编织成纯粹的雷光。即使无相之雷已经被击败，雷元素也持续在这只棱镜中汇聚。\"', '/img/upgrade/gem/i_203', '[\"30级以上无相之雷掉落\"]', '[\"无相之雷Lv 30+\"]', NULL, NULL, NULL, '[\"beidou\",\"fischl\",\"keqing\",\"lisa\",\"razor\"]', NULL);
INSERT INTO `breakthrough` VALUES (52, 'ElementalStones', '极寒之核', '4', '/AscensionMaterial/ElementalStones/极寒之核.png', '角色培养素材', '\"急冻树被击败后，裸露出的原核。其中蕴含着纯净的冰元素。<br>在大地深处中奔涌的能量与思念终归会喷发。就连哀思般沉寂的寒霜，也会附着在地面上的生命形态当中；即使原本不该狩猎生灵的藤蔓植物，也会变得庞大而凶悍吧。\"', '/img/upgrade/gem/i_204', '[\"30级以上急冻树掉落\"]', '[\"急冻树Lv 30+\"]', NULL, NULL, NULL, '[\"chongyun\",\"kaeya\",\"qiqi\",\"diona\",\"ganyu\",\"rosaria\"]', NULL);
INSERT INTO `breakthrough` VALUES (53, 'ElementalStones', '飓风之种', '4', '/AscensionMaterial/ElementalStones/飓风之种.png', '角色培养素材', '\"无相之风被击败后，所坍缩成的形态。其中蕴含着至纯的风之力。<br>传说在纤细脆弱的蝴蝶体内，也藏有引发飓风的力量。这由纯粹的风构成的蝴蝶，无疑在等待着再次召来暴风的那一天吧。\"', '/img/upgrade/gem/i_205', '[\"30级以上无相之风掉落\"]', '[\"无相之风Lv 30+\"]', NULL, NULL, NULL, '[\"jean\",\"sucrose\",\"venti\"]', NULL);
INSERT INTO `breakthrough` VALUES (54, 'ElementalStones', '玄岩之塔', '4', '/AscensionMaterial/ElementalStones/玄岩之塔.png', '角色培养素材', '\"无相之岩的外壳构成的石柱。由纯粹的岩元素高度浓缩而成。<br>岩元素是最为沉重的元素。无相之岩将外壳筑成高耸的岩柱，将自己抬升至高处，或许不仅是为了应敌。或许也是为了更加接近与它无缘的天空吧。\"', '/img/upgrade/gem/i_206', '[\"30级以上无相之岩掉落\"]', '[\"无相之岩Lv 30+\"]', NULL, NULL, NULL, '[\"ningguang\",\"noelle\",\"zhongli\",\"albedo\"]', NULL);
INSERT INTO `breakthrough` VALUES (55, 'ElementalStones', '未熟之玉', '4', '/AscensionMaterial/ElementalStones/未熟之玉.png', '角色培养素材', '\"从古岩龙蜥处取来的结晶物质，蕴含着成为龙的可能性。<br>沉眠在山岳中的龙蜥体内，会析出这种黯淡的晶石。璃月有坊间传说认为，力量随着年月积聚，而这种晶石最终会取代原本的心脏，而龙蜥也会成为真正的龙，撼动山岳、撕裂大地。\"', '/img/upgrade/gem/i_207', '[\"30级以上古岩龙蜥掉落\"]', '[\"古岩龙蜥Lv 30+\"]', NULL, NULL, NULL, '[\"xiao\",\"hutao\",\"feiyan\"]', NULL);
INSERT INTO `breakthrough` VALUES (56, 'ElementalStones', '晶凝之华', '4', '/AscensionMaterial/ElementalStones/晶凝之华.png', '角色培养素材', '\"无相之冰绽开的极寒瞬间，凝结而出的至纯冰晶。<br>冰霜的结晶永远依凭着冰冷的定则生长、盛放，最终开出冻结一切的凛冬之花。或许滞顿的时间，无法解冻的幽怀，才应当是永存不灭之花的真谛。\"', '/img/upgrade/gem/i_208', '[\"30级以上无相之冰掉落\"]', '[\"无相之冰Lv 30+\"]', NULL, NULL, NULL, '[\"eula\",\"aloy\"]', NULL);
INSERT INTO `breakthrough` VALUES (57, 'ElementalStones', '魔偶机心', '4', '/AscensionMaterial/ElementalStones/魔偶机心.png', '角色培养素材', '\"能够驱动无机质的机关魔偶剑鬼，使它如同生者一般挥舞刀剑的核心。<br>无论是如何精湛的武艺、多么美丽的记忆，也会随着肉体的衰败而凋零。<br>恐怕只有将自己的四肢、躯体都换成牢固的机关，最终将心脏换成精密的「机心」，才能比凡骨肉胎更加接近永恒吧…\"', '/img/upgrade/gem/i_210', '[\"30级以上魔偶剑鬼掉落\"]', '[\"魔偶剑鬼Lv 30+\"]', NULL, NULL, NULL, '[\"kazuha\",\"sayu\"]', NULL);
INSERT INTO `breakthrough` VALUES (58, 'ElementalStones', '恒常机关之心', '4', '/AscensionMaterial/ElementalStones/恒常机关之心.png', '角色培养素材', '\"驱使恒常机关阵列自律行动的核心。<br>有人推测这种核心上的纹样当中以肉眼不可见的语言写上了智能与逻辑，也有人进一步认为这种特殊的遗迹机关因为格外庞大的核心与复杂的纹样，自发选择抛弃了生物的姿态，转而追求性能的强大。\"', '/img/upgrade/gem/i_211', '[\"30级以上恒常机关阵列掉落\"]', '[\"恒常机关阵列Lv 30+\"]', NULL, NULL, NULL, '[\"ayaka\",\"gorou\"]', NULL);
INSERT INTO `breakthrough` VALUES (59, 'ElementalStones', '阴燃之珠', '4', '/AscensionMaterial/ElementalStones/阴燃之珠.png', '角色培养素材', '\"无相之火坍缩成的结晶，不断散发着热量。<br>炽热的火焰终有一天会冷却，而熄灭的柴薪也会随着意志而再度熊熊燃烧。无相之火不断的冷却与复燃、消散与凝聚也是这个世界意志的一部分吧，就如同宇宙万物的生生不息。直到漆黑的命运终有一日降临，将森罗万象归于空无为止。\"', '/img/upgrade/gem/i_212', '[\"30级以上无相之火掉落\"]', '[\"无相之火Lv 30+\"]', NULL, NULL, NULL, '[\"yoimiya\",\"thoma\"]', NULL);
INSERT INTO `breakthrough` VALUES (60, 'ElementalStones', '排异之露', '4', '/AscensionMaterial/ElementalStones/排异之露.png', '角色培养素材', '\"无相之水坍缩后，留下的奇异水珠。<br>具有强烈的排异本能，也是无相之水的攻击手段之一。据说水中蕴含着记忆与意志，也能随水体的融合获得成长。然而无相之水唤出的浪潮，与地上地下一切湍流之物皆不相容。哪怕落入佩特莉可那无比纯粹的水中，这滴水也会像水银一般抗拒着周遭的融合吧。\"', '/img/upgrade/gem/i_213', '[\"30级以上无相之水掉落\"]', '[\"无相之水Lv 30+\"]', NULL, NULL, NULL, '[\"kokomi\",\"ayato\"]', NULL);
INSERT INTO `breakthrough` VALUES (61, 'ElementalStones', '雷霆数珠', '4', '/AscensionMaterial/ElementalStones/雷霆数珠.png', '角色培养素材', '\"雷音权现留下的，蕴含电气的雷之珠。<br>传说雷音权现只会随着雷霆降生在遗留着强大忿恨的土地上。驱使它永远地徘徊在破碎的土地上，释放贯天之雷的，在歌人的故事中往往是生命无法承受的失望与痛苦。\"', '/img/upgrade/gem/i_214', '[\"30级以上雷音权现掉落\"]', '[\"雷音权现Lv 30+\"]', NULL, NULL, NULL, '[\"raiden\",\"sara\"]', NULL);
INSERT INTO `breakthrough` VALUES (62, 'ElementalStones', '兽境王器', '4', '/AscensionMaterial/ElementalStones/兽境王器.png', '角色培养素材', '\"战胜王兽后，得到的折断犄角。在它遭到驱逐后，不应残留在此世才对。<br>即使在兽境群狼中贵为王兽，在魔兽的系谱中也不及其兄长们的万分之一吧。<br>「黄金」将无数兽境的群狼如同将弃置的画纸一般压缩后而意外产生的畸形魔兽，而寄寓在其犄角中的是足以号令黑色的狼群为它溶解空间的权威。\"', '/img/upgrade/gem/i_215', '[\"30级以上黄金王兽掉落\"]', '[\"黄金王兽Lv 30+\"]', NULL, NULL, NULL, '[\"itto\",\"yunjin\"]', NULL);
INSERT INTO `breakthrough` VALUES (63, 'ElementalStones', '龙嗣伪鳍', '4', '/AscensionMaterial/ElementalStones/龙嗣伪鳍.png', '角色培养素材', '\"击败深海龙蜥之后，可以拾取到的一截生物组织。<br>虽然看起来像是鲨鳍，但其实并不发挥什么作用。<br>曾经的 白夜国之民对深海龙蜥有深入的研究。它们最为不同的一点就是可以靠意志决定自身特征的去留与遗传。因为厌恶人造光源，龙蜥成年就会失去视觉上的大部分细节；同时为了战斗，它们进化出了其他元素的行为多型性。\"', '/img/upgrade/gem/i_216', '[\"30级以上深海龙蜥之群挑战奖励\"]', '[\"此心安处 (x2)\"]', NULL, NULL, NULL, '[\"shenhe\",\"yaemiko\"]', NULL);
INSERT INTO `breakthrough` VALUES (64, 'CommonMaterial', '史莱姆凝液', '1', '/AscensionMaterial/CommonMaterial/史莱姆凝液.png', '角色培养素材', '\"覆于史莱姆体表的浓稠流体，各地元素工坊中最常见的原料。\"', '/img/upgrade/material/i_21', '[\"史莱姆掉落\"]', '[\"草史莱姆Lv 0+\",\"大型草史莱姆Lv 0+\",\"风史莱姆Lv 0+\",\"大型风史莱姆Lv 0+\",\"雷史莱姆Lv 0+\",\"大型雷史莱姆Lv 0+\",\"变异雷史莱姆Lv 0+\",\"冰史莱姆Lv 0+\",\"大型冰史莱姆Lv 0+\",\"水史莱姆Lv 0+\",\"大型水史莱姆Lv 0+\",\"火史莱姆Lv 0+\",\"大型火史莱姆Lv 0+\",\"岩史莱姆Lv 0+\",\"大型岩史莱姆Lv 0+\"]', NULL, '史莱姆清 = 史莱姆凝液x 3', NULL, '[\"lisa\",\"venti\",\"xiangling\",\"xiao\",\"zhongli\",\"itto\"]', '[\"黎明神剑\",\"黑剑\",\"笛剑\",\"天空之刃\",\"训练大剑\",\"佣兵重剑\",\"白铁大剑\",\"宗室大剑\",\"雪葬的星银\",\"衔珠海皇\",\"天空之傲\",\"西风长枪\",\"护摩之杖\",\"神射手之誓\",\"暗巷猎手\",\"祭礼弓\",\"阿莫斯之弓\",\"魔导绪论\",\"万国诸海图谱\",\"四风原典\"]');
