INSERT INTO `breakthrough` VALUES (67, 'CommonMaterial', '破损的面具', '1', '/AscensionMaterial/CommonMaterial/破损的面具.png', '角色培养素材', '\"破碎的骨质面具，曾经属于某个丘丘人。<br>破损的部分比完整的部分更多，已经失去了假面应有的功能。\"', '/img/upgrade/material/i_31', '[\"丘丘人掉落\",\"射手类丘丘人少量掉落\",\"丘丘萨满少量掉落\",\"大体格丘丘人少量掉落\"]', '[\"丘丘人Lv 0+\",\"打手丘丘人Lv 0+\",\"木盾丘丘人Lv 0+\",\"射手丘丘人Lv 0+\",\"火箭丘丘人Lv 0+\",\"爆弹丘丘人Lv 0+\",\"冲锋丘丘人Lv 0+\",\"冰箭丘丘人Lv 0+\",\"雷箭丘丘人Lv 0+\",\"岩盾丘丘人Lv 0+\",\"冰弹丘丘人Lv 0+\",\"冰盾丘丘人Lv 0+\",\"雷弹丘丘人Lv 0+\",\"木盾丘丘暴徒Lv 0+\",\"火斧丘丘暴徒Lv 0+\",\"岩盾丘丘暴徒Lv 0+\",\"丘丘霜铠王Lv 0+\",\"丘丘岩盔王Lv 0+\",\"冰盾丘丘暴徒Lv 0+\",\"雷斧丘丘暴徒Lv 0+\",\"丘丘雷兜王Lv 0+\",\"水丘丘萨满Lv 0+\",\"草丘丘萨满Lv 0+\",\"风丘丘萨满Lv 0+\",\"岩丘丘萨满Lv 0+\",\"冰丘丘萨满Lv 0+\",\"雷丘丘萨满Lv 0+\"]', NULL, '污秽的面具 = 破损的面具x 3', NULL, '[\"chongyun\",\"jean\",\"noelle\",\"razor\",\"traveler_boy_anemo\",\"traveler_boy_geo\",\"xingqiu\",\"eula\",\"traveler_girl_anemo\",\"traveler_girl_geo\",\"traveler_boy_electro\",\"traveler_girl_electro\",\"sara\",\"yunjin\"]', '[\"暗铁剑\",\"宗室长剑\",\"辰砂之纺锤\",\"斫峰之刃\",\"以理服人\",\"试作古华\",\"松籁响起之时\",\"试作星镰\",\"弹弓\",\"弓藏\",\"陨龙之梦\",\"学徒笔记\",\"口袋魔导书\",\"异世界行记\",\"流浪乐章\",\"昭心\",\"嘟嘟可故事集\",\"尘世之锁\"]');
INSERT INTO `breakthrough` VALUES (68, 'CommonMaterial', '污秽的面具', '2', '/AscensionMaterial/CommonMaterial/污秽的面具.png', '角色培养素材', '\"沾满了难以辨认的污渍，散发着微妙气味的骨质面具。<br>即使这样也要戴在脸上，这就是丘丘人的执着。\"', '/img/upgrade/material/i_32', '[\"合成获得\",\"40级以上射手类丘丘人少量掉落\",\"40级以上丘丘萨满少量掉落\",\"40级以上大体格丘丘人少量掉落\",\"合成获得\",\"星尘兑换\"]', '[\"丘丘人Lv 40+\",\"打手丘丘人Lv 40+\",\"木盾丘丘人Lv 40+\",\"射手丘丘人Lv 40+\",\"火箭丘丘人Lv 40+\",\"爆弹丘丘人Lv 40+\",\"冲锋丘丘人Lv 40+\",\"冰箭丘丘人Lv 40+\",\"雷箭丘丘人Lv 40+\",\"岩盾丘丘人Lv 40+\",\"冰弹丘丘人Lv 40+\",\"冰盾丘丘人Lv 40+\",\"雷弹丘丘人Lv 40+\",\"木盾丘丘暴徒Lv 40+\",\"火斧丘丘暴徒Lv 40+\",\"岩盾丘丘暴徒Lv 40+\",\"丘丘霜铠王Lv 40+\",\"丘丘岩盔王Lv 40+\",\"冰盾丘丘暴徒Lv 40+\",\"雷斧丘丘暴徒Lv 40+\",\"丘丘雷兜王Lv 40+\",\"水丘丘萨满Lv 40+\",\"草丘丘萨满Lv 40+\",\"风丘丘萨满Lv 40+\",\"岩丘丘萨满Lv 40+\",\"冰丘丘萨满Lv 40+\",\"雷丘丘萨满Lv 40+\"]', NULL, '破损的面具x 3', '不祥的面具 = 污秽的面具x 3', '[\"chongyun\",\"jean\",\"noelle\",\"razor\",\"traveler_boy_anemo\",\"traveler_boy_geo\",\"xingqiu\",\"eula\",\"traveler_girl_anemo\",\"traveler_girl_geo\",\"traveler_boy_electro\",\"traveler_girl_electro\",\"sara\",\"yunjin\"]', '[\"暗铁剑\",\"宗室长剑\",\"辰砂之纺锤\",\"斫峰之刃\",\"以理服人\",\"试作古华\",\"松籁响起之时\",\"试作星镰\",\"弹弓\",\"弓藏\",\"陨龙之梦\",\"学徒笔记\",\"口袋魔导书\",\"异世界行记\",\"流浪乐章\",\"昭心\",\"嘟嘟可故事集\",\"尘世之锁\"]');
INSERT INTO `breakthrough` VALUES (69, 'CommonMaterial', '不祥的面具', '3', '/AscensionMaterial/CommonMaterial/不祥的面具.png', '角色培养素材', '\"光亮的骨质面具，上面涂绘着意在恐吓敌人的油彩。<br>丘丘人对面具的执着缘起为何，至今无人能解。一说认为是不愿看见自己在水中的倒影。\"', '/img/upgrade/material/i_33', '[\"合成获得\",\"60级以上射手类丘丘人少量掉落\",\"60级以上丘丘萨满少量掉落\",\"60级以上大体格丘丘人少量掉落\",\"合成获得\"]', '[\"丘丘人Lv 60+\",\"打手丘丘人Lv 60+\",\"木盾丘丘人Lv 60+\",\"射手丘丘人Lv 60+\",\"火箭丘丘人Lv 60+\",\"爆弹丘丘人Lv 60+\",\"冲锋丘丘人Lv 60+\",\"冰箭丘丘人Lv 60+\",\"雷箭丘丘人Lv 60+\",\"岩盾丘丘人Lv 60+\",\"冰弹丘丘人Lv 60+\",\"冰盾丘丘人Lv 60+\",\"雷弹丘丘人Lv 60+\",\"木盾丘丘暴徒Lv 60+\",\"火斧丘丘暴徒Lv 60+\",\"岩盾丘丘暴徒Lv 60+\",\"丘丘霜铠王Lv 60+\",\"丘丘岩盔王Lv 60+\",\"冰盾丘丘暴徒Lv 60+\",\"雷斧丘丘暴徒Lv 60+\",\"丘丘雷兜王Lv 60+\",\"水丘丘萨满Lv 60+\",\"草丘丘萨满Lv 60+\",\"风丘丘萨满Lv 60+\",\"岩丘丘萨满Lv 60+\",\"冰丘丘萨满Lv 60+\",\"雷丘丘萨满Lv 60+\"]', NULL, '污秽的面具x 3', NULL, '[\"chongyun\",\"jean\",\"noelle\",\"razor\",\"traveler_boy_anemo\",\"traveler_boy_geo\",\"xingqiu\",\"eula\",\"traveler_girl_anemo\",\"traveler_girl_geo\",\"traveler_boy_electro\",\"traveler_girl_electro\",\"sara\",\"yunjin\"]', '[\"暗铁剑\",\"宗室长剑\",\"辰砂之纺锤\",\"斫峰之刃\",\"以理服人\",\"试作古华\",\"松籁响起之时\",\"试作星镰\",\"弹弓\",\"弓藏\",\"陨龙之梦\",\"学徒笔记\",\"口袋魔导书\",\"异世界行记\",\"流浪乐章\",\"昭心\",\"嘟嘟可故事集\",\"尘世之锁\"]');
INSERT INTO `breakthrough` VALUES (70, 'CommonMaterial', '导能绘卷', '1', '/AscensionMaterial/CommonMaterial/导能绘卷.png', '角色培养素材', '\"大抵是某种关于魔法的绘卷残篇。隐隐地散发着某种异样的、不详的温暖。\"', '/img/upgrade/material/i_41', '[\"丘丘萨满掉落\"]', '[\"水丘丘萨满Lv 0+\",\"草丘丘萨满Lv 0+\",\"风丘丘萨满Lv 0+\",\"岩丘丘萨满Lv 0+\",\"冰丘丘萨满Lv 0+\",\"雷丘丘萨满Lv 0+\"]', NULL, '封魔绘卷 = 导能绘卷x 3', NULL, '[\"barbara\",\"klee\",\"qiqi\",\"albedo\",\"yoimiya\"]', '[\"旅行剑\",\"祭礼剑\",\"暗巷闪光\",\"苍古自由之誓\",\"石英大剑\",\"雨裁\",\"狼的末路\",\"新手长枪\",\"铁尖枪\",\"匣里灭辰\",\"天空之脊\",\"反曲弓\",\"宗室长弓\",\"落霞\",\"讨龙英杰谭\",\"黑岩绯玉\",\"西风秘典\",\"白辰之环\"]');
INSERT INTO `breakthrough` VALUES (71, 'CommonMaterial', '封魔绘卷', '2', '/AscensionMaterial/CommonMaterial/封魔绘卷.png', '角色培养素材', '\"模糊地绘制了难解形象的古书断章。通过模仿其中的人物形象，一些魔物能小小地再现魔法的奇迹。\"', '/img/upgrade/material/i_42', '[\"合成获得\"]', '[\"水丘丘萨满Lv 40+\",\"草丘丘萨满Lv 40+\",\"风丘丘萨满Lv 40+\",\"岩丘丘萨满Lv 40+\",\"冰丘丘萨满Lv 40+\",\"雷丘丘萨满Lv 40+\"]', NULL, '导能绘卷x 3', '禁咒绘卷 = 封魔绘卷x 3', '[\"barbara\",\"klee\",\"qiqi\",\"albedo\",\"yoimiya\"]', '[\"旅行剑\",\"祭礼剑\",\"暗巷闪光\",\"苍古自由之誓\",\"石英大剑\",\"雨裁\",\"狼的末路\",\"新手长枪\",\"铁尖枪\",\"匣里灭辰\",\"天空之脊\",\"反曲弓\",\"宗室长弓\",\"落霞\",\"讨龙英杰谭\",\"黑岩绯玉\",\"西风秘典\",\"白辰之环\"]');
INSERT INTO `breakthrough` VALUES (72, 'CommonMaterial', '禁咒绘卷', '3', '/AscensionMaterial/CommonMaterial/禁咒绘卷.png', '角色培养素材', '\"绘制着古代形象的古书抄本。鲜有人能解读它的含义；据说最终理解了其中真意的学者都陷入了癫狂。\"', '/img/upgrade/material/i_43', '[\"合成获得\"]', '[\"水丘丘萨满Lv 60+\",\"草丘丘萨满Lv 60+\",\"风丘丘萨满Lv 60+\",\"岩丘丘萨满Lv 60+\",\"冰丘丘萨满Lv 60+\",\"雷丘丘萨满Lv 60+\"]', NULL, '封魔绘卷x 3', NULL, '[\"barbara\",\"klee\",\"qiqi\",\"albedo\",\"yoimiya\"]', '[\"旅行剑\",\"祭礼剑\",\"暗巷闪光\",\"苍古自由之誓\",\"石英大剑\",\"雨裁\",\"狼的末路\",\"新手长枪\",\"铁尖枪\",\"匣里灭辰\",\"天空之脊\",\"反曲弓\",\"宗室长弓\",\"落霞\",\"讨龙英杰谭\",\"黑岩绯玉\",\"西风秘典\",\"白辰之环\"]');
INSERT INTO `breakthrough` VALUES (73, 'CommonMaterial', '牢固的箭簇', '1', '/AscensionMaterial/CommonMaterial/牢固的箭簇.png', '角色培养素材', '\"做工粗糙的箭簇。即使如此，也不容小觑：弩是危险的武器，即使贵为骑士，也可能被从后方来的弩矢击倒。\"', '/img/upgrade/material/i_51', '[\"射手类丘丘人掉落\"]', '[\"射手丘丘人Lv 0+\",\"火箭丘丘人Lv 0+\",\"冰箭丘丘人Lv 0+\",\"雷箭丘丘人Lv 0+\"]', NULL, '锐利的箭簇 = 牢固的箭簇x 3', NULL, '[\"amber\",\"fischl\",\"diona\"]', '[\"无锋剑\",\"银剑\",\"冷刃\",\"黑岩长剑\",\"西风剑\",\"风鹰剑\",\"沐浴龙血的剑\",\"千岩古剑\",\"祭礼大剑\",\"黑缨枪\",\"千岩长枪\",\"鸦羽弓\",\"绝弦\",\"苍翠猎弓\",\"破魔之弓\",\"掠食者\",\"天空之翼\",\"飞雷之弦振\",\"试作金珀\",\"天空之卷\"]');
INSERT INTO `breakthrough` VALUES (74, 'CommonMaterial', '锐利的箭簇', '2', '/AscensionMaterial/CommonMaterial/锐利的箭簇.png', '角色培养素材', '\"做工精良的箭簇。锐利的尖端或许能像投石入水一样轻易穿透敌人的甲胄，没入猎物的体内。\"', '/img/upgrade/material/i_52', '[\"合成获得\"]', '[\"射手丘丘人Lv 40+\",\"火箭丘丘人Lv 40+\",\"冰箭丘丘人Lv 40+\",\"雷箭丘丘人Lv 40+\"]', NULL, '牢固的箭簇x 3', '历战的箭簇 = 锐利的箭簇x 3', '[\"amber\",\"fischl\",\"diona\"]', '[\"无锋剑\",\"银剑\",\"冷刃\",\"黑岩长剑\",\"西风剑\",\"风鹰剑\",\"沐浴龙血的剑\",\"千岩古剑\",\"祭礼大剑\",\"黑缨枪\",\"千岩长枪\",\"鸦羽弓\",\"绝弦\",\"苍翠猎弓\",\"破魔之弓\",\"掠食者\",\"天空之翼\",\"飞雷之弦振\",\"试作金珀\",\"天空之卷\"]');
INSERT INTO `breakthrough` VALUES (75, 'CommonMaterial', '历战的箭簇', '3', '/AscensionMaterial/CommonMaterial/历战的箭簇.png', '角色培养素材', '\"老旧的、沾染了干涸血迹的箭簇。箭头在反复的使用中已经失去了应有的锐利，根本不能作为武器使用。<br>但它也代表着猎手的骄傲，是它们的护身符与奖章。\"', '/img/upgrade/material/i_53', '[\"合成获得\"]', '[\"射手丘丘人Lv 60+\",\"火箭丘丘人Lv 60+\",\"冰箭丘丘人Lv 60+\",\"雷箭丘丘人Lv 60+\"]', NULL, '锐利的箭簇x 3', NULL, '[\"amber\",\"fischl\",\"diona\"]', '[\"无锋剑\",\"银剑\",\"冷刃\",\"黑岩长剑\",\"西风剑\",\"风鹰剑\",\"沐浴龙血的剑\",\"千岩古剑\",\"祭礼大剑\",\"黑缨枪\",\"千岩长枪\",\"鸦羽弓\",\"绝弦\",\"苍翠猎弓\",\"破魔之弓\",\"掠食者\",\"天空之翼\",\"飞雷之弦振\",\"试作金珀\",\"天空之卷\"]');
INSERT INTO `breakthrough` VALUES (76, 'CommonMaterial', '新兵的徽记', '1', '/AscensionMaterial/CommonMaterial/新兵的徽记.png', '角色培养素材', '\"标识着新兵身份的徽记。不知曾经带着它的人，是怀着何种心情加入愚人众的战争机器的呢。\"', '/img/upgrade/material/i_111', '[\"先遣队掉落\",\"债务处理人少量掉落\",\"雷萤术士少量掉落\"]', '[\"愚人众先遣队·冰铳重卫士Lv 0+\",\"愚人众先遣队·水铳重卫士Lv 0+\",\"愚人众先遣队·雷锤前锋军Lv 0+\",\"愚人众先遣队·岩使游击兵Lv 0+\",\"愚人众先遣队·风拳前锋军Lv 0+\",\"愚人众先遣队·火铳游击兵Lv 0+\",\"愚人众·火之债务处理人Lv 0+\",\"愚人众·雷萤术士Lv 0+\",\"愚人众·冰萤术士Lv 0+\"]', NULL, '士官的徽记 = 新兵的徽记x 3', NULL, '[\"diluc\",\"ningguang\",\"tartaglia\",\"rosaria\",\"yelan\"]', '[\"飞天御剑\",\"试作斩岩\",\"腐殖之剑\",\"黑岩斩刀\",\"西风大剑\",\"白缨枪\",\"黑岩刺枪\",\"宗室猎枪\",\"龙脊长枪\",\"和璞鸢\",\"黑檀弓\",\"钢轮弓\",\"终末嗟叹之诗\",\"甲级宝珏\",\"宗室秘法录\"]');
INSERT INTO `breakthrough` VALUES (77, 'CommonMaterial', '士官的徽记', '2', '/AscensionMaterial/CommonMaterial/士官的徽记.png', '角色培养素材', '\"标识着士官身份的徽记，有着区分一般士兵的奇异造型。其中或许蕴含着复杂的情绪吧。\"', '/img/upgrade/material/i_112', '[\"合成获得\",\"40级以上债务处理人少量掉落\",\"40级以上雷萤术士少量掉落\",\"合成获得\"]', '[\"愚人众先遣队·冰铳重卫士Lv 40+\",\"愚人众先遣队·水铳重卫士Lv 40+\",\"愚人众先遣队·雷锤前锋军Lv 40+\",\"愚人众先遣队·岩使游击兵Lv 40+\",\"愚人众先遣队·风拳前锋军Lv 40+\",\"愚人众先遣队·火铳游击兵Lv 40+\",\"愚人众·火之债务处理人Lv 40+\",\"愚人众·雷萤术士Lv 40+\",\"愚人众·冰萤术士Lv 40+\"]', NULL, '新兵的徽记x 3', '尉官的徽记 = 士官的徽记x 3', '[\"diluc\",\"ningguang\",\"tartaglia\",\"rosaria\",\"yelan\"]', '[\"飞天御剑\",\"试作斩岩\",\"腐殖之剑\",\"黑岩斩刀\",\"西风大剑\",\"白缨枪\",\"黑岩刺枪\",\"宗室猎枪\",\"龙脊长枪\",\"和璞鸢\",\"黑檀弓\",\"钢轮弓\",\"终末嗟叹之诗\",\"甲级宝珏\",\"宗室秘法录\"]');
INSERT INTO `breakthrough` VALUES (78, 'CommonMaterial', '尉官的徽记', '3', '/AscensionMaterial/CommonMaterial/尉官的徽记.png', '角色培养素材', '\"标识着尉官身份的徽记。愚人众拥有庞大的军队，能在其中担任尉官者，想必是有过人之处吧。\"', '/img/upgrade/material/i_113', '[\"合成获得\",\"60级以上债务处理人少量掉落\",\"60级以上雷萤术士少量掉落\",\"合成获得\"]', '[\"愚人众先遣队·冰铳重卫士Lv 60+\",\"愚人众先遣队·水铳重卫士Lv 60+\",\"愚人众先遣队·雷锤前锋军Lv 60+\",\"愚人众先遣队·岩使游击兵Lv 60+\",\"愚人众先遣队·风拳前锋军Lv 60+\",\"愚人众先遣队·火铳游击兵Lv 60+\",\"愚人众·火之债务处理人Lv 60+\",\"愚人众·雷萤术士Lv 60+\",\"愚人众·冰萤术士Lv 60+\"]', NULL, '士官的徽记x 3', NULL, '[\"diluc\",\"ningguang\",\"tartaglia\",\"rosaria\",\"yelan\"]', '[\"飞天御剑\",\"试作斩岩\",\"腐殖之剑\",\"黑岩斩刀\",\"西风大剑\",\"白缨枪\",\"黑岩刺枪\",\"宗室猎枪\",\"龙脊长枪\",\"和璞鸢\",\"黑檀弓\",\"钢轮弓\",\"终末嗟叹之诗\",\"甲级宝珏\",\"宗室秘法录\"]');
INSERT INTO `breakthrough` VALUES (79, 'CommonMaterial', '寻宝鸦印', '1', '/AscensionMaterial/CommonMaterial/寻宝鸦印.png', '角色培养素材', '\"骄傲地标识着盗宝团身份的印记，描绘着他们对宝藏永无止境的追求。话虽如此，盗贼的身份真的需要骄傲地展示吗…？\"', '/img/upgrade/material/i_121', '[\"盗宝团成员掉落\"]', '[\"盗宝团·「溜溜」Lv 0+\",\"盗宝团·斥候Lv 0+\",\"盗宝团·火之药剂师Lv 0+\",\"盗宝团·水之药剂师Lv 0+\",\"盗宝团·雷之药剂师Lv 0+\",\"盗宝团·冰之药剂师Lv 0+\",\"盗宝团·杂工Lv 0+\",\"盗宝团·「怪鸟」Lv 0+\",\"盗宝团·神射手Lv 0+\",\"盗宝团·「卡门」Lv 0+\",\"盗宝团·掘墓者Lv 0+\",\"盗宝团·海上男儿Lv 0+\",\"盗宝团·「大姐头」Lv 0+\",\"千岩军士兵Lv 0+\",\"千岩军教头Lv 0+\",\"盗宝团·拳术家Lv 0+\",\"盗宝团·粉碎者Lv 0+\"]', NULL, '藏银鸦印 = 寻宝鸦印x 3', NULL, '[\"beidou\",\"bennett\",\"kaeya\",\"xinyan\",\"feiyan\",\"kazuha\",\"thoma\"]', '[\"吃虎鱼刀\",\"匣里龙吟\",\"降临之剑\",\"磐岩结绿\",\"飞天大御剑\",\"白影剑\",\"无工之剑\",\"流月针\",\"喜多院十文字\",\"贯虹之槊\",\"猎弓\",\"历练的猎弓\",\"信使\",\"试作澹月\",\"幽夜华尔兹\",\"翡玉法球\",\"祭礼残章\",\"暗巷的酒与诗\"]');
INSERT INTO `breakthrough` VALUES (80, 'CommonMaterial', '藏银鸦印', '2', '/AscensionMaterial/CommonMaterial/藏银鸦印.png', '角色培养素材', '\"盗宝团的成员用于相互辨识的乌鸦纹章。盗宝团不问出身，无论何者，只要怀着贪求宝藏的心，有铤而走险的胆识，都可以成为「优秀」的成员。\"', '/img/upgrade/material/i_122', '[\"合成获得\"]', '[\"盗宝团·「溜溜」Lv 40+\",\"盗宝团·斥候Lv 40+\",\"盗宝团·火之药剂师Lv 40+\",\"盗宝团·水之药剂师Lv 40+\",\"盗宝团·雷之药剂师Lv 40+\",\"盗宝团·冰之药剂师Lv 40+\",\"盗宝团·杂工Lv 40+\",\"盗宝团·「怪鸟」Lv 40+\",\"盗宝团·神射手Lv 40+\",\"盗宝团·「卡门」Lv 40+\",\"盗宝团·掘墓者Lv 40+\",\"盗宝团·海上男儿Lv 40+\",\"盗宝团·「大姐头」Lv 40+\",\"千岩军士兵Lv 40+\",\"千岩军教头Lv 40+\",\"盗宝团·拳术家Lv 40+\",\"盗宝团·粉碎者Lv 40+\"]', NULL, '寻宝鸦印x 3', '攫金鸦印 = 藏银鸦印x 3', '[\"beidou\",\"bennett\",\"kaeya\",\"xinyan\",\"feiyan\",\"kazuha\",\"thoma\"]', '[\"吃虎鱼刀\",\"匣里龙吟\",\"降临之剑\",\"磐岩结绿\",\"飞天大御剑\",\"白影剑\",\"无工之剑\",\"流月针\",\"喜多院十文字\",\"贯虹之槊\",\"猎弓\",\"历练的猎弓\",\"信使\",\"试作澹月\",\"幽夜华尔兹\",\"翡玉法球\",\"祭礼残章\",\"暗巷的酒与诗\"]');
INSERT INTO `breakthrough` VALUES (81, 'CommonMaterial', '攫金鸦印', '3', '/AscensionMaterial/CommonMaterial/攫金鸦印.png', '角色培养素材', '\"象征着盗宝团唯一的原则与傲骨的徽印。只要广袤的大地上、幽邃的深渊里、未知的海洋中仍有待发掘的秘宝，那么手段不论地征服宝藏的盗宝之魂，就永远不会熄灭。\"', '/img/upgrade/material/i_123', '[\"合成获得\"]', '[\"岩之寻宝罗盘 = 攫金鸦印x 10 石珀x 30 水晶块x 50\",\"风之寻宝罗盘 = 攫金鸦印x 10 慕风蘑菇x 30 水晶块x 50\",\"雷之寻宝罗盘 = 攫金鸦印x 10 鬼兜虫x 30 水晶块x 50\"]', NULL, '藏银鸦印x 3', NULL, '[\"beidou\",\"bennett\",\"kaeya\",\"xinyan\",\"feiyan\",\"kazuha\",\"thoma\"]', '[\"吃虎鱼刀\",\"匣里龙吟\",\"降临之剑\",\"磐岩结绿\",\"飞天大御剑\",\"白影剑\",\"无工之剑\",\"流月针\",\"喜多院十文字\",\"贯虹之槊\",\"猎弓\",\"历练的猎弓\",\"信使\",\"试作澹月\",\"幽夜华尔兹\",\"翡玉法球\",\"祭礼残章\",\"暗巷的酒与诗\"]');
INSERT INTO `breakthrough` VALUES (82, 'CommonMaterial', '骗骗花蜜', '1', '/AscensionMaterial/CommonMaterial/骗骗花蜜.png', '角色培养素材', '\"从花蕊中提取出的花蜜，其中含有少量元素。<br>尝起来和甜甜花的味道有些微妙地相似。\"', '/img/upgrade/material/i_131', '[\"骗骗花掉落\"]', '[\"冰霜骗骗花Lv 0+\",\"炽热骗骗花Lv 0+\",\"电气骗骗花Lv 0+\"]', NULL, '微光花蜜 = 骗骗花蜜x 3', NULL, '[\"keqing\",\"mona\",\"sucrose\",\"ganyu\",\"hutao\",\"sayu\",\"shenhe\"]', '[\"铁蜂刺\",\"铁影阔剑\",\"螭骨剑\",\"钟剑\",\"钺矛\",\"「旗杆」\",\"决斗之枪\",\"息灾\",\"黑岩战弓\",\"西风猎弓\",\"风花之颂\",\"琥珀玥\",\"匣里日月\",\"忍冬之果\"]');
INSERT INTO `breakthrough` VALUES (83, 'CommonMaterial', '微光花蜜', '2', '/AscensionMaterial/CommonMaterial/微光花蜜.png', '角色培养素材', '\"元素纯度较高的花蜜。<br>学界一般认为骗骗花是元素植物的高级生命形态，但始终无法解明它的掠食习性。\"', '/img/upgrade/material/i_132', '[\"合成获得\"]', '[\"冰霜骗骗花Lv 40+\",\"炽热骗骗花Lv 40+\",\"电气骗骗花Lv 40+\"]', NULL, '骗骗花蜜x 3', '原素花蜜 = 微光花蜜x 3', '[\"keqing\",\"mona\",\"sucrose\",\"ganyu\",\"hutao\",\"sayu\",\"shenhe\"]', '[\"铁蜂刺\",\"铁影阔剑\",\"螭骨剑\",\"钟剑\",\"钺矛\",\"「旗杆」\",\"决斗之枪\",\"息灾\",\"黑岩战弓\",\"西风猎弓\",\"风花之颂\",\"琥珀玥\",\"匣里日月\",\"忍冬之果\"]');
INSERT INTO `breakthrough` VALUES (84, 'CommonMaterial', '原素花蜜', '3', '/AscensionMaterial/CommonMaterial/原素花蜜.png', '角色培养素材', '\"饱含能量的黏稠蜜汁。<br>骗骗花欺瞒猎物是为了狩猎，而狩猎进食或许是为了进化成某种更强大、更纯粹的姿态吧…\"', '/img/upgrade/material/i_133', '[\"合成获得\"]', '[\"冰霜骗骗花Lv 60+\",\"炽热骗骗花Lv 60+\",\"电气骗骗花Lv 60+\"]', NULL, '微光花蜜x 3', NULL, '[\"keqing\",\"mona\",\"sucrose\",\"ganyu\",\"hutao\",\"sayu\",\"shenhe\"]', '[\"铁蜂刺\",\"铁影阔剑\",\"螭骨剑\",\"钟剑\",\"钺矛\",\"「旗杆」\",\"决斗之枪\",\"息灾\",\"黑岩战弓\",\"西风猎弓\",\"风花之颂\",\"琥珀玥\",\"匣里日月\",\"忍冬之果\"]');
INSERT INTO `breakthrough` VALUES (85, 'CommonMaterial', '晦暗刻像', '2', '/AscensionMaterial/CommonMaterial/晦暗刻像.png', '角色培养素材', '\"刻画着某人形象的小塑像。<br>即使经过了长久的岁月，也没有受到任何损伤。似乎被原本的主人如同圣象一般呵护着。\"', '/img/upgrade/material/i_151', '[\"深渊法师掉落\"]', '[\"波乱月白经津\",\"若水\"]', NULL, '夤夜刻像 = 晦暗刻像x 3', '', '[]', '[\"波乱月白经津\",\"若水\"]');
INSERT INTO `breakthrough` VALUES (86, 'CommonMaterial', '夤夜刻像', '3', '/AscensionMaterial/CommonMaterial/夤夜刻像.png', '角色培养素材', '\"刻画着某人形象的独目偶像。<br>握在手中时似乎能感受到其中散发的丝丝暖意，就像挚友的臂膀那般。或许能给予明白雕像背后个中含义的人持续前进的勇气吧。\"', '/img/upgrade/material/i_152', '[\"合成获得\"]', '[\"幽邃刻像 = 夤夜刻像x 3\"]', NULL, '晦暗刻像x 3', '幽邃刻像 = 夤夜刻像x 3', '[]', '[\"波乱月白经津\",\"若水\"]');
INSERT INTO `breakthrough` VALUES (87, 'CommonMaterial', '幽邃刻像', '4', '/AscensionMaterial/CommonMaterial/幽邃刻像.png', '角色培养素材', '\"微微散发着不详的能量的独目刻像，是以何种材质制成则完全不可知。<br>凝视着这尊偶像时，似乎能听见令人心情宁静的奇妙低语…<br>「看吧，我的孩子。天之座下的万物终将在动荡中消亡。永远宁静的漆黑深空会包容一切。」\"', '/img/upgrade/material/i_153', '[\"合成获得\"]', '[\"火深渊法师Lv 60+\",\"冰深渊法师Lv 60+\",\"水深渊法师Lv 60+\",\"雷深渊法师Lv 60+\",\"黯色空壳·旗令Lv 60+\",\"黯色空壳·破阵Lv 60+\",\"黯色空壳·近卫Lv 60+\",\"黑蛇骑士·斩风之剑Lv 60+\"]', NULL, '夤夜刻像x 3', NULL, '[]', '[\"波乱月白经津\",\"若水\"]');
INSERT INTO `breakthrough` VALUES (88, 'CommonMaterial', '破旧的刀镡', '1', '/AscensionMaterial/CommonMaterial/破旧的刀镡.png', '角色培养素材', '\"装配在稻妻刀剑上的护具，见证过许多战斗。<br>对剑术纯熟者而言，镡可以在实战中阻挡对手的刀刃；而对于新手剑士，镡是保护自己右手的关键。\"', '/img/upgrade/material/i_161', '[\"野伏众掉落\"]', '[\"野伏·阵刀番Lv 0+\",\"野伏·火付番Lv 0+\",\"野伏·机巧番Lv 0+\",\"海乱鬼·雷腾Lv 0+\",\"海乱鬼·炎威Lv 0+\"]', NULL, '影打刀镡 = 破旧的刀镡x 3', NULL, '[\"ayaka\",\"raiden\",\"yaemiko\",\"ayato\"]', '[\"天目影打刀\",\"雾切之回光\",\"波乱月白经津\",\"桂木斩长正\",\"恶王丸\",\"赤角石溃杵\",\"断浪长鳍\",\"薙草之稻光\"]');
INSERT INTO `breakthrough` VALUES (89, 'CommonMaterial', '影打刀镡', '2', '/AscensionMaterial/CommonMaterial/影打刀镡.png', '角色培养素材', '\"装配在优秀刀剑上的护具。<br>稻妻有悠久的刀剑锻冶传统。匠人锻造一把刀时，常常会制造数柄样品，从中甄选。其中最为优秀的作品是为「真打」，而其余的则被称为「影打」。\"', '/img/upgrade/material/i_162', '[\"合成获得\"]', '[\"野伏·阵刀番Lv 40+\",\"野伏·火付番Lv 40+\",\"野伏·机巧番Lv 40+\",\"海乱鬼·雷腾Lv 40+\",\"海乱鬼·炎威Lv 40+\"]', NULL, '破旧的刀镡x 3', '名刀镡 = 影打刀镡x 3', '[\"ayaka\",\"raiden\",\"yaemiko\",\"ayato\"]', '[\"天目影打刀\",\"雾切之回光\",\"波乱月白经津\",\"桂木斩长正\",\"恶王丸\",\"赤角石溃杵\",\"断浪长鳍\",\"薙草之稻光\"]');
INSERT INTO `breakthrough` VALUES (90, 'CommonMaterial', '名刀镡', '3', '/AscensionMaterial/CommonMaterial/名刀镡.png', '角色培养素材', '\"装配在名刀上的华美护具。<br>有人坚信刀剑有灵魂、有生命，这也是为什么人们会为刀起名。爱护刀的人，也会得到刀的赏识。但与刀剑成为挚友、甚至爱人的人，须知这份情义是由杀伐浇灌的。常常浴血、与刀共舞之人，他们的热血最终总会被薄情的钢铁冷却。\"', '/img/upgrade/material/i_163', '[\"合成获得\"]', '[\"野伏·阵刀番Lv 60+\",\"野伏·火付番Lv 60+\",\"野伏·机巧番Lv 60+\",\"海乱鬼·雷腾Lv 60+\",\"海乱鬼·炎威Lv 60+\"]', NULL, '影打刀镡x 3', NULL, '[\"ayaka\",\"raiden\",\"yaemiko\",\"ayato\"]', '[\"天目影打刀\",\"雾切之回光\",\"波乱月白经津\",\"桂木斩长正\",\"恶王丸\",\"赤角石溃杵\",\"断浪长鳍\",\"薙草之稻光\"]');
INSERT INTO `breakthrough` VALUES (91, 'CommonMaterial', '混沌机关', '2', '/AscensionMaterial/CommonMaterial/混沌机关.png', '角色培养素材', '\"来自不再活动的古代遗迹机关。<br>似乎是拟生机关的动力来源。\"', '/img/upgrade/material/i_171', '[\"遗迹机兵掉落\"]', '[\"尘辉兑换\"]', NULL, '混沌枢纽 = 混沌机关x 3', '', '[]', '[\"天目影打刀\",\"雾切之回光\",\"桂木斩长正\",\"喜多院十文字\",\"「渔获」\",\"薙草之稻光\"]');
INSERT INTO `breakthrough` VALUES (92, 'CommonMaterial', '混沌枢纽', '3', '/AscensionMaterial/CommonMaterial/混沌枢纽.png', '角色培养素材', '\"来自不再活动的古代遗迹机关。<br>在无法维持机关构装体的结构性之后，其中蕴含的伟大技术与未知力量也失去意义了吧。\"', '/img/upgrade/material/i_172', '[\"合成获得\"]', '[\"混沌真眼 = 混沌枢纽x 3\"]', NULL, '混沌机关x 3', '混沌真眼 = 混沌枢纽x 3', '[]', '[\"天目影打刀\",\"雾切之回光\",\"桂木斩长正\",\"喜多院十文字\",\"「渔获」\",\"薙草之稻光\"]');
INSERT INTO `breakthrough` VALUES (93, 'CommonMaterial', '混沌真眼', '4', '/AscensionMaterial/CommonMaterial/混沌真眼.png', '角色培养素材', '\"来自不再活动的古代遗迹机关。<br>这类机关的「遗迹」之名源自它们热衷的活动区域。但遗迹机关本身与它们所寄居的残垣断壁之间却没有多少共同之处。<br>在遥远的过去，遗迹曾经属于伟大的、几乎可以触碰天空穹顶的文明，而「遗迹」则在大地的深处蓄势待发，等待着踏平一切国度的指令下达。\"', '/img/upgrade/material/i_173', '[\"合成获得\"]', '[\"遗迹巡弋者Lv 60+\",\"遗迹歼击者Lv 60+\",\"遗迹防卫者Lv 60+\",\"遗迹侦察者Lv 60+\"]', NULL, '混沌枢纽x 3', NULL, '[]', '[\"天目影打刀\",\"雾切之回光\",\"桂木斩长正\",\"喜多院十文字\",\"「渔获」\",\"薙草之稻光\"]');
INSERT INTO `breakthrough` VALUES (94, 'LocalMaterial', '嘟嘟莲', '1', '/AscensionMaterial/LocalMaterial/嘟嘟莲.png', '蒙德区域特产', '\"生长在水边的花。花瓣经过烹制口感爽脆，清甜微苦。\"', '/img/upgrade/gem/i_600', '[\"湖泊水边采集\",\"推荐：清泉镇采集\"]', NULL, NULL, NULL, '嘟嘟莲海鲜羹 = 螃蟹x 4 嘟嘟莲x 1 薄荷x 2', '[\"kaeya\",\"diona\"]', NULL);
INSERT INTO `breakthrough` VALUES (95, 'LocalMaterial', '塞西莉亚花', '1', '/AscensionMaterial/LocalMaterial/塞西莉亚花.png', '蒙德区域特产', '\"名字与外形同等美丽的花朵，只生长在清冷而风急的高处，如同浪子一样真心难以触碰。\"', '/img/upgrade/gem/i_601', '[\"野外采集\",\"推荐：摘星崖采集\"]', NULL, NULL, NULL, '风神瞳共鸣石 = 蒲公英籽x 5 塞西莉亚花x 5 水晶块x 1', '[\"venti\",\"albedo\"]', NULL);
INSERT INTO `breakthrough` VALUES (96, 'LocalMaterial', '石珀', '1', '/AscensionMaterial/LocalMaterial/石珀.png', '璃月区域特产', '\"极纯的岩元素凝成的珍稀晶石，常与其他矿物伴生。也被称为「岩之心」。\"', '/img/upgrade/gem/i_602', '[\"璃月山壁下采集\",\"推荐：琥牢山采集\"]', NULL, NULL, NULL, '磐石精油 = 青蛙x 1 石珀x 1', '[\"chongyun\",\"keqing\",\"zhongli\"]', NULL);
INSERT INTO `breakthrough` VALUES (97, 'LocalMaterial', '蒲公英籽', '1', '/AscensionMaterial/LocalMaterial/蒲公英籽.png', '蒙德区域特产', '\"随风而行的细小种子。即使拔去了旅行的羽絮，其中也寄宿着远方的希望吧。\"', '/img/upgrade/gem/i_603', '[\"野外采集\",\"推荐：蒙德城门采集\"]', NULL, NULL, NULL, '狂风精油 = 蜥蜴尾巴x 1 蒲公英籽x 1', '[\"jean\",\"eula\"]', NULL);
INSERT INTO `breakthrough` VALUES (98, 'LocalMaterial', '琉璃百合', '1', '/AscensionMaterial/LocalMaterial/琉璃百合.png', '璃月区域特产', '\"极为古老的花卉。据说曾经遍布璃月大地，能将大地的记忆积攒成花期时的芬芳。\"', '/img/upgrade/gem/i_604', '[\"野外采集\",\"推荐：璃月港玉京台采集\",\"推荐：轻策庄采集\"]', NULL, NULL, NULL, '岩神瞳共鸣石 = 石珀x 5 琉璃百合x 5 水晶块x 1', '[\"ningguang\",\"yunjin\"]', NULL);
INSERT INTO `breakthrough` VALUES (99, 'LocalMaterial', '绝云椒椒', '1', '/AscensionMaterial/LocalMaterial/绝云椒椒.png', '璃月区域特产', '\"璃月地方特产的辛辣植物。只要闻到它的味道，身体就会变得炽热，渴求水分。\"', '/img/upgrade/gem/i_605', '[\"野外采集\",\"推荐：轻策庄采集\"]', NULL, NULL, NULL, '爆炒肉片 = 兽肉x 1 绝云椒椒x 1', '[\"xiangling\"]', NULL);
INSERT INTO `breakthrough` VALUES (100, 'LocalMaterial', '夜泊石', '1', '/AscensionMaterial/LocalMaterial/夜泊石.png', '璃月区域特产', '\"稀有的特质矿石，在静谧的暗夜里会幽幽地发光。据称是天地间奔流的元素在异变中凝聚成的珍奇宝石。\"', '/img/upgrade/gem/i_606', '[\"璃月山洞内采集\",\"推荐：明蕴镇采集\"]', NULL, NULL, NULL, 'Shop #1028 (No Name Set)', '[\"beidou\",\"feiyan\"]', NULL);
INSERT INTO `breakthrough` VALUES (101, 'LocalMaterial', '慕风蘑菇', '1', '/AscensionMaterial/LocalMaterial/慕风蘑菇.png', '蒙德区域特产', '\"沐浴着和煦的风而生长的菌类，与风一样生生不息，滋养生命。\"', '/img/upgrade/gem/i_607', '[\"房檐下采集\"]', NULL, NULL, NULL, '风之寻宝罗盘 = 攫金鸦印x 10 慕风蘑菇x 30 水晶块x 50', '[\"barbara\",\"klee\",\"mona\"]', NULL);
INSERT INTO `breakthrough` VALUES (102, 'LocalMaterial', '霓裳花', '1', '/AscensionMaterial/LocalMaterial/霓裳花.png', '璃月区域特产', '\"在璃月如霓云般盛放的妖红之花，能制成如水般顺滑的织物。\"', '/img/upgrade/gem/i_608', '[\"璃月港玉京台附近采集\",\"望舒客栈附近采集\",\"推荐：璃月港采集\"]', NULL, NULL, NULL, '布匹 = 霓裳花x 1', '[\"xingqiu\",\"hutao\"]', NULL);
INSERT INTO `breakthrough` VALUES (103, 'LocalMaterial', '小灯草', '1', '/AscensionMaterial/LocalMaterial/小灯草.png', '蒙德区域特产', '\"在夜间会隐隐散发辉光的野花。与其他食材一同烹饪能提升鲜味。\"', '/img/upgrade/gem/i_609', '[\"夜间野外寻找\",\"推荐：奔狼领采集\",\"推荐：低语森林采集\"]', NULL, NULL, NULL, '「堆高高」 = 兽肉x 3 土豆x 3 小灯草x 1 奶酪x 1', '[\"amber\",\"diluc\",\"fischl\"]', NULL);
INSERT INTO `breakthrough` VALUES (104, 'LocalMaterial', '落落莓', '1', '/AscensionMaterial/LocalMaterial/落落莓.png', '蒙德区域特产', '\"饱满透亮的莓果，在口中绽放出清甜的花香。在过去，守望风暴的人仅有的慰藉便是落落莓的甜蜜与对城市安宁的希望。\"', '/img/upgrade/gem/i_610', '[\"野外采集\",\"推荐：望风山地采集\"]', NULL, NULL, NULL, '红色染料 = 落落莓x 1', '[\"lisa\",\"noelle\",\"rosaria\"]', NULL);
INSERT INTO `breakthrough` VALUES (105, 'LocalMaterial', '琉璃袋', '1', '/AscensionMaterial/LocalMaterial/琉璃袋.png', '璃月区域特产', '\"拥有旺盛生命力的小花，传说朝下盛开的花姿是为了守住它的香气。\"', '/img/upgrade/gem/i_611', '[\"璃月山壁采集\"]', NULL, NULL, NULL, '岩港三鲜 = 土豆x 2 琉璃袋x 1 松茸x 1', '[\"qiqi\",\"xinyan\"]', NULL);
INSERT INTO `breakthrough` VALUES (106, 'LocalMaterial', '风车菊', '1', '/AscensionMaterial/LocalMaterial/风车菊.png', '蒙德区域特产', '\"喜好风的植物。对于崇信风、本身也如风一般爽朗自在的蒙德人而言，风车菊便是「看得见的风」。\"', '/img/upgrade/gem/i_612', '[\"风起地七天神像附近采集\",\"野外树下采集\",\"推荐：风龙废墟采集\"]', NULL, NULL, NULL, '捕风瓶 = 飓风之种x 10 风车菊x 30 水晶块x 50', '[\"bennett\",\"sucrose\",\"traveler_boy_anemo\",\"traveler_boy_geo\",\"traveler_girl_anemo\",\"traveler_girl_geo\",\"traveler_boy_electro\",\"traveler_girl_electro\"]', NULL);
INSERT INTO `breakthrough` VALUES (107, 'LocalMaterial', '钩钩果', '1', '/AscensionMaterial/LocalMaterial/钩钩果.png', '蒙德区域特产', '\"带着勾刺的莓果，会挂在狼的毛皮上来迁徙。看见它时，仿佛能听见狼的凄嚎在林间回荡…\"', '/img/upgrade/gem/i_613', '[\"野外采集\",\"推荐：奔狼领采集\"]', NULL, NULL, NULL, '蓝色染料 = 钩钩果x 1', '[\"razor\"]', NULL);
INSERT INTO `breakthrough` VALUES (108, 'LocalMaterial', '清心', '1', '/AscensionMaterial/LocalMaterial/清心.png', '璃月区域特产', '\"只生长在孤高石峰的通透白花。不愿接受平原的温暖与湿润，宁可在清冷的尖峰眺望远方。\"', '/img/upgrade/gem/i_614', '[\"石林顶端采集\",\"推荐：绝云间采集\",\"推荐：孤云阁采集\"]', NULL, NULL, NULL, '天枢肉 = 兽肉x 4 糖x 2 清心x 1 松茸x 1', '[\"xiao\",\"ganyu\",\"shenhe\"]', NULL);
INSERT INTO `breakthrough` VALUES (109, 'LocalMaterial', '星螺', '1', '/AscensionMaterial/LocalMaterial/星螺.png', '璃月区域特产', '\"随着海潮来到沙滩上的螺。将耳朵凑近空壳，能听见海充满思念的呼唤。\"', '/img/upgrade/gem/i_663', '[\"璃月海滩采集\"]', NULL, NULL, NULL, 'Shop #1046 (No Name Set)', '[\"tartaglia\",\"yelan\"]', NULL);
INSERT INTO `breakthrough` VALUES (110, 'LocalMaterial', '海灵芝', '1', '/AscensionMaterial/LocalMaterial/海灵芝.png', '稻妻区域特产', '\"在某些特殊的海域中、岛屿上才会生长的特殊物种。虽然外形看起来像菌类，但其实是由软体生物分泌增生而成的。\"', '/img/upgrade/gem/i_675', '[\"稻妻海域采集\"]', NULL, NULL, NULL, 'Shop #1053 (No Name Set)', '[\"kazuha\",\"yaemiko\"]', NULL);
INSERT INTO `breakthrough` VALUES (111, 'LocalMaterial', '绯樱绣球', '1', '/AscensionMaterial/LocalMaterial/绯樱绣球.png', '稻妻区域特产', '\"从鸣神大社的神樱上飘落的花瓣，怀着稻妻大地的眷顾。\"', '/img/upgrade/gem/i_678', '[\"野外采集\",\"推荐：鸣神岛采集\"]', NULL, NULL, NULL, '三彩团子 = 牛奶x 2 金鱼草x 2 绯樱绣球x 2 稻米x 1', '[\"ayaka\",\"ayato\"]', NULL);
INSERT INTO `breakthrough` VALUES (112, 'LocalMaterial', '晶化骨髓', '1', '/AscensionMaterial/LocalMaterial/晶化骨髓.png', '稻妻区域特产', '\"蕴含着「祟神」力量的晶体。冶炼时加入这种物质的话，能极大地提高钢铁制品的强度与韧性。\"', '/img/upgrade/gem/i_679', '[\"野外采集\",\"推荐：八酝岛采集\"]', NULL, NULL, NULL, NULL, '[\"sayu\",\"aloy\"]', NULL);
INSERT INTO `breakthrough` VALUES (113, 'LocalMaterial', '鸣草', '1', '/AscensionMaterial/LocalMaterial/鸣草.png', '稻妻区域特产', '\"即使在无风的日子里，亦会随雷鸣之声隐隐振动的植物。看起来像是花瓣的结构其实是叶片的延伸，保护着脆弱的花朵。\"', '/img/upgrade/gem/i_681', '[\"野外采集\",\"推荐：踏鞴砂采集\"]', NULL, NULL, NULL, '雷神瞳共鸣石 = 鸣草x 5 绯樱绣球x 5 紫晶块x 1', '[\"yoimiya\",\"shinobu\"]', NULL);
INSERT INTO `breakthrough` VALUES (114, 'LocalMaterial', '珊瑚真珠', '1', '/AscensionMaterial/LocalMaterial/珊瑚真珠.png', '稻妻区域特产', '\"与海祇的珊瑚相伴而生的宝珠，散发着月光般的丝丝凉意。\"', '/img/upgrade/gem/i_685', '[\"野外采集\",\"推荐：海祇岛采集\"]', NULL, NULL, NULL, NULL, '[\"kokomi\",\"gorou\"]', NULL);
INSERT INTO `breakthrough` VALUES (115, 'LocalMaterial', '天云草实', '1', '/AscensionMaterial/LocalMaterial/天云草实.png', '稻妻区域特产', '\"生长在清籁岛的天云草结成的果实，贴在耳畔能听见微弱的电流声。\"', '/img/upgrade/gem/i_686', '[\"野外采集\",\"推荐：清籁岛采集\"]', NULL, NULL, NULL, NULL, '[\"raiden\"]', NULL);
INSERT INTO `breakthrough` VALUES (116, 'LocalMaterial', '幽灯蕈', '1', '/AscensionMaterial/LocalMaterial/幽灯蕈.png', '稻妻区域特产', '\"如同夜灯般微微发光的蘑菇，其中蕴含着不可思议的力量。\"', '/img/upgrade/gem/i_688', '[\"野外采集\",\"推荐：鹤观采集\"]', NULL, NULL, NULL, NULL, '[\"thoma\"]', NULL);
INSERT INTO `breakthrough` VALUES (117, 'TalentLevel-Up', '「诗文」的教导', '2', '/AscensionMaterial/TalentLevel-Up/「诗文」的教导.png', '天赋培养素材', '\"天赋培养素材。<br>风之国土的灵魂是诗文。<br>诗文是美好的心意。在美好的日子里，吹拂原野的风中飘扬着田园诗、情诗，如和风拨动草叶般拨动人们的心。\"', '/img/upgrade/guide/i_401', '[\"忘却之峡\"]', NULL, '[]', NULL, '「诗文」的指引 = 「诗文」的教导x 3', '[\"fischl\",\"kaeya\",\"lisa\",\"venti\",\"albedo\",\"rosaria\"]', NULL);
INSERT INTO `breakthrough` VALUES (118, 'TalentLevel-Up', '「诗文」的指引', '3', '/AscensionMaterial/TalentLevel-Up/「诗文」的指引.png', '天赋培养素材', '\"天赋培养素材。<br>风之国土的灵魂是诗文。<br>诗文是激励的心意。在黯淡的日子里，穿过巷道的风中低语着愤怒的诗文，如同战鼓，如同风暴前的低鸣。\"', '/img/upgrade/guide/i_402', '[\"忘却之峡\"]', NULL, '[]', '「诗文」的教导x 3', '「诗文」的哲学 = 「诗文」的指引x 3', '[\"fischl\",\"kaeya\",\"lisa\",\"venti\",\"albedo\",\"rosaria\"]', NULL);
INSERT INTO `breakthrough` VALUES (119, 'TalentLevel-Up', '「诗文」的哲学', '4', '/AscensionMaterial/TalentLevel-Up/「诗文」的哲学.png', '天赋培养素材', '\"天赋培养素材。<br>风之国土的灵魂是诗文。<br>诗文是传颂的心意。即使一切都将逝去，即使一切都将不再，随风而行的诗歌，仍会将此世间一切美好传遍天空、大地与海洋的一切角落。\"', '/img/upgrade/guide/i_403', '[\"忘却之峡\"]', NULL, '[]', NULL, NULL, '[\"fischl\",\"kaeya\",\"lisa\",\"traveler_boy_anemo\",\"traveler_boy_geo\",\"venti\",\"albedo\",\"rosaria\",\"traveler_girl_anemo\",\"traveler_girl_geo\"]', NULL);
INSERT INTO `breakthrough` VALUES (120, 'TalentLevel-Up', '「浮世」的教导', '2', '/AscensionMaterial/TalentLevel-Up/「浮世」的教导.png', '天赋培养素材', '\"天赋培养素材。<br>雷之国土的梦想是浮世。<br>浮世之荣华乃是人之美的最高体现。人之在世岂不与疾电相类？如美梦或花火般浮华一瞬，在永恒的夜幕中留下绚烂的印记。\"', '/img/upgrade/guide/i_406', '[\"菫色之庭\"]', NULL, '[]', NULL, '「浮世」的指引 = 「浮世」的教导x 3', '[\"yoimiya\",\"traveler_boy_electro\",\"traveler_girl_electro\",\"kokomi\",\"thoma\"]', NULL);
INSERT INTO `breakthrough` VALUES (121, 'TalentLevel-Up', '「浮世」的指引', '3', '/AscensionMaterial/TalentLevel-Up/「浮世」的指引.png', '天赋培养素材', '\"天赋培养素材。<br>雷之国土的梦想是浮世。<br>浮世之难求乃是人之美的价值所在。人之在世岂不与雷影相似？意欲追逐掇取则遥踞云端，灯火阑珊的静谧之时却不期而来。\"', '/img/upgrade/guide/i_407', '[\"菫色之庭\"]', NULL, '[]', '「浮世」的教导x 3', '「浮世」的哲学 = 「浮世」的指引x 3', '[\"yoimiya\",\"kokomi\",\"thoma\"]', NULL);
INSERT INTO `breakthrough` VALUES (122, 'TalentLevel-Up', '「浮世」的哲学', '4', '/AscensionMaterial/TalentLevel-Up/「浮世」的哲学.png', '天赋培养素材', '\"天赋培养素材。<br>雷之国土的梦想是浮世。<br>浮世之短暂乃是人之美的本性所在。人之在世岂不与稻光相近？神威显现的刹那恍若隽永，而永恒的威鸣又有谁人能够承受？\"', '/img/upgrade/guide/i_408', '[\"菫色之庭\"]', NULL, '[]', NULL, NULL, '[\"yoimiya\",\"kokomi\",\"thoma\"]', NULL);
INSERT INTO `breakthrough` VALUES (123, 'TalentLevel-Up', '「勤劳」的教导', '2', '/AscensionMaterial/TalentLevel-Up/「勤劳」的教导.png', '天赋培养素材', '\"天赋培养素材。<br>岩之国土的根基是勤劳。<br>勤劳即为通过人的努力，克服高山与大海，追求繁荣与黄金的勇气与力量。\"', '/img/upgrade/guide/i_411', '[\"太山府\"]', NULL, '[]', NULL, '「勤劳」的指引 = 「勤劳」的教导x 3', '[\"chongyun\",\"xiangling\",\"ganyu\",\"hutao\",\"kazuha\",\"yunjin\"]', NULL);
INSERT INTO `breakthrough` VALUES (124, 'TalentLevel-Up', '「勤劳」的指引', '3', '/AscensionMaterial/TalentLevel-Up/「勤劳」的指引.png', '天赋培养素材', '\"天赋培养素材。<br>岩之国土的根基是勤劳。<br>勤劳是流传在璃月人血脉中的催化剂，能将岩石化为黄金，是伟大商港之所以为伟大商港的原因。\"', '/img/upgrade/guide/i_412', '[\"太山府\"]', NULL, '[]', '「勤劳」的教导x 3', '「勤劳」的哲学 = 「勤劳」的指引x 3', '[\"chongyun\",\"xiangling\",\"ganyu\",\"hutao\",\"kazuha\",\"yunjin\"]', NULL);
INSERT INTO `breakthrough` VALUES (125, 'TalentLevel-Up', '「勤劳」的哲学', '4', '/AscensionMaterial/TalentLevel-Up/「勤劳」的哲学.png', '天赋培养素材', '\"天赋培养素材。<br>岩之国土的根基是勤劳。<br>勤劳即为相信自己，即为相信通过汗水、智慧与力量，在诸神的土地上，人人都可以拥有自己的一席之地。\"', '/img/upgrade/guide/i_413', '[\"太山府\"]', NULL, '[]', NULL, NULL, '[\"chongyun\",\"xiangling\",\"ganyu\",\"hutao\",\"kazuha\",\"yunjin\"]', NULL);
INSERT INTO `breakthrough` VALUES (126, 'TalentLevel-Up', '「风雅」的教导', '2', '/AscensionMaterial/TalentLevel-Up/「风雅」的教导.png', '天赋培养素材', '\"天赋培养素材。<br>雷之国土的姿容是风雅。<br>风雅是不谄媚。风雅之人永远是高贵的。正似只愿高飞与雷暴争鸣的海鹰；迎合鄙俗则恰如将花冠倒插泥淖，仅让尊严染上了污渍。\"', '/img/upgrade/guide/i_416', '[\"菫色之庭\"]', NULL, '[]', NULL, '「风雅」的指引 = 「风雅」的教导x 3', '[\"ayaka\",\"sara\",\"itto\",\"ayato\",\"shinobu\"]', NULL);
INSERT INTO `breakthrough` VALUES (127, 'TalentLevel-Up', '「风雅」的指引', '3', '/AscensionMaterial/TalentLevel-Up/「风雅」的指引.png', '天赋培养素材', '\"天赋培养素材。<br>雷之国土的姿容是风雅。<br>风雅是不倨傲。风雅之人永远是谦虚的。只有消除虚荣与怠慢，才能明亮双眼，从哪怕最不堪入目的表象之下寻求美与雅致的真境。\"', '/img/upgrade/guide/i_417', '[\"菫色之庭\"]', NULL, '[]', '「风雅」的教导x 3', '「风雅」的哲学 = 「风雅」的指引x 3', '[\"ayaka\",\"traveler_boy_electro\",\"traveler_girl_electro\",\"sara\",\"itto\",\"ayato\",\"shinobu\"]', NULL);
INSERT INTO `breakthrough` VALUES (128, 'TalentLevel-Up', '「风雅」的哲学', '4', '/AscensionMaterial/TalentLevel-Up/「风雅」的哲学.png', '天赋培养素材', '\"天赋培养素材。<br>雷之国土的姿容是风雅。<br>风雅是不庸俗。风雅之人永远是独行的。不屈于平庸之恶，不感动于平庸之爱，不逃避于平庸的无知，才可能明辨何为雅致与鄙陋。\"', '/img/upgrade/guide/i_418', '[\"菫色之庭\"]', NULL, '[]', NULL, NULL, '[\"ayaka\",\"sara\",\"itto\",\"ayato\",\"shinobu\"]', NULL);
INSERT INTO `breakthrough` VALUES (129, 'TalentLevel-Up', '「自由」的教导', '2', '/AscensionMaterial/TalentLevel-Up/「自由」的教导.png', '天赋培养素材', '\"天赋培养素材。<br>风之国土的精神是自由。<br>自由其一，是生存的自由，即是能健全、健康地生活，而不必担心朝不保夕的自由。\"', '/img/upgrade/guide/i_421', '[\"忘却之峡\"]', NULL, '[]', NULL, '「自由」的指引 = 「自由」的教导x 3', '[\"amber\",\"barbara\",\"klee\",\"sucrose\",\"traveler_boy_anemo\",\"traveler_boy_geo\",\"tartaglia\",\"diona\",\"traveler_girl_anemo\",\"traveler_girl_geo\",\"aloy\"]', NULL);
INSERT INTO `breakthrough` VALUES (130, 'TalentLevel-Up', '「自由」的指引', '3', '/AscensionMaterial/TalentLevel-Up/「自由」的指引.png', '天赋培养素材', '\"天赋培养素材。<br>风之国土的精神是自由。<br>自由其二，是漫游的自由，即是能在苍翠的土地上自由地通行，而不受阻挠的自由。\"', '/img/upgrade/guide/i_422', '[\"忘却之峡\"]', NULL, '[]', '「自由」的教导x 3', '「自由」的哲学 = 「自由」的指引x 3', '[\"amber\",\"barbara\",\"klee\",\"sucrose\",\"tartaglia\",\"diona\",\"aloy\"]', NULL);
INSERT INTO `breakthrough` VALUES (131, 'TalentLevel-Up', '「自由」的哲学', '4', '/AscensionMaterial/TalentLevel-Up/「自由」的哲学.png', '天赋培养素材', '\"天赋培养素材。<br>风之国土的精神是自由。<br>自由其三，是歌唱的自由，即是能在风之神塑造的美好土地上歌唱，让自己的心与歌一同随风而去的自由。\"', '/img/upgrade/guide/i_423', '[\"忘却之峡\"]', NULL, '[]', NULL, NULL, '[\"amber\",\"barbara\",\"klee\",\"sucrose\",\"tartaglia\",\"diona\",\"aloy\"]', NULL);
INSERT INTO `breakthrough` VALUES (132, 'TalentLevel-Up', '「天光」的教导', '2', '/AscensionMaterial/TalentLevel-Up/「天光」的教导.png', '天赋培养素材', '\"天赋培养素材。<br>雷之国土的愿望是天光。<br>自认通透一切的大君，永远企图将高天之光彩尽收囊中。然而无从分享的愿景，却反令须臾之人更加炽烈地追求，犹如飞蛾扑火。\"', '/img/upgrade/guide/i_426', '[\"菫色之庭\"]', NULL, '[]', NULL, '「天光」的指引 = 「天光」的教导x 3', '[\"sayu\",\"raiden\",\"gorou\",\"yaemiko\"]', NULL);
INSERT INTO `breakthrough` VALUES (133, 'TalentLevel-Up', '「天光」的指引', '3', '/AscensionMaterial/TalentLevel-Up/「天光」的指引.png', '天赋培养素材', '\"天赋培养素材。<br>雷之国土的愿望是天光。<br>即使晖光被重重雷云所掩盖，鸣神的国土之上，依旧有志士如云雀般突破雷光与天之怒海，追寻天光之迹，哪怕迎来冷酷的殛灭。\"', '/img/upgrade/guide/i_427', '[\"菫色之庭\"]', NULL, '[]', '「天光」的教导x 3', '「天光」的哲学 = 「天光」的指引x 3', '[\"sayu\",\"raiden\",\"gorou\",\"yaemiko\"]', NULL);
INSERT INTO `breakthrough` VALUES (134, 'TalentLevel-Up', '「天光」的哲学', '4', '/AscensionMaterial/TalentLevel-Up/「天光」的哲学.png', '天赋培养素材', '\"天赋培养素材。<br>雷之国土的愿望是天光。<br>即使永远生活在地下，人也会不屈地祈求天光。人总是追逐超越，无可止步。静止封闭的永恒亦固然壮丽，但其本质终究是死亡。\"', '/img/upgrade/guide/i_428', '[\"菫色之庭\"]', NULL, '[]', NULL, NULL, '[\"sayu\",\"traveler_boy_electro\",\"traveler_girl_electro\",\"raiden\",\"gorou\",\"yaemiko\"]', NULL);
INSERT INTO `breakthrough` VALUES (135, 'TalentLevel-Up', '「黄金」的教导', '2', '/AscensionMaterial/TalentLevel-Up/「黄金」的教导.png', '天赋培养素材', '\"天赋培养素材。<br>岩之国土的象征是黄金。<br>黄金是流淌在璃月土地深处的血液，是鼓动璃月的心脏有力搏动的肌肉，是让璃月傲然挺立的筋骨。\"', '/img/upgrade/guide/i_431', '[\"太山府\"]', NULL, '[]', NULL, '「黄金」的指引 = 「黄金」的教导x 3', '[\"beidou\",\"xingqiu\",\"zhongli\",\"xinyan\",\"feiyan\"]', NULL);
INSERT INTO `breakthrough` VALUES (136, 'TalentLevel-Up', '「黄金」的指引', '3', '/AscensionMaterial/TalentLevel-Up/「黄金」的指引.png', '天赋培养素材', '\"天赋培养素材。<br>岩之国土的象征是黄金。<br>黄金意味着转换，这是璃月的岩山、大地、港城与人们的默契。在璃月，勤劳被转换为黄金，而黄金被转换为繁荣。\"', '/img/upgrade/guide/i_432', '[\"太山府\"]', NULL, '[]', '「黄金」的教导x 3', '「黄金」的哲学 = 「黄金」的指引x 3', '[\"beidou\",\"xingqiu\",\"zhongli\",\"xinyan\",\"feiyan\"]', NULL);
INSERT INTO `breakthrough` VALUES (137, 'TalentLevel-Up', '「黄金」的哲学', '4', '/AscensionMaterial/TalentLevel-Up/「黄金」的哲学.png', '天赋培养素材', '\"天赋培养素材。<br>岩之国土的象征是黄金。<br>黄金是璃月的财富。但璃月真正的财富，是璃月的人们那如黄金般闪耀的心灵。\"', '/img/upgrade/guide/i_433', '[\"太山府\"]', NULL, '[]', NULL, NULL, '[\"beidou\",\"xingqiu\",\"zhongli\",\"xinyan\",\"feiyan\"]', NULL);
INSERT INTO `breakthrough` VALUES (138, 'TalentLevel-Up', '「繁荣」的教导', '2', '/AscensionMaterial/TalentLevel-Up/「繁荣」的教导.png', '天赋培养素材', '\"天赋培养素材。<br>岩之国土的追求是繁荣。<br>繁荣是璃月的恩惠，是璃月的神与人共同建立的伟大城市，是璃月的大地安全和平的源泉。\"', '/img/upgrade/guide/i_441', '[\"太山府\"]', NULL, '[]', NULL, '「繁荣」的指引 = 「繁荣」的教导x 3', '[\"keqing\",\"ningguang\",\"qiqi\",\"xiao\",\"shenhe\",\"yelan\"]', NULL);
INSERT INTO `breakthrough` VALUES (139, 'TalentLevel-Up', '「繁荣」的指引', '3', '/AscensionMaterial/TalentLevel-Up/「繁荣」的指引.png', '天赋培养素材', '\"天赋培养素材。<br>岩之国土的追求是繁荣。<br>繁荣是璃月对它的子女的许诺：以繁荣回报勤劳者，以黄金照亮这片土地。\"', '/img/upgrade/guide/i_442', '[\"太山府\"]', NULL, '[]', '「繁荣」的教导x 3', '「繁荣」的哲学 = 「繁荣」的指引x 3', '[\"keqing\",\"ningguang\",\"qiqi\",\"xiao\",\"shenhe\",\"yelan\"]', NULL);
INSERT INTO `breakthrough` VALUES (140, 'TalentLevel-Up', '「繁荣」的哲学', '4', '/AscensionMaterial/TalentLevel-Up/「繁荣」的哲学.png', '天赋培养素材', '\"天赋培养素材。<br>岩之国土的追求是繁荣。<br>繁荣是璃月的过去、现在与未来。在此世间一切国度都无法企及的繁荣，均归于璃月，以及璃月的子女。\"', '/img/upgrade/guide/i_443', '[\"太山府\"]', NULL, '[]', NULL, NULL, '[\"keqing\",\"ningguang\",\"qiqi\",\"xiao\",\"shenhe\",\"yelan\"]', NULL);
INSERT INTO `breakthrough` VALUES (141, 'TalentLevel-Up', '「抗争」的教导', '2', '/AscensionMaterial/TalentLevel-Up/「抗争」的教导.png', '天赋培养素材', '\"天赋培养素材。<br>风之国土的脊梁是抗争。<br>蒙德的历史即是抗争的历史。抗争是为了蒙德过去的一切抗争不被遗忘，如同草木突破土壤，如同恒风洞穿石墙。\"', '/img/upgrade/guide/i_451', '[\"忘却之峡\"]', NULL, '[]', NULL, '「抗争」的指引 = 「抗争」的教导x 3', '[\"bennett\",\"diluc\",\"jean\",\"mona\",\"noelle\",\"razor\",\"eula\"]', NULL);
INSERT INTO `breakthrough` VALUES (142, 'TalentLevel-Up', '「抗争」的指引', '3', '/AscensionMaterial/TalentLevel-Up/「抗争」的指引.png', '天赋培养素材', '\"天赋培养素材。<br>风之国土的脊梁是抗争。<br>蒙德的历史即是抗争的历史。抗争是为了蒙德现在的人们能够拥有自由，正如风之神吹散冰雪，正如温妮莎奋起抗争。\"', '/img/upgrade/guide/i_452', '[\"忘却之峡\"]', NULL, '[]', '「抗争」的教导x 3', '「抗争」的哲学 = 「抗争」的指引x 3', '[\"bennett\",\"diluc\",\"jean\",\"mona\",\"noelle\",\"razor\",\"traveler_boy_anemo\",\"traveler_boy_geo\",\"eula\",\"traveler_girl_anemo\",\"traveler_girl_geo\"]', NULL);
INSERT INTO `breakthrough` VALUES (143, 'TalentLevel-Up', '「抗争」的哲学', '4', '/AscensionMaterial/TalentLevel-Up/「抗争」的哲学.png', '天赋培养素材', '\"天赋培养素材。<br>风之国土的脊梁是抗争。<br>蒙德的历史即是抗争的历史。抗争是为了蒙德未来的诗文，也能自由地随着眷顾的风，传到远方。\"', '/img/upgrade/guide/i_453', '[\"忘却之峡\"]', NULL, '[]', NULL, NULL, '[\"bennett\",\"diluc\",\"jean\",\"mona\",\"noelle\",\"razor\",\"eula\"]', NULL);
INSERT INTO `breakthrough` VALUES (144, 'TalentLevel-Up', '东风之翎', '5', '/AscensionMaterial/TalentLevel-Up/东风之翎.png', '角色培养素材', '\"从蒙德的东风之主身上，取下的一枚翎羽。<br>那是经历了千年之风的龙之羽。追求自由的本心使它几乎唤起轻风，好逃离现今的桎梏；但它的旧主对你的赏识，使它留在了你的掌心里。\"', '/img/upgrade/guide/i_461', '[\"70级以上风魔龙挑战奖励\"]', NULL, '[\"深入风龙废墟\"]', '东风之爪x 1+ 异梦溶媒x 1', '东风之爪 = 东风之翎x 1 异梦溶媒x 1', '[\"bennett\",\"diluc\",\"jean\"]', NULL);
INSERT INTO `breakthrough` VALUES (145, 'TalentLevel-Up', '东风之爪', '5', '/AscensionMaterial/TalentLevel-Up/东风之爪.png', '角色培养素材', '\"从蒙德的东风之主身上，取下的一丝锋锐。<br>因为过去撕裂了伟大魔龙「杜林」的血肉的缘故，过去蕴含着骇人的猛毒。但经过你的净化与治愈，其中躁动的毒性终于平息。\"', '/img/upgrade/guide/i_462', '[\"70级以上风魔龙挑战奖励\"]', NULL, '[\"深入风龙废墟\"]', '东风之翎x 1+ 异梦溶媒x 1', '东风之翎 = 东风之爪x 1 异梦溶媒x 1', '[\"lisa\",\"noelle\",\"razor\",\"xiangling\"]', NULL);
INSERT INTO `breakthrough` VALUES (146, 'TalentLevel-Up', '东风的吐息', '5', '/AscensionMaterial/TalentLevel-Up/东风的吐息.png', '角色培养素材', '\"从蒙德的东风之主唤起的狂风中，采来的些许力量。<br>它的身躯是优美而通透的，信风与龙卷都愿为它效力。只要乘风而行，风就是它的同伴、它的歌声与它的自由。<br>而它的敌人，就是一千种名字各异的风的敌人。\"', '/img/upgrade/guide/i_463', '[\"70级以上风魔龙挑战奖励\"]', NULL, '[\"深入风龙废墟\"]', '东风之翎x 1+ 异梦溶媒x 1', '东风之翎 = 东风的吐息x 1 异梦溶媒x 1', '[\"amber\",\"beidou\",\"chongyun\",\"traveler_boy_anemo\",\"traveler_boy_geo\",\"traveler_girl_anemo\",\"traveler_girl_geo\"]', NULL);
INSERT INTO `breakthrough` VALUES (147, 'TalentLevel-Up', '北风之尾', '5', '/AscensionMaterial/TalentLevel-Up/北风之尾.png', '角色培养素材', '\"从北风的伟大王狼身上，取来的些许尾鳞。<br>失去了生命与形体的北风王狼仍有超越凡俗的伟大力量。为了迎接挑战而用冰与风复现的形体也无法容下它的全部，少数灵魂附在了在战斗中脱落的冰鳞上。\"', '/img/upgrade/guide/i_464', '[\"70级以上北风狼挑战奖励\"]', NULL, '[]', '北风之环x 1+ 异梦溶媒x 1', '北风之环 = 北风之尾x 1 异梦溶媒x 1', '[\"qiqi\",\"venti\",\"xingqiu\"]', NULL);
INSERT INTO `breakthrough` VALUES (148, 'TalentLevel-Up', '北风之环', '5', '/AscensionMaterial/TalentLevel-Up/北风之环.png', '角色培养素材', '\"从北风的伟大王狼身上，取来的些许凝冰。<br>这片大地曾经北风呼啸，呼气凝霜。如砂砾的冰与雪在寒风中嚣叫，飞舞着将肌肤撕裂，让血液凝结。如今的北风王狼力量虽然不如往昔，却仍有能将大地冰封的力量。\"', '/img/upgrade/guide/i_465', '[\"70级以上北风狼挑战奖励\"]', NULL, '[]', '北风之尾x 1+ 异梦溶媒x 1', '北风之尾 = 北风之环x 1 异梦溶媒x 1', '[\"barbara\",\"keqing\",\"klee\",\"mona\"]', NULL);
INSERT INTO `breakthrough` VALUES (149, 'TalentLevel-Up', '北风的魂匣', '5', '/AscensionMaterial/TalentLevel-Up/北风的魂匣.png', '角色培养素材', '\"从北风的伟大王狼之灵处，取来的些许力量。<br>这片大地上曾经北风呼啸，呼气凝霜。但它在最终终于明白，酷烈的北风只能熄灭生命，不能滋养生命。在生命的末尾，它让自己的力量流入这片大地，永远守护大地与大地的住民。\"', '/img/upgrade/guide/i_466', '[\"70级以上北风狼挑战奖励\"]', NULL, '[]', '北风之尾x 1+ 异梦溶媒x 1', '北风之尾 = 北风的魂匣x 1 异梦溶媒x 1', '[\"fischl\",\"kaeya\",\"ningguang\",\"sucrose\"]', NULL);
INSERT INTO `breakthrough` VALUES (150, 'TalentLevel-Up', '吞天之鲸·只角', '5', '/AscensionMaterial/TalentLevel-Up/吞天之鲸·只角.png', '角色培养素材', '\"战胜解放了邪眼之力的「公子」后，取来的水鲸之角。<br>传说中，命之座的图形反映着人的气质。而巡弋漆黑夜空的星辰之鲸，恐怕映照出的是粉碎一切、吞噬一切的狂傲理念吧…<br>而接受了这一面的他，欣然唤出灭世的水之鲸，投身与你的战斗。\"', '/img/upgrade/guide/i_467', '[\"70级以上公子挑战奖励\"]', NULL, '[\"进入「黄金屋」\"]', '魔王之刃·残片x 1+ 异梦溶媒x 1', '魔王之刃·残片 = 吞天之鲸·只角x 1 异梦溶媒x 1', '[\"zhongli\",\"xinyan\",\"albedo\"]', NULL);
INSERT INTO `breakthrough` VALUES (151, 'TalentLevel-Up', '魔王之刃·残片', '5', '/AscensionMaterial/TalentLevel-Up/魔王之刃·残片.png', '角色培养素材', '\"战胜解放了邪眼之力的「公子」后，得到的武器破片。<br>因为曾经抵达无穷的至暗之地，以少年的双眼目睹了那个国度的漆黑，因此不可逆地染上了不应属于这个世界的深邃色彩。<br>纵使「邪眼」的力量与其无关，但物似主人形…\"', '/img/upgrade/guide/i_468', '[\"70级以上公子挑战奖励\"]', NULL, '[\"进入「黄金屋」\"]', '吞天之鲸·只角x 1+ 异梦溶媒x 1', '吞天之鲸·只角 = 魔王之刃·残片x 1 异梦溶媒x 1', '[\"tartaglia\",\"diona\",\"hutao\"]', NULL);
INSERT INTO `breakthrough` VALUES (152, 'TalentLevel-Up', '武炼之魂·孤影', '5', '/AscensionMaterial/TalentLevel-Up/武炼之魂·孤影.png', '角色培养素材', '\"战胜解放了邪眼之力的「公子」后，取来的些许力量。<br>经过无尽历练的气势与战意在全力的厮杀中喷涌而出，物质化的产物。<br>倘若人类拥有命运，那恐怕他的命运也为之歪曲了吧。否则，他又为何总是身处斗争的漩涡中心呢。\"', '/img/upgrade/guide/i_469', '[\"70级以上公子挑战奖励\"]', NULL, '[\"进入「黄金屋」\"]', '吞天之鲸·只角x 1+ 异梦溶媒x 1', '吞天之鲸·只角 = 武炼之魂·孤影x 1 异梦溶媒x 1', '[\"xiao\",\"ganyu\",\"rosaria\"]', NULL);
INSERT INTO `breakthrough` VALUES (153, 'TalentLevel-Up', '龙王之冕', '5', '/AscensionMaterial/TalentLevel-Up/龙王之冕.png', '角色培养素材', '\"千年刚玉凝成的结晶之角，乃是「龙王」的天生冠冕。<br>注之以精魄，塑之以顽岩。它自山涧而生，在群岩的国土彰示天地的威权，而黄金般的长角则是它在过去与某位君王分庭抗礼的标志。<br>曾一度裂折失色，如今却在你的手中闪烁着冰冷的黄金光泽。\"', '/img/upgrade/guide/i_470', '[\"70级以上若陀龙王挑战奖励\"]', NULL, '[\"「伏龙树」之底\"]', '血玉之枝x 1+ 异梦溶媒x 1', '血玉之枝 = 龙王之冕x 1 异梦溶媒x 1', '[\"eula\",\"yoimiya\",\"traveler_boy_electro\",\"traveler_girl_electro\"]', NULL);
INSERT INTO `breakthrough` VALUES (154, 'TalentLevel-Up', '血玉之枝', '5', '/AscensionMaterial/TalentLevel-Up/血玉之枝.png', '角色培养素材', '\"闪烁着特异色泽的骨质枝条，以「龙王」的力量滋养而生。<br>憎恨淌过天成的泉道，交汇冰冷的月光，令晶化的枝桠舒展而生…<br>即使在战斗中已被斩落，龙血滋养的枝叶仍在簌簌作响，永无宁时。\"', '/img/upgrade/guide/i_471', '[\"70级以上若陀龙王挑战奖励\"]', NULL, '[\"「伏龙树」之底\"]', '龙王之冕x 1+ 异梦溶媒x 1', '龙王之冕 = 血玉之枝x 1 异梦溶媒x 1', '[\"ayaka\",\"feiyan\"]', NULL);
INSERT INTO `breakthrough` VALUES (155, 'TalentLevel-Up', '鎏金之鳞', '5', '/AscensionMaterial/TalentLevel-Up/鎏金之鳞.png', '角色培养素材', '\"玄岩之中自然而生的鳞甲，坚韧而沉默，蕴含着「龙王」的威能。<br>在无数代蝼蚁归于腐土的岁月中，黄金与黑曜嵌入磐石造就的血肉，蔓延盘生而成甲胄。<br>炽热的痛感，无声的呼号，或许皆会在恩怨终结的一刻消散吧。\"', '/img/upgrade/guide/i_472', '[\"70级以上若陀龙王挑战奖励\"]', NULL, '[\"「伏龙树」之底\"]', '龙王之冕x 1+ 异梦溶媒x 1', '龙王之冕 = 鎏金之鳞x 1 异梦溶媒x 1', '[\"kazuha\",\"sayu\",\"yelan\"]', NULL);
INSERT INTO `breakthrough` VALUES (156, 'TalentLevel-Up', '熔毁之刻', '5', '/AscensionMaterial/TalentLevel-Up/熔毁之刻.png', '角色培养素材', '\"战胜解放了原本力量的「女士」后，得到的某种蕴含着强大力量的畸形碎片。<br>「我做了一个很长的梦。<br>「我梦见我与你，在我们缔造的纯白世界中再会。<br>「但我终于醒来了。我早已经被烈火吞没。你早已与毒血一同回归大地。<br>「不过都无所谓了。要不断往前走，就要烧毁身后的一切，就要相信他所说的永远纯白无暇的国度，在地平线的尽头，『陛下』的梦中，在他让我看见的『妄念』里。」\"', '/img/upgrade/guide/i_480', '[\"70级以上女士挑战奖励\"]', NULL, '[\"鸣神岛·天守\"]', '狱火之蝶x 1+ 异梦溶媒x 1', '狱火之蝶 = 熔毁之刻x 1 异梦溶媒x 1', '[\"raiden\",\"aloy\",\"gorou\"]', NULL);
INSERT INTO `breakthrough` VALUES (157, 'TalentLevel-Up', '狱火之蝶', '5', '/AscensionMaterial/TalentLevel-Up/狱火之蝶.png', '角色培养素材', '\"战胜解放了原本力量的「女士」后，得到的某种蕴含着温暖的力量的畸形碎片。<br>应激的冰之封印被打破，过往的意志冲破苍白的囚牢，她再度想起自己原本的名字时，不死蝶会随着血肉焚毁一同逸散飞舞。而她也想起了漫游大地，散布烈焰，烧尽一切罪恶的过去，以及自己未能再见的那个人的面貌。\"', '/img/upgrade/guide/i_481', '[\"70级以上女士挑战奖励\"]', NULL, '[\"鸣神岛·天守\"]', '熔毁之刻x 1+ 异梦溶媒x 1', '熔毁之刻 = 狱火之蝶x 1 异梦溶媒x 1', '[\"kokomi\",\"thoma\",\"shenhe\"]', NULL);
INSERT INTO `breakthrough` VALUES (158, 'TalentLevel-Up', '灰烬之心', '5', '/AscensionMaterial/TalentLevel-Up/灰烬之心.png', '角色培养素材', '\"战胜解放了原本力量的「女士」后，得到的某种蕴含着强大思念的畸形碎片。<br>即使是泪水也无法使火焰熄灭。铁水般炽热的哀伤与愤怒在她的血管中奔涌，从泪腺中迸发。<br>在她的心脏被永恒之火烧尽前，最初的愚者找到了她。他明白，只有更加深刻的仇恨与妄执，才能熄灭吞噬她的火焰，让她成为自己的同伴…\"', '/img/upgrade/guide/i_482', '[\"70级以上女士挑战奖励\"]', NULL, '[\"鸣神岛·天守\"]', '熔毁之刻x 1+ 异梦溶媒x 1', '熔毁之刻 = 灰烬之心x 1 异梦溶媒x 1', '[\"sara\",\"itto\",\"yunjin\"]', NULL);
INSERT INTO `breakthrough` VALUES (159, 'TalentLevel-Up', '凶将之手眼', '5', '/AscensionMaterial/TalentLevel-Up/凶将之手眼.png', '角色培养素材', '\"从净土中取回来的人偶关节，这一部分乃是象征着「所见即所为」的手眼造型。<br>已经化做祸神的「她」，同样可以驾驭此等手眼神通。目光所见之瞬间，便已斩出一刀。<br>然而万劫太过久远，凶将之骸亦得春生。\"', '/img/upgrade/guide/i_483', '[\"70级以上永恒的守护者挑战奖励\"]', NULL, '[\"梦想乐土之殁\"]', '祸神之禊泪x 1+ 异梦溶媒x 1', '祸神之禊泪 = 凶将之手眼x 1 异梦溶媒x 1', '[\"ayato\"]', NULL);
INSERT INTO `breakthrough` VALUES (160, 'TalentLevel-Up', '祸神之禊泪', '5', '/AscensionMaterial/TalentLevel-Up/祸神之禊泪.png', '角色培养素材', '\"从净土中取回的人偶关节，这一部分乃是「恶曜之眼」的造型。<br>仅仅依靠凝视就可以降下灾祸与诅咒，因此把人生之不顺归咎于「祸神」似乎理所当然。但是好好想想吧，倘若意志足够强大，信念足够坚定…<br>万劫太过久远，凶星恶曜亦能以泪涤净自身。\"', '/img/upgrade/guide/i_484', '[\"70级以上永恒的守护者挑战奖励\"]', NULL, '[\"梦想乐土之殁\"]', '凶将之手眼x 1+ 异梦溶媒x 1', '凶将之手眼 = 祸神之禊泪x 1 异梦溶媒x 1', '[\"shinobu\"]', NULL);
INSERT INTO `breakthrough` VALUES (161, 'TalentLevel-Up', '万劫之真意', '5', '/AscensionMaterial/TalentLevel-Up/万劫之真意.png', '角色培养素材', '\"在一切尘埃落定之后，从净土带回的小小头饰。<br>一念则万劫尽，总有思念可以超越时间。<br>百千万劫，「真」实之意，不知道是否传达到了。\"', '/img/upgrade/guide/i_485', '[\"70级以上永恒的守护者挑战奖励\"]', NULL, '[\"梦想乐土之殁\"]', '凶将之手眼x 1+ 异梦溶媒x 1', '凶将之手眼 = 万劫之真意x 1 异梦溶媒x 1', '[\"yaemiko\"]', NULL);
INSERT INTO `breakthrough` VALUES (162, 'TalentLevel-Up', '符纹之齿', '4', '/AscensionMaterial/TalentLevel-Up/符纹之齿.png', '角色培养素材', '\"神秘的蛇状古代机械用于掘削山壁的「齿」，其上篆刻着无解的不祥符文。<br>相传在大地的深黑裂缝中潜藏着庞大的虺类，它们永远怀着对地上世界的憎恨与恶意，抱着不可知的险恶目的，发掘着宏大的隧道…\"', '/img/upgrade/guide/i_486', '[\"测试\"]', NULL, '[]', NULL, NULL, '[\"yelan\",\"shinobu\"]', NULL);
INSERT INTO `breakthrough` VALUES (163, 'TalentLevel-Up', '智识之冕', '5', '/AscensionMaterial/TalentLevel-Up/智识之冕.png', '天赋培养素材', '\"提升天赋所需的珍贵素材。<br>远古时期，承载与传授智识所用的仪式器具。尽管如今，智慧居于言语及典籍中，但「智识之冕」中残存的力量与智慧仍能给如今的人们带来伟大的突破吧。\"', '/img/upgrade/guide/i_491', '[\"限时活动奖励\"]', NULL, '[]', '', '', '[]', NULL);
INSERT INTO `breakthrough` VALUES (164, 'Secondary', '浮游干核', '1', '/AscensionMaterial/Secondary/浮游干核.png', '角色培养素材', '\"能让飘浮灵浮游的能量核，其中含有些微的元素。<br>大部分的能量似乎已经逸散了。\"', '/img/upgrade/material/i_185', '[\"飘浮灵掉落\"]', '[\"水飘浮灵Lv 0+\",\"岩飘浮灵Lv 0+\",\"风飘浮灵Lv 0+\",\"冰飘浮灵Lv 0+\",\"雷飘浮灵Lv 0+\",\"火飘浮灵Lv 0+\"]', NULL, '浮游幽核 = 浮游干核x 3', NULL, '[\"kokomi\",\"aloy\",\"gorou\",\"shinobu\"]', '[\"「渔获」\",\"破魔之弓\",\"掠食者\",\"曚云之月\",\"飞雷之弦振\",\"冬极白星\",\"若水\",\"白辰之环\",\"证誓之明瞳\",\"不灭月华\",\"神乐之真意\"]');
INSERT INTO `breakthrough` VALUES (165, 'Secondary', '浮游幽核', '2', '/AscensionMaterial/Secondary/浮游幽核.png', '角色培养素材', '\"能让飘浮灵浮游的能量核。<br>这种元素生物似乎有奇异的习性，会周期性大量涌现、消失。\"', '/img/upgrade/material/i_186', '[\"合成获得\"]', '[\"水飘浮灵Lv 40+\",\"岩飘浮灵Lv 40+\",\"风飘浮灵Lv 40+\",\"冰飘浮灵Lv 40+\",\"雷飘浮灵Lv 40+\",\"火飘浮灵Lv 40+\"]', NULL, '浮游干核x 3', '浮游晶化核 = 浮游幽核x 3', '[\"kokomi\",\"aloy\",\"gorou\",\"shinobu\"]', '[\"「渔获」\",\"曚云之月\",\"冬极白星\",\"若水\",\"证誓之明瞳\",\"不灭月华\",\"神乐之真意\"]');
INSERT INTO `breakthrough` VALUES (166, 'Secondary', '浮游晶化核', '3', '/AscensionMaterial/Secondary/浮游晶化核.png', '角色培养素材', '\"击败飘浮灵后有时能发现的完整的能量核。<br>在热衷于给任何事物都分个高下的人当中，经常发生这样的争论：飘浮灵和风史莱姆的浮力，究竟孰高孰低。\"', '/img/upgrade/material/i_187', '[\"合成获得\"]', '[\"水飘浮灵Lv 60+\",\"岩飘浮灵Lv 60+\",\"风飘浮灵Lv 60+\",\"冰飘浮灵Lv 60+\",\"雷飘浮灵Lv 60+\",\"火飘浮灵Lv 60+\"]', NULL, '浮游幽核x 3', NULL, '[\"kokomi\",\"aloy\",\"gorou\",\"shinobu\"]', '[\"「渔获」\",\"曚云之月\",\"冬极白星\",\"若水\",\"证誓之明瞳\",\"不灭月华\",\"神乐之真意\"]');
INSERT INTO `breakthrough` VALUES (167, 'ElementalStones', '异梦溶媒', '4', '/AscensionMaterial/ElementalStones/异梦溶媒.png', '消耗品', '\"能够使追忆与试炼中取得的角色培养素材发生变质，使其变为所需要的形态。<br>曾经人们相信灵魂和记忆也是有其质料的。如果曾经有人梦到过天堂，醒来后取出了一朵花，那它应该就是由此构成的。异梦溶媒可以溶解回忆中取得的东西，并且将其变成另一种梦。<br>与强者交手的回忆，的确成为了更强之路的砖石。\"', '/img/upgrade/gem/i_209', '[]', '[]', NULL, NULL, NULL, '[]', NULL);
INSERT INTO `breakthrough` VALUES (168, 'Primary', '高塔孤王的破瓦', '2', '/AscensionMaterial/Secondary/高塔孤王的破瓦.png', '武器突破素材', '\"赋予武器突破之力的材料。<br>最近被称为风龙废墟的地方，曾经是烈风的魔神「迭卡拉庇安」建造的王都。因这繁华之梦全是魔神之力所造，因此梦碎瞬间的破瓦断片也有强大的力量。\"', '/img/upgrade/weapon/i_501', '[\"塞西莉亚苗圃\"]', '[\"塞西莉亚苗圃\"]', NULL, '高塔孤王的残垣 = 高塔孤王的破瓦x 3', NULL, '[]', '[\"无锋剑\",\"银剑\",\"冷刃\",\"西风剑\",\"宗室长剑\",\"暗巷闪光\",\"辰砂之纺锤\",\"风鹰剑\",\"铁影阔剑\",\"钟剑\",\"雪葬的星银\",\"松籁响起之时\",\"鸦羽弓\",\"绝弦\",\"苍翠猎弓\",\"幽夜华尔兹\",\"学徒笔记\",\"口袋魔导书\",\"魔导绪论\",\"西风秘典\",\"宗室秘法录\"]');
INSERT INTO `breakthrough` VALUES (169, 'Primary', '高塔孤王的残垣', '3', '/AscensionMaterial/Secondary/高塔孤王的残垣.png', '武器突破素材', '\"赋予武器突破之力的材料。<br>烈风的魔神「迭卡拉庇安」对自己所造的王城十分满意，他在高塔上接受所有追随者的跪拜。但他却不知道，众人对他俯身并非出于敬仰爱戴。\"', '/img/upgrade/weapon/i_502', '[\"塞西莉亚苗圃\"]', '[\"塞西莉亚苗圃\"]', NULL, '高塔孤王的破瓦x 3', '高塔孤王的断片 = 高塔孤王的残垣x 3', '[]', '[\"无锋剑\",\"银剑\",\"冷刃\",\"西风剑\",\"宗室长剑\",\"暗巷闪光\",\"辰砂之纺锤\",\"风鹰剑\",\"铁影阔剑\",\"钟剑\",\"雪葬的星银\",\"松籁响起之时\",\"鸦羽弓\",\"绝弦\",\"苍翠猎弓\",\"幽夜华尔兹\",\"学徒笔记\",\"口袋魔导书\",\"魔导绪论\",\"西风秘典\",\"宗室秘法录\"]');
INSERT INTO `breakthrough` VALUES (170, 'Primary', '高塔孤王的断片', '4', '/AscensionMaterial/Secondary/高塔孤王的断片.png', '武器突破素材', '\"赋予武器突破之力的材料。<br>「安德留斯」曾经对高塔的孤王宣战，却也无法损伤王都分毫。若不是「自由」高歌的瞬间让这座城市土崩瓦解，这一切都会继续永远之梦。\"', '/img/upgrade/weapon/i_503', '[\"塞西莉亚苗圃\"]', '[\"塞西莉亚苗圃\"]', NULL, '高塔孤王的残垣x 3', '高塔孤王的碎梦 = 高塔孤王的断片x 3', '[]', '[\"无锋剑\",\"银剑\",\"冷刃\",\"西风剑\",\"宗室长剑\",\"暗巷闪光\",\"辰砂之纺锤\",\"风鹰剑\",\"铁影阔剑\",\"钟剑\",\"雪葬的星银\",\"松籁响起之时\",\"鸦羽弓\",\"绝弦\",\"苍翠猎弓\",\"幽夜华尔兹\",\"学徒笔记\",\"口袋魔导书\",\"魔导绪论\",\"西风秘典\",\"宗室秘法录\"]');
INSERT INTO `breakthrough` VALUES (171, 'Primary', '高塔孤王的碎梦', '5', '/AscensionMaterial/Secondary/高塔孤王的碎梦.png', '武器突破素材', '\"赋予武器突破之力的材料。<br>众人对高塔上的孤王俯身并非出于敬仰爱戴，而是被它的烈风吹得无法起身。这是巴巴托斯崛起之前，风神之一柱的野心与碎梦。<br>它是如此用力地做梦，因此梦的碎片也带着强大的力量。\"', '/img/upgrade/weapon/i_504', '[\"塞西莉亚苗圃\"]', '[\"塞西莉亚苗圃\"]', NULL, '高塔孤王的断片x 3', NULL, '[]', '[\"无锋剑\",\"银剑\",\"冷刃\",\"西风剑\",\"宗室长剑\",\"暗巷闪光\",\"辰砂之纺锤\",\"风鹰剑\",\"铁影阔剑\",\"钟剑\",\"雪葬的星银\",\"松籁响起之时\",\"鸦羽弓\",\"绝弦\",\"苍翠猎弓\",\"幽夜华尔兹\",\"学徒笔记\",\"口袋魔导书\",\"魔导绪论\",\"西风秘典\",\"宗室秘法录\"]');
INSERT INTO `breakthrough` VALUES (172, 'Primary', '孤云寒林的光砂', '2', '/AscensionMaterial/Secondary/孤云寒林的光砂.png', '武器突破素材', '\"赋予武器突破之力的材料。<br>孤云阁耸立的岩柱，曾经都是摩拉克斯为了镇伏敌手降下的岩枪。久而久之，魔神之体渗入了岩柱，使之拥有了圣性。岩柱偶尔会剥落一些散发着怨恨和不祥的碎屑。\"', '/img/upgrade/weapon/i_511', '[\"震雷连山密宫\"]', '[\"震雷连山密宫\"]', NULL, '孤云寒林的辉岩 = 孤云寒林的光砂x 3', NULL, '[]', '[\"暗铁剑\",\"黑岩长剑\",\"匣里龙吟\",\"斫峰之刃\",\"石英大剑\",\"千岩古剑\",\"白影剑\",\"白缨枪\",\"流月针\",\"和璞鸢\",\"弹弓\",\"黑岩战弓\",\"弓藏\",\"陨龙之梦\",\"若水\",\"翡玉法球\",\"黑岩绯玉\",\"匣里日月\"]');
INSERT INTO `breakthrough` VALUES (173, 'Primary', '孤云寒林的辉岩', '3', '/AscensionMaterial/Secondary/孤云寒林的辉岩.png', '武器突破素材', '\"赋予武器突破之力的材料。<br>所以对于知情人士来说，孤云阁并不是什么愉快的海上奇观，而是曾经璃月众神的墓园。这里的确会周期性地涌起不祥和污秽，不过那都被另一个降妖伏魔的少年平息了。\"', '/img/upgrade/weapon/i_512', '[\"震雷连山密宫\"]', '[\"震雷连山密宫\"]', NULL, '孤云寒林的光砂x 3', '孤云寒林的圣骸 = 孤云寒林的辉岩x 3', '[]', '[\"暗铁剑\",\"黑岩长剑\",\"匣里龙吟\",\"斫峰之刃\",\"石英大剑\",\"千岩古剑\",\"白影剑\",\"白缨枪\",\"流月针\",\"和璞鸢\",\"弹弓\",\"黑岩战弓\",\"弓藏\",\"陨龙之梦\",\"若水\",\"翡玉法球\",\"黑岩绯玉\",\"匣里日月\"]');
INSERT INTO `breakthrough` VALUES (174, 'Primary', '孤云寒林的圣骸', '4', '/AscensionMaterial/Secondary/孤云寒林的圣骸.png', '武器突破素材', '\"赋予武器突破之力的材料。<br>岩枪的核心是发着暗淡光辉的核心，那是战败魔神的力量深入岩石形成的。他们的怨恨并不针对摩拉克斯，因为他们早就已经没有自我了，不可能还残留私怨。\"', '/img/upgrade/weapon/i_513', '[\"震雷连山密宫\"]', '[\"震雷连山密宫\"]', NULL, '孤云寒林的辉岩x 3', '孤云寒林的神体 = 孤云寒林的圣骸x 3', '[]', '[\"暗铁剑\",\"黑岩长剑\",\"匣里龙吟\",\"斫峰之刃\",\"石英大剑\",\"千岩古剑\",\"白影剑\",\"白缨枪\",\"流月针\",\"和璞鸢\",\"弹弓\",\"黑岩战弓\",\"弓藏\",\"陨龙之梦\",\"若水\",\"翡玉法球\",\"黑岩绯玉\",\"匣里日月\"]');
INSERT INTO `breakthrough` VALUES (175, 'Primary', '孤云寒林的神体', '5', '/AscensionMaterial/Secondary/孤云寒林的神体.png', '武器突破素材', '\"赋予武器突破之力的材料。<br>于此躁动、作祟的，正是嗟叹连绵的众神圣骸之体。它们是未能实现的理想，对人类另一种幸福生活的描绘。它们以深海、层岩收纳，却不愿意就此入梦。\"', '/img/upgrade/weapon/i_514', '[\"震雷连山密宫\"]', '[\"震雷连山密宫\"]', NULL, '孤云寒林的圣骸x 3', NULL, '[]', '[\"暗铁剑\",\"黑岩长剑\",\"匣里龙吟\",\"斫峰之刃\",\"石英大剑\",\"千岩古剑\",\"白影剑\",\"白缨枪\",\"流月针\",\"和璞鸢\",\"弹弓\",\"黑岩战弓\",\"弓藏\",\"陨龙之梦\",\"若水\",\"翡玉法球\",\"黑岩绯玉\",\"匣里日月\"]');
INSERT INTO `breakthrough` VALUES (176, 'Primary', '凛风奔狼的始龀', '2', '/AscensionMaterial/Secondary/凛风奔狼的始龀.png', '武器突破素材', '\"赋予武器突破之力的材料。<br>群狼是受它祝福的近卫军，就算是小狼的乳牙也有相当的潜力。<br>曾经的众神皆背负爱人的责任。因此引导群狼，却只收养弃婴、接纳流浪者的「安德留斯」十分异类。\"', '/img/upgrade/weapon/i_521', '[\"塞西莉亚苗圃\"]', '[\"塞西莉亚苗圃\"]', NULL, '凛风奔狼的裂齿 = 凛风奔狼的始龀x 3', NULL, '[]', '[\"黎明神剑\",\"黑剑\",\"笛剑\",\"降临之剑\",\"天空之刃\",\"训练大剑\",\"佣兵重剑\",\"沐浴龙血的剑\",\"祭礼大剑\",\"天空之傲\",\"决斗之枪\",\"龙脊长枪\",\"猎弓\",\"历练的猎弓\",\"神射手之誓\",\"祭礼弓\",\"天空之翼\",\"终末嗟叹之诗\",\"讨龙英杰谭\",\"流浪乐章\",\"暗巷的酒与诗\",\"嘟嘟可故事集\",\"天空之卷\"]');
INSERT INTO `breakthrough` VALUES (177, 'Primary', '凛风奔狼的裂齿', '3', '/AscensionMaterial/Secondary/凛风奔狼的裂齿.png', '武器突破素材', '\"赋予武器突破之力的材料。<br>狼的生活经常伴有厮杀，断骨裂齿的死斗也不罕见。<br>「安德留斯」认为人类只会带来失望，但是纯真的婴儿是无辜的。狼群选择了孩子，孩子如果也选择了狼，那么他们就结成了「卢皮卡」——命运的家族。\"', '/img/upgrade/weapon/i_522', '[\"塞西莉亚苗圃\"]', '[\"塞西莉亚苗圃\"]', NULL, '凛风奔狼的始龀x 3', '凛风奔狼的断牙 = 凛风奔狼的裂齿x 3', '[]', '[\"黎明神剑\",\"黑剑\",\"笛剑\",\"降临之剑\",\"天空之刃\",\"训练大剑\",\"佣兵重剑\",\"沐浴龙血的剑\",\"祭礼大剑\",\"天空之傲\",\"决斗之枪\",\"龙脊长枪\",\"猎弓\",\"历练的猎弓\",\"神射手之誓\",\"祭礼弓\",\"天空之翼\",\"终末嗟叹之诗\",\"讨龙英杰谭\",\"流浪乐章\",\"暗巷的酒与诗\",\"嘟嘟可故事集\",\"天空之卷\"]');
INSERT INTO `breakthrough` VALUES (178, 'Primary', '凛风奔狼的断牙', '4', '/AscensionMaterial/Secondary/凛风奔狼的断牙.png', '武器突破素材', '\"赋予武器突破之力的材料。<br>狼群也充分理解，人如果没有同类会感到孤独。光荣的断牙是狼们离别的赠礼，据说真的有保护平安的魔力。<br>在遥远世界的传说里，有一只母狼领养了伟大的双子。狼与人所同住的「家」则被称为「狼之洞」，也就是「卢佩卡」，与这个世界的「卢皮卡」同义。\"', '/img/upgrade/weapon/i_523', '[\"塞西莉亚苗圃\"]', '[\"塞西莉亚苗圃\"]', NULL, '凛风奔狼的裂齿x 3', '凛风奔狼的怀乡 = 凛风奔狼的断牙x 3', '[]', '[\"黎明神剑\",\"黑剑\",\"笛剑\",\"降临之剑\",\"天空之刃\",\"训练大剑\",\"佣兵重剑\",\"沐浴龙血的剑\",\"祭礼大剑\",\"天空之傲\",\"决斗之枪\",\"龙脊长枪\",\"猎弓\",\"历练的猎弓\",\"神射手之誓\",\"祭礼弓\",\"天空之翼\",\"终末嗟叹之诗\",\"讨龙英杰谭\",\"流浪乐章\",\"暗巷的酒与诗\",\"嘟嘟可故事集\",\"天空之卷\"]');
INSERT INTO `breakthrough` VALUES (179, 'Primary', '凛风奔狼的怀乡', '5', '/AscensionMaterial/Secondary/凛风奔狼的怀乡.png', '武器突破素材', '\"赋予武器突破之力的材料。<br>认为自己讨厌人类的奔狼之王，自觉无法去描绘人类的幸福生活，因此没有资格成为尘世的风之王。于是它选择了消失。但是事实不是这样的，它看着被抛弃之人的眼神是如此温柔。\"', '/img/upgrade/weapon/i_524', '[\"塞西莉亚苗圃\"]', '[\"塞西莉亚苗圃\"]', NULL, '凛风奔狼的断牙x 3', NULL, '[]', '[\"黎明神剑\",\"黑剑\",\"笛剑\",\"降临之剑\",\"天空之刃\",\"训练大剑\",\"佣兵重剑\",\"沐浴龙血的剑\",\"祭礼大剑\",\"天空之傲\",\"决斗之枪\",\"龙脊长枪\",\"猎弓\",\"历练的猎弓\",\"神射手之誓\",\"祭礼弓\",\"天空之翼\",\"终末嗟叹之诗\",\"讨龙英杰谭\",\"流浪乐章\",\"暗巷的酒与诗\",\"嘟嘟可故事集\",\"天空之卷\"]');
INSERT INTO `breakthrough` VALUES (180, 'Primary', '雾海云间的铅丹', '2', '/AscensionMaterial/Secondary/雾海云间的铅丹.png', '武器突破素材', '\"赋予武器突破之力的材料。<br>绝云间乃是仙家府地。而相传仙家除了三眼五显神通，可以操纵尘世的元素之力外，另有三项不传秘：丹鼎、符箓与外景。\"', '/img/upgrade/weapon/i_531', '[\"震雷连山密宫\"]', '[\"震雷连山密宫\"]', NULL, '雾海云间的汞丹 = 雾海云间的铅丹x 3', NULL, '[]', '[\"吃虎鱼刀\",\"试作斩岩\",\"磐岩结绿\",\"以理服人\",\"黑岩斩刀\",\"雨裁\",\"无工之剑\",\"钺矛\",\"黑岩刺枪\",\"匣里灭辰\",\"宗室猎枪\",\"息灾\",\"信使\",\"试作澹月\",\"甲级宝珏\",\"试作金珀\",\"昭心\"]');
INSERT INTO `breakthrough` VALUES (181, 'Primary', '雾海云间的汞丹', '3', '/AscensionMaterial/Secondary/雾海云间的汞丹.png', '武器突破素材', '\"赋予武器突破之力的材料。<br>符箓可以让仙人力量借符号残留，外景可以用心像造出宇宙洞天。丹鼎之术，则一直笼罩在秘密之中。毕竟尝试吞下这些金丹的话，结果必定十死无生。\"', '/img/upgrade/weapon/i_532', '[\"震雷连山密宫\"]', '[\"震雷连山密宫\"]', NULL, '雾海云间的铅丹x 3', '雾海云间的金丹 = 雾海云间的汞丹x 3', '[]', '[\"吃虎鱼刀\",\"试作斩岩\",\"磐岩结绿\",\"以理服人\",\"黑岩斩刀\",\"雨裁\",\"无工之剑\",\"钺矛\",\"黑岩刺枪\",\"匣里灭辰\",\"宗室猎枪\",\"息灾\",\"信使\",\"试作澹月\",\"甲级宝珏\",\"试作金珀\",\"昭心\"]');
INSERT INTO `breakthrough` VALUES (182, 'Primary', '雾海云间的金丹', '4', '/AscensionMaterial/Secondary/雾海云间的金丹.png', '武器突破素材', '\"赋予武器突破之力的材料。<br>其实所谓金丹，真的是字面意思的金属之丹。这些都是仙人们制造的合金，融合了神通之力，可以给与武器超越的力量。武人似乎都觉得武道优于仙道，不怎么访神问仙。所以金丹并没什么机会给出去。\"', '/img/upgrade/weapon/i_533', '[\"震雷连山密宫\"]', '[\"震雷连山密宫\"]', NULL, '雾海云间的汞丹x 3', '雾海云间的转还 = 雾海云间的金丹x 3', '[]', '[\"吃虎鱼刀\",\"试作斩岩\",\"磐岩结绿\",\"以理服人\",\"黑岩斩刀\",\"雨裁\",\"无工之剑\",\"钺矛\",\"黑岩刺枪\",\"匣里灭辰\",\"宗室猎枪\",\"息灾\",\"信使\",\"试作澹月\",\"甲级宝珏\",\"试作金珀\",\"昭心\"]');
INSERT INTO `breakthrough` VALUES (183, 'Primary', '雾海云间的转还', '5', '/AscensionMaterial/Secondary/雾海云间的转还.png', '武器突破素材', '\"赋予武器突破之力的材料。<br>这是金丹的最高品质，它模拟了混沌初开、清浊不分、天地一体的状态。据说这个是天地创世的缩影。当然，那位是知道的，世界的重现不是这样的。\"', '/img/upgrade/weapon/i_534', '[\"震雷连山密宫\"]', '[\"震雷连山密宫\"]', NULL, '雾海云间的金丹x 3', NULL, '[]', '[\"吃虎鱼刀\",\"试作斩岩\",\"磐岩结绿\",\"以理服人\",\"黑岩斩刀\",\"雨裁\",\"无工之剑\",\"钺矛\",\"黑岩刺枪\",\"匣里灭辰\",\"宗室猎枪\",\"息灾\",\"信使\",\"试作澹月\",\"甲级宝珏\",\"试作金珀\",\"昭心\"]');
INSERT INTO `breakthrough` VALUES (184, 'Primary', '狮牙斗士的枷锁', '2', '/AscensionMaterial/Secondary/狮牙斗士的枷锁.png', '武器突破素材', '\"赋予武器突破之力的材料。<br>人们总是会对英雄的故事过分夸大啦。甚至是曾经束缚英雄的枷锁，都被升华到了自由之敌的高度。<br>因此这些锁链也拥有了非凡的力量。\"', '/img/upgrade/weapon/i_541', '[\"塞西莉亚苗圃\"]', '[\"塞西莉亚苗圃\"]', NULL, '狮牙斗士的铁链 = 狮牙斗士的枷锁x 3', NULL, '[]', '[\"旅行剑\",\"祭礼剑\",\"腐殖之剑\",\"苍古自由之誓\",\"白铁大剑\",\"西风大剑\",\"宗室大剑\",\"狼的末路\",\"新手长枪\",\"铁尖枪\",\"西风长枪\",\"天空之脊\",\"反曲弓\",\"暗巷猎手\",\"西风猎弓\",\"宗室长弓\",\"风花之颂\",\"阿莫斯之弓\",\"异世界行记\",\"祭礼残章\",\"忍冬之果\",\"四风原典\"]');
INSERT INTO `breakthrough` VALUES (185, 'Primary', '狮牙斗士的铁链', '3', '/AscensionMaterial/Secondary/狮牙斗士的铁链.png', '武器突破素材', '\"赋予武器突破之力的材料。<br>曾经施在大英雄温妮莎身上的枷锁——或者这么说吧，那个时代里，所有剑斗士都会背负镣铐。狮牙的斗士也只是其中一员。\"', '/img/upgrade/weapon/i_542', '[\"塞西莉亚苗圃\"]', '[\"塞西莉亚苗圃\"]', NULL, '狮牙斗士的枷锁x 3', '狮牙斗士的镣铐 = 狮牙斗士的铁链x 3', '[]', '[\"旅行剑\",\"祭礼剑\",\"腐殖之剑\",\"苍古自由之誓\",\"白铁大剑\",\"西风大剑\",\"宗室大剑\",\"狼的末路\",\"新手长枪\",\"铁尖枪\",\"西风长枪\",\"天空之脊\",\"反曲弓\",\"暗巷猎手\",\"西风猎弓\",\"宗室长弓\",\"风花之颂\",\"阿莫斯之弓\",\"异世界行记\",\"祭礼残章\",\"忍冬之果\",\"四风原典\"]');
INSERT INTO `breakthrough` VALUES (186, 'Primary', '狮牙斗士的镣铐', '4', '/AscensionMaterial/Secondary/狮牙斗士的镣铐.png', '武器突破素材', '\"赋予武器突破之力的材料。<br>温妮莎并不是被镣铐或者锁链困住的，她想要挣脱枷锁和镣铐的话，随时都能击碎蒙德融造的普通金属。毕竟这里既没有优秀的矿石，也没有故乡的神火。<br>锁住她的，是守护族人的责任。\"', '/img/upgrade/weapon/i_543', '[\"塞西莉亚苗圃\"]', '[\"塞西莉亚苗圃\"]', NULL, '狮牙斗士的铁链x 3', '狮牙斗士的理想 = 狮牙斗士的镣铐x 3', '[]', '[\"旅行剑\",\"祭礼剑\",\"腐殖之剑\",\"苍古自由之誓\",\"白铁大剑\",\"西风大剑\",\"宗室大剑\",\"狼的末路\",\"新手长枪\",\"铁尖枪\",\"西风长枪\",\"天空之脊\",\"反曲弓\",\"暗巷猎手\",\"西风猎弓\",\"宗室长弓\",\"风花之颂\",\"阿莫斯之弓\",\"异世界行记\",\"祭礼残章\",\"忍冬之果\",\"四风原典\"]');
INSERT INTO `breakthrough` VALUES (187, 'Primary', '狮牙斗士的理想', '5', '/AscensionMaterial/Secondary/狮牙斗士的理想.png', '武器突破素材', '\"赋予武器突破之力的材料。<br>温妮莎从奴役之中得到了自由。结果锁住她的东西越来越大了：从族人变成了整个蒙德，甚至变成了「自由」，乃至这个世界。<br>她是发自真心这么希望着的：愿这个世界变得更加坚强一些。\"', '/img/upgrade/weapon/i_544', '[\"塞西莉亚苗圃\"]', '[\"塞西莉亚苗圃\"]', NULL, '狮牙斗士的镣铐x 3', NULL, '[]', '[\"旅行剑\",\"祭礼剑\",\"腐殖之剑\",\"苍古自由之誓\",\"白铁大剑\",\"西风大剑\",\"宗室大剑\",\"狼的末路\",\"新手长枪\",\"铁尖枪\",\"西风长枪\",\"天空之脊\",\"反曲弓\",\"暗巷猎手\",\"西风猎弓\",\"宗室长弓\",\"风花之颂\",\"阿莫斯之弓\",\"异世界行记\",\"祭礼残章\",\"忍冬之果\",\"四风原典\"]');
INSERT INTO `breakthrough` VALUES (188, 'Primary', '漆黑陨铁的一粒', '2', '/AscensionMaterial/Secondary/漆黑陨铁的一粒.png', '武器突破素材', '\"赋予武器突破之力的材料。<br>曾经在璃月云来海之外落下过一个漆黑陨铁，据说也是为了降伏魔兽妖物。但是因为沾染了强大的污秽之血，这块材料的特性已经不再是纯粹的岩王帝君之力了。\"', '/img/upgrade/weapon/i_551', '[\"震雷连山密宫\"]', '[\"震雷连山密宫\"]', NULL, '漆黑陨铁的一片 = 漆黑陨铁的一粒x 3', NULL, '[]', '[\"飞天御剑\",\"铁蜂刺\",\"飞天大御剑\",\"试作古华\",\"螭骨剑\",\"衔珠海皇\",\"黑缨枪\",\"「旗杆」\",\"千岩长枪\",\"试作星镰\",\"贯虹之槊\",\"护摩之杖\",\"黑檀弓\",\"钢轮弓\",\"落霞\",\"琥珀玥\",\"万国诸海图谱\",\"尘世之锁\"]');
INSERT INTO `breakthrough` VALUES (189, 'Primary', '漆黑陨铁的一片', '3', '/AscensionMaterial/Secondary/漆黑陨铁的一片.png', '武器突破素材', '\"赋予武器突破之力的材料。<br>璃月之地向来都有海怪的传说，毕竟彼方是超越提瓦特的未知区域了。没有了尘世七神的守护，外面不知道是怎样的一团混沌。能把岩王帝君的力量都染黑的，正是提瓦特秩序之外的力量。\"', '/img/upgrade/weapon/i_552', '[\"震雷连山密宫\"]', '[\"震雷连山密宫\"]', NULL, '漆黑陨铁的一粒x 3', '漆黑陨铁的一角 = 漆黑陨铁的一片x 3', '[]', '[\"飞天御剑\",\"铁蜂刺\",\"飞天大御剑\",\"试作古华\",\"螭骨剑\",\"衔珠海皇\",\"黑缨枪\",\"「旗杆」\",\"千岩长枪\",\"试作星镰\",\"贯虹之槊\",\"护摩之杖\",\"黑檀弓\",\"钢轮弓\",\"落霞\",\"琥珀玥\",\"万国诸海图谱\",\"尘世之锁\"]');
INSERT INTO `breakthrough` VALUES (190, 'Primary', '漆黑陨铁的一角', '4', '/AscensionMaterial/Secondary/漆黑陨铁的一角.png', '武器突破素材', '\"赋予武器突破之力的材料。<br>提瓦特沿海的诸国把超越七神庇护的外部称之为「暗之外海」，据说许多战败的魔神不愿意活在新的七神秩序之下，于是逃到了远岛，化作了邪神。不过它们的力量和岩王帝君的力量本就同源，和这个吞噬一切的漆黑并不一样。\"', '/img/upgrade/weapon/i_553', '[\"震雷连山密宫\"]', '[\"震雷连山密宫\"]', NULL, '漆黑陨铁的一片x 3', '漆黑陨铁的一块 = 漆黑陨铁的一角x 3', '[]', '[\"飞天御剑\",\"铁蜂刺\",\"飞天大御剑\",\"试作古华\",\"螭骨剑\",\"衔珠海皇\",\"黑缨枪\",\"「旗杆」\",\"千岩长枪\",\"试作星镰\",\"贯虹之槊\",\"护摩之杖\",\"黑檀弓\",\"钢轮弓\",\"落霞\",\"琥珀玥\",\"万国诸海图谱\",\"尘世之锁\"]');
INSERT INTO `breakthrough` VALUES (191, 'Primary', '漆黑陨铁的一块', '5', '/AscensionMaterial/Secondary/漆黑陨铁的一块.png', '武器突破素材', '\"赋予武器突破之力的材料。<br>坎瑞亚覆灭的时代，某位大罪人制造了无数的魔兽。它们流淌着与此世不容的黑血，在大陆上践踏众生、破坏一切。它们的生命是变质的，由世界外的力量赐予的。硬要说的话，袭击蒙德的「杜林」也是此类。\"', '/img/upgrade/weapon/i_554', '[\"震雷连山密宫\"]', '[\"震雷连山密宫\"]', NULL, '漆黑陨铁的一角x 3', NULL, '[]', '[\"飞天御剑\",\"铁蜂刺\",\"飞天大御剑\",\"试作古华\",\"螭骨剑\",\"衔珠海皇\",\"黑缨枪\",\"「旗杆」\",\"千岩长枪\",\"试作星镰\",\"贯虹之槊\",\"护摩之杖\",\"黑檀弓\",\"钢轮弓\",\"落霞\",\"琥珀玥\",\"万国诸海图谱\",\"尘世之锁\"]');
INSERT INTO `breakthrough` VALUES (192, 'Primary', '远海夷地的瑚枝', '2', '/AscensionMaterial/Secondary/远海夷地的瑚枝.png', '武器突破素材', '\"赋予武器突破之力的材料。<br>稻妻列岛中距离鸣神岛最遥远的「海祇」，在海祇岛的古语中的意思是「统管诸海的大神」。传说大蛇在常夜的渊下国土中初次现身时，身上覆满了四色的珊瑚枝，散发着煌煌的荧光。\"', '/img/upgrade/weapon/i_561', '[\"砂流之庭\"]', '[\"砂流之庭\"]', NULL, '远海夷地的玉枝 = 远海夷地的瑚枝x 3', NULL, '[]', '[\"天目影打刀\",\"雾切之回光\",\"恶王丸\",\"白辰之环\",\"证誓之明瞳\",\"不灭月华\"]');
INSERT INTO `breakthrough` VALUES (193, 'Primary', '远海夷地的玉枝', '3', '/AscensionMaterial/Secondary/远海夷地的玉枝.png', '武器突破素材', '\"赋予武器突破之力的材料。<br>这种珊瑚在提瓦特的大地上是见不到的，是大蛇闯入「暗之外海」时，获得的礼赠。因此对于深海的大蛇而言，身负珊瑚枝象征着力量；而失去珊瑚枝时，力量会相应地折损。换言之，折断的珊瑚枝中也蕴含着超常的力量。\"', '/img/upgrade/weapon/i_562', '[\"砂流之庭\"]', '[\"砂流之庭\"]', NULL, '远海夷地的瑚枝x 3', '远海夷地的琼枝 = 远海夷地的玉枝x 3', '[]', '[\"天目影打刀\",\"雾切之回光\",\"恶王丸\",\"白辰之环\",\"证誓之明瞳\",\"不灭月华\"]');
INSERT INTO `breakthrough` VALUES (194, 'Primary', '远海夷地的琼枝', '4', '/AscensionMaterial/Secondary/远海夷地的琼枝.png', '武器突破素材', '\"赋予武器突破之力的材料。<br>失去了一切、逃向暗之外海的魔神在海渊中，见到了什么都没有的弃民。于是它决定留下来，成为他们的「远吕羽氏尊」、「海祇大御神」。<br>传说大蛇神曾经折下身上所有的珊瑚枝，让蜷缩在黑暗中的孩子们拥有照亮周遭的光明。又有一说它用折下的珊瑚枝架起了登高的阶梯，让它的孩子们再次回到了地面上，见到阳光。\"', '/img/upgrade/weapon/i_563', '[\"砂流之庭\"]', '[\"砂流之庭\"]', NULL, '远海夷地的玉枝x 3', '远海夷地的金枝 = 远海夷地的琼枝x 3', '[]', '[\"天目影打刀\",\"雾切之回光\",\"恶王丸\",\"白辰之环\",\"证誓之明瞳\",\"不灭月华\"]');
INSERT INTO `breakthrough` VALUES (195, 'Primary', '远海夷地的金枝', '5', '/AscensionMaterial/Secondary/远海夷地的金枝.png', '武器突破素材', '\"赋予武器突破之力的材料。<br>「海祇」之名所背负的含义，归根到底只是曾栖身在深海中的弃民，对引领他们的蛇神一厢情愿的期许罢了。<br>也正是因为有了仰望自己的子民，所以蛇神不得不留在本应逃离的世界里，折下身上的珊瑚枝，向自己不应涉足的土地入侵，与自己无法抗衡的敌手战斗，直到神体与山丘一同被斩断；直到鲜血化为电浆；直到思念与力量沦为永远无法消散的「祟神」。\"', '/img/upgrade/weapon/i_564', '[\"砂流之庭\"]', '[\"砂流之庭\"]', NULL, '远海夷地的琼枝x 3', NULL, '[]', '[\"天目影打刀\",\"雾切之回光\",\"恶王丸\",\"白辰之环\",\"证誓之明瞳\",\"不灭月华\"]');
INSERT INTO `breakthrough` VALUES (196, 'Primary', '鸣神御灵的明惠', '2', '/AscensionMaterial/Secondary/鸣神御灵的明惠.png', '武器突破素材', '\"赋予武器突破之力的材料。<br>在过去，先民会攀登如今被称为影向山的高峰，将雷电灼烧过的树木雕琢成曲勾形，为照亮高天的闪电、震撼大地的鸣雷赋予造型。这个形象在漫长岁月的积累中，最终演化成了「雷之三重巴」的纹样，象征着雷的眷顾、智慧与威能，以及代行此等眷顾、智慧与威能的人们。\"', '/img/upgrade/weapon/i_571', '[\"砂流之庭\"]', '[\"砂流之庭\"]', NULL, '鸣神御灵的欢喜 = 鸣神御灵的明惠x 3', NULL, '[]', '[\"波乱月白经津\",\"桂木斩长正\",\"赤角石溃杵\",\"破魔之弓\",\"掠食者\",\"曚云之月\",\"飞雷之弦振\"]');
INSERT INTO `breakthrough` VALUES (197, 'Primary', '鸣神御灵的欢喜', '3', '/AscensionMaterial/Secondary/鸣神御灵的欢喜.png', '武器突破素材', '\"赋予武器突破之力的材料。<br>在鸣神的幕府中设有奉行之名，乃是诸类事务的管理与执行者。在妖物悠长久远的故事中，在过去，深受将军殿下亲近信赖者会佩戴这种曲勾造型的护符；而奉行之名也取自欢喜奉行之意，得到她青睐的人，也会回报以爱与忠义。而从某一时点开始，一切都不再是原本的模样了。\"', '/img/upgrade/weapon/i_572', '[\"砂流之庭\"]', '[\"砂流之庭\"]', NULL, '鸣神御灵的明惠x 3', '鸣神御灵的亲爱 = 鸣神御灵的欢喜x 3', '[]', '[\"波乱月白经津\",\"桂木斩长正\",\"赤角石溃杵\",\"破魔之弓\",\"掠食者\",\"曚云之月\",\"飞雷之弦振\"]');
INSERT INTO `breakthrough` VALUES (198, 'Primary', '鸣神御灵的亲爱', '4', '/AscensionMaterial/Secondary/鸣神御灵的亲爱.png', '武器突破素材', '\"赋予武器突破之力的材料。<br>漫游在荒野中，或是与人一同生活的妖物都会被「雷之三重巴」所代表的御建鸣神主尊所吸引。纵使他们的寿命远远久于万物灵长，也有终结的一日。要在有限的寿命中求得永恒与无限，就只能骥望于「永恒」能记住他们了。而她也回应了他们的愿望，将所有的敌手与友人铭记于心。据说，无论是栖息在迷雾当中、撕裂天空的魔性之枭，胆敢擅闯御苑的怪化狸，还是那位武艺高强容貌如月、最终却对自己露出尖牙的鬼族少女；无论是曾经展开黑翼自在空行的天狗，抑或是与她同行，最终又永远消失的「狐斋宫大人」…<br>无数的故事静静地沉睡在她的心中，终有一日会在她所梦想的永恒净土中灼灼发光吧。\"', '/img/upgrade/weapon/i_573', '[\"砂流之庭\"]', '[\"砂流之庭\"]', NULL, '鸣神御灵的欢喜x 3', '鸣神御灵的勇武 = 鸣神御灵的亲爱x 3', '[]', '[\"波乱月白经津\",\"桂木斩长正\",\"赤角石溃杵\",\"破魔之弓\",\"掠食者\",\"曚云之月\",\"飞雷之弦振\"]');
INSERT INTO `breakthrough` VALUES (199, 'Primary', '鸣神御灵的勇武', '5', '/AscensionMaterial/Secondary/鸣神御灵的勇武.png', '武器突破素材', '\"赋予武器突破之力的材料。<br>雷电之尊的重宝在于威严，威严由勇武与震怒体现，震怒源于心中的爱执，勇武则是震怒的权现。最终，无论何者，只要敢于挡住望向永恒的目光，只要敢于伤害稻妻的子民，就会成为她的敌人。<br>据说御灵有四魂，仙狐有三匹，名刀有二本；而御建鸣神主尊大御所大人的威严之相，只会呈现为流星光底的无想一线。\"', '/img/upgrade/weapon/i_574', '[\"砂流之庭\"]', '[\"砂流之庭\"]', NULL, '鸣神御灵的亲爱x 3', NULL, '[]', '[\"波乱月白经津\",\"桂木斩长正\",\"赤角石溃杵\",\"破魔之弓\",\"掠食者\",\"曚云之月\",\"飞雷之弦振\"]');
INSERT INTO `breakthrough` VALUES (200, 'Primary', '今昔剧画之恶尉', '2', '/AscensionMaterial/Secondary/今昔剧画之恶尉.png', '武器突破素材', '\"赋予武器突破之力的材料。<br>民话传说中，过去有名鬼人「虎千代」。武艺高强，曾有在刀剑一千柄当中狂舞，被割破的十二单如同菫花般零落，身体却未受一丝伤害的说法。后来却对将军大人露出獠牙，拔刀反叛，被斩下一臂一角后遁逃，最终郁愤发狂，自裁而殁。<br>狂言称怀抱执妄之鬼往往内心惊悸而忧患。妄想永恒却恐惧须臾，总会为恐惧与怨怒吞没，是为自业自得。这种面具刻画的就是露出尖牙的虎千代。\"', '/img/upgrade/weapon/i_581', '[\"砂流之庭\"]', '[\"砂流之庭\"]', NULL, '今昔剧画之虎啮 = 今昔剧画之恶尉x 3', NULL, '[]', '[\"喜多院十文字\",\"「渔获」\",\"断浪长鳍\",\"薙草之稻光\",\"冬极白星\",\"神乐之真意\"]');
INSERT INTO `breakthrough` VALUES (201, 'Primary', '今昔剧画之虎啮', '3', '/AscensionMaterial/Secondary/今昔剧画之虎啮.png', '武器突破素材', '\"赋予武器突破之力的材料。<br>《菫染山月虎啮鉴》中描绘的鬼人「虎千代」，曾以利齿咬碎将军的薙刀，取得过一时的上风。因此以他为原型制造的面具上往往有精心雕琢的尖牙。<br>在其结局中，负伤之兽般遁入林野，连清凉的月光也避之唯恐不及。穿过窄巷与峡谷的风声像是受伤的鬼在低声哀嚎，因此也有「虎千代风」这一季语。\"', '/img/upgrade/weapon/i_582', '[\"砂流之庭\"]', '[\"砂流之庭\"]', NULL, '今昔剧画之恶尉x 3', '今昔剧画之一角 = 今昔剧画之虎啮x 3', '[]', '[\"喜多院十文字\",\"「渔获」\",\"断浪长鳍\",\"薙草之稻光\",\"冬极白星\",\"神乐之真意\"]');
INSERT INTO `breakthrough` VALUES (202, 'Primary', '今昔剧画之一角', '4', '/AscensionMaterial/Secondary/今昔剧画之一角.png', '武器突破素材', '\"赋予武器突破之力的材料。<br>在鬼族失落的子守奉公子守歌中，鬼人「虎千代」是一位翩翩少年，拥有优雅勇健的身姿与华丽的容貌。他原本是将军麾下的爱将，曾忠心追随她深入漆黑的渊薮，击退邪秽之物，为血脉日渐稀薄的鬼族争取功绩。<br>纵使如今这样的歌谣已经无人传唱了，以另一种形象流传下来的有角鬼面形象仍然有着非凡的力量。\"', '/img/upgrade/weapon/i_583', '[\"砂流之庭\"]', '[\"砂流之庭\"]', NULL, '今昔剧画之虎啮x 3', '今昔剧画之鬼人 = 今昔剧画之一角x 3', '[]', '[\"喜多院十文字\",\"「渔获」\",\"断浪长鳍\",\"薙草之稻光\",\"冬极白星\",\"神乐之真意\"]');
INSERT INTO `breakthrough` VALUES (203, 'Primary', '今昔剧画之鬼人', '5', '/AscensionMaterial/Secondary/今昔剧画之鬼人.png', '武器突破素材', '\"赋予武器突破之力的材料。<br>背上「雷之三重巴」的纹样时名为「千代」的鬼族女武者，面对漆黑的军势时，曾被虎躯蛇尾的世外之兽吞下。最后她撕开魔兽的胸腔，得以幸存。<br>这是「虎啮的千代」之名的来由。在此后的岁月中，这一名号渐渐简化为「虎千代」。<br>但在深邃之兽的腹中，她染上了深罪的黯色，也透过猩红的利齿看见同行者被撕碎。沉湎于漆黑景色的她最终向御建鸣神主尊拔剑；后来，她持刀之臂与尖角被斩落，如同负伤的野兽般从城中逃向林野；再后来似乎是被天狗，或是终末番，或是山中修行的岩藏之胤当成不认得的怪物，收拾掉了罢，因为她俊美的容貌已经因为漆黑的仇恨与负伤痛苦变得狰狞扭曲。抑或是遇见了大蛇遗骸附近的鬼面执剑人形，故此结束了命运的旅途。<br>昔日曾与渊薮战斗者难免梦见漆黑的剧画。曾与魔兽厮杀又沦为当中一员者亦不在少数。世界的边缘正在变得稀薄而脆弱，这种侵蚀或许并非是单向的。\"', '/img/upgrade/weapon/i_584', '[\"砂流之庭\"]', '[\"砂流之庭\"]', NULL, '今昔剧画之一角x 3', NULL, '[]', '[\"喜多院十文字\",\"「渔获」\",\"断浪长鳍\",\"薙草之稻光\",\"冬极白星\",\"神乐之真意\"]');
INSERT INTO `breakthrough` VALUES (204, 'Secondary', '沉重号角', '2', '/AscensionMaterial/Primary/沉重号角.png', '角色培养素材', '\"粗砺的号角，是丘丘人用来警告同伴的道具。现在散布在号角上的战损使其失去了发声的功能。\"', '/img/upgrade/material/i_61', '[\"大体格丘丘人掉落\"]', '[\"木盾丘丘暴徒Lv 0+\",\"火斧丘丘暴徒Lv 0+\",\"岩盾丘丘暴徒Lv 0+\",\"丘丘霜铠王Lv 0+\",\"丘丘岩盔王Lv 0+\",\"冰盾丘丘暴徒Lv 0+\",\"雷斧丘丘暴徒Lv 0+\",\"丘丘雷兜王Lv 0+\"]', '[]', '黑铜号角 = 沉重号角x 3', NULL, '[]', '[\"无锋剑\",\"银剑\",\"冷刃\",\"西风剑\",\"宗室长剑\",\"暗巷闪光\",\"腐殖之剑\",\"风鹰剑\",\"铁影阔剑\",\"钟剑\",\"雪葬的星银\",\"松籁响起之时\",\"鸦羽弓\",\"绝弦\",\"苍翠猎弓\",\"幽夜华尔兹\",\"终末嗟叹之诗\",\"学徒笔记\",\"口袋魔导书\",\"魔导绪论\",\"西风秘典\",\"宗室秘法录\"]');
INSERT INTO `breakthrough` VALUES (205, 'Secondary', '黑铜号角', '3', '/AscensionMaterial/Primary/黑铜号角.png', '角色培养素材', '\"金属制的魔物号角。通常只能从格外强壮的丘丘人身上获得，因为鸣响号角是需要气力的事情。\"', '/img/upgrade/material/i_62', '[\"合成获得\"]', '[\"木盾丘丘暴徒Lv 40+\",\"火斧丘丘暴徒Lv 40+\",\"岩盾丘丘暴徒Lv 40+\",\"丘丘霜铠王Lv 40+\",\"丘丘岩盔王Lv 40+\",\"冰盾丘丘暴徒Lv 40+\",\"雷斧丘丘暴徒Lv 40+\",\"丘丘雷兜王Lv 40+\"]', '[]', '沉重号角x 3', '黑晶号角 = 黑铜号角x 3', '[]', '[\"无锋剑\",\"银剑\",\"冷刃\",\"西风剑\",\"宗室长剑\",\"暗巷闪光\",\"腐殖之剑\",\"风鹰剑\",\"铁影阔剑\",\"钟剑\",\"雪葬的星银\",\"松籁响起之时\",\"鸦羽弓\",\"绝弦\",\"苍翠猎弓\",\"幽夜华尔兹\",\"终末嗟叹之诗\",\"学徒笔记\",\"口袋魔导书\",\"魔导绪论\",\"西风秘典\",\"宗室秘法录\"]');
INSERT INTO `breakthrough` VALUES (206, 'Secondary', '黑晶号角', '4', '/AscensionMaterial/Primary/黑晶号角.png', '角色培养素材', '\"隐隐反射着不祥光芒的金属号角，饰有材质不明的黑晶。几乎没有使用过，推测是丘丘人重要的仪式器具。\"', '/img/upgrade/material/i_63', '[\"合成获得\"]', '[\"木盾丘丘暴徒Lv 60+\",\"火斧丘丘暴徒Lv 60+\",\"岩盾丘丘暴徒Lv 60+\",\"丘丘霜铠王Lv 60+\",\"丘丘岩盔王Lv 60+\",\"冰盾丘丘暴徒Lv 60+\",\"雷斧丘丘暴徒Lv 60+\",\"丘丘雷兜王Lv 60+\"]', '[]', '黑铜号角x 3', NULL, '[]', '[\"无锋剑\",\"银剑\",\"冷刃\",\"西风剑\",\"宗室长剑\",\"暗巷闪光\",\"腐殖之剑\",\"风鹰剑\",\"铁影阔剑\",\"钟剑\",\"雪葬的星银\",\"松籁响起之时\",\"鸦羽弓\",\"绝弦\",\"苍翠猎弓\",\"幽夜华尔兹\",\"终末嗟叹之诗\",\"学徒笔记\",\"口袋魔导书\",\"魔导绪论\",\"西风秘典\",\"宗室秘法录\"]');
INSERT INTO `breakthrough` VALUES (207, 'Secondary', '地脉的旧枝', '2', '/AscensionMaterial/Primary/地脉的旧枝.png', '角色培养素材', '\"来自深邃地底的脆弱枯枝。即使饱经岁月，也能从斑驳的树皮下，看出尚未散尽的力量。\"', '/img/upgrade/material/i_71', '[\"深渊法师掉落\"]', '[\"火深渊法师Lv 0+\",\"冰深渊法师Lv 0+\",\"水深渊法师Lv 0+\",\"雷深渊法师Lv 0+\"]', '[]', '地脉的枯叶 = 地脉的旧枝x 3', NULL, '[]', '[\"黎明神剑\",\"黑剑\",\"笛剑\",\"降临之剑\",\"天空之刃\",\"训练大剑\",\"佣兵重剑\",\"沐浴龙血的剑\",\"祭礼大剑\",\"天空之傲\",\"决斗之枪\",\"护摩之杖\",\"猎弓\",\"历练的猎弓\",\"神射手之誓\",\"祭礼弓\",\"风花之颂\",\"天空之翼\",\"讨龙英杰谭\",\"流浪乐章\",\"暗巷的酒与诗\",\"嘟嘟可故事集\",\"天空之卷\"]');
INSERT INTO `breakthrough` VALUES (208, 'Secondary', '地脉的枯叶', '3', '/AscensionMaterial/Primary/地脉的枯叶.png', '角色培养素材', '\"来自深邃地底的植物枝条。即使远离它的原主，其中也残留着低沉脉动的力量。\"', '/img/upgrade/material/i_72', '[\"合成获得\"]', '[\"火深渊法师Lv 40+\",\"冰深渊法师Lv 40+\",\"水深渊法师Lv 40+\",\"雷深渊法师Lv 40+\"]', '[]', '地脉的旧枝x 3', '地脉的新芽 = 地脉的枯叶x 3', '[]', '[\"黎明神剑\",\"黑剑\",\"笛剑\",\"降临之剑\",\"天空之刃\",\"训练大剑\",\"佣兵重剑\",\"沐浴龙血的剑\",\"祭礼大剑\",\"天空之傲\",\"决斗之枪\",\"护摩之杖\",\"猎弓\",\"历练的猎弓\",\"神射手之誓\",\"祭礼弓\",\"风花之颂\",\"天空之翼\",\"讨龙英杰谭\",\"流浪乐章\",\"暗巷的酒与诗\",\"嘟嘟可故事集\",\"天空之卷\"]');
INSERT INTO `breakthrough` VALUES (209, 'Secondary', '地脉的新芽', '4', '/AscensionMaterial/Primary/地脉的新芽.png', '角色培养素材', '\"据说有一株巨木曾经贯彻了世界的各处；据说这枝是它的一部分。仿佛从未被人折下带到遥远的地表，仍然充满了勃发的生机，长出了新叶。\"', '/img/upgrade/material/i_73', '[\"合成获得\"]', '[\"火深渊法师Lv 60+\",\"冰深渊法师Lv 60+\",\"水深渊法师Lv 60+\",\"雷深渊法师Lv 60+\"]', '[]', '地脉的枯叶x 3', NULL, '[]', '[\"黎明神剑\",\"黑剑\",\"笛剑\",\"降临之剑\",\"天空之刃\",\"训练大剑\",\"佣兵重剑\",\"沐浴龙血的剑\",\"祭礼大剑\",\"天空之傲\",\"决斗之枪\",\"护摩之杖\",\"猎弓\",\"历练的猎弓\",\"神射手之誓\",\"祭礼弓\",\"风花之颂\",\"天空之翼\",\"讨龙英杰谭\",\"流浪乐章\",\"暗巷的酒与诗\",\"嘟嘟可故事集\",\"天空之卷\"]');
INSERT INTO `breakthrough` VALUES (210, 'Secondary', '混沌装置', '2', '/AscensionMaterial/Primary/混沌装置.png', '角色培养素材', '\"来自不再活动的古代遗迹构装体。曾经是维系它身躯的某个部位，有着精妙的工学美感。\"', '/img/upgrade/material/i_81', '[\"遗迹守卫掉落\",\"遗迹猎者掉落\"]', '[\"遗迹守卫Lv 0+\",\"遗迹猎者Lv 0+\",\"遗迹重机Lv 0+\"]', '[]', '混沌回路 = 混沌装置x 3', NULL, '[]', '[\"旅行剑\",\"祭礼剑\",\"辰砂之纺锤\",\"苍古自由之誓\",\"白铁大剑\",\"西风大剑\",\"宗室大剑\",\"狼的末路\",\"新手长枪\",\"铁尖枪\",\"西风长枪\",\"天空之脊\",\"反曲弓\",\"暗巷猎手\",\"西风猎弓\",\"宗室长弓\",\"阿莫斯之弓\",\"异世界行记\",\"祭礼残章\",\"忍冬之果\",\"四风原典\"]');
INSERT INTO `breakthrough` VALUES (211, 'Secondary', '混沌回路', '3', '/AscensionMaterial/Primary/混沌回路.png', '角色培养素材', '\"来自不再活动的古代遗迹构装体。曾经是驱使它行动的逻辑回路。其中蕴含的伟大技术，如今已经无人可解。\"', '/img/upgrade/material/i_82', '[\"合成获得\",\"40级以上遗迹猎者掉落\",\"合成获得\"]', '[\"遗迹守卫Lv 40+\",\"遗迹猎者Lv 40+\",\"遗迹重机Lv 40+\"]', '[]', '混沌装置x 3', '混沌炉心 = 混沌回路x 3', '[]', '[\"旅行剑\",\"祭礼剑\",\"辰砂之纺锤\",\"苍古自由之誓\",\"白铁大剑\",\"西风大剑\",\"宗室大剑\",\"狼的末路\",\"新手长枪\",\"铁尖枪\",\"西风长枪\",\"天空之脊\",\"反曲弓\",\"暗巷猎手\",\"西风猎弓\",\"宗室长弓\",\"阿莫斯之弓\",\"异世界行记\",\"祭礼残章\",\"忍冬之果\",\"四风原典\"]');
INSERT INTO `breakthrough` VALUES (212, 'Secondary', '混沌炉心', '4', '/AscensionMaterial/Primary/混沌炉心.png', '角色培养素材', '\"来自不再活动的古代遗迹构装体。曾经是驱动它活动的能源之心。若能理解、重构这样的神秘技术，或许能改变这个世界也说不定。\"', '/img/upgrade/material/i_83', '[\"合成获得\",\"60级以上遗迹猎者掉落\",\"合成获得\"]', '[\"遗迹守卫Lv 60+\",\"遗迹猎者Lv 60+\",\"遗迹重机Lv 60+\"]', '[]', '混沌回路x 3', NULL, '[]', '[\"旅行剑\",\"祭礼剑\",\"辰砂之纺锤\",\"苍古自由之誓\",\"白铁大剑\",\"西风大剑\",\"宗室大剑\",\"狼的末路\",\"新手长枪\",\"铁尖枪\",\"西风长枪\",\"天空之脊\",\"反曲弓\",\"暗巷猎手\",\"西风猎弓\",\"宗室长弓\",\"阿莫斯之弓\",\"异世界行记\",\"祭礼残章\",\"忍冬之果\",\"四风原典\"]');
INSERT INTO `breakthrough` VALUES (213, 'Secondary', '雾虚花粉', '2', '/AscensionMaterial/Primary/雾虚花粉.png', '角色培养素材', '\"雾虚草在密闭空间中会产生的奇妙孢子，是飞萤喜好的食物。\"', '/img/upgrade/material/i_91', '[\"愚人众·萤术士掉落\"]', '[\"愚人众·雷萤术士Lv 0+\",\"愚人众·冰萤术士Lv 0+\"]', '[]', '雾虚草囊 = 雾虚花粉x 3', NULL, '[]', '[\"吃虎鱼刀\",\"试作斩岩\",\"磐岩结绿\",\"以理服人\",\"黑岩斩刀\",\"雨裁\",\"无工之剑\",\"钺矛\",\"黑岩刺枪\",\"匣里灭辰\",\"宗室猎枪\",\"龙脊长枪\",\"息灾\",\"信使\",\"试作澹月\",\"甲级宝珏\",\"试作金珀\",\"昭心\"]');
INSERT INTO `breakthrough` VALUES (214, 'Secondary', '雾虚草囊', '3', '/AscensionMaterial/Primary/雾虚草囊.png', '角色培养素材', '\"保存完好的雾虚草。有的人会利用飞萤对雾虚草的喜好，来控制它们。\"', '/img/upgrade/material/i_92', '[\"合成获得\"]', '[\"愚人众·雷萤术士Lv 40+\",\"愚人众·冰萤术士Lv 40+\"]', '[]', '雾虚花粉x 3', '雾虚灯芯 = 雾虚草囊x 3', '[]', '[\"吃虎鱼刀\",\"试作斩岩\",\"磐岩结绿\",\"以理服人\",\"黑岩斩刀\",\"雨裁\",\"无工之剑\",\"钺矛\",\"黑岩刺枪\",\"匣里灭辰\",\"宗室猎枪\",\"龙脊长枪\",\"息灾\",\"信使\",\"试作澹月\",\"甲级宝珏\",\"试作金珀\",\"昭心\"]');
INSERT INTO `breakthrough` VALUES (215, 'Secondary', '雾虚灯芯', '4', '/AscensionMaterial/Primary/雾虚灯芯.png', '角色培养素材', '\"将本就罕见的雾虚草集结成的束，会散发出幽晦的冥光；提着这种灯的人，则会召来飞萤与不幸。\"', '/img/upgrade/material/i_93', '[\"合成获得\"]', '[\"愚人众·雷萤术士Lv 60+\",\"愚人众·冰萤术士Lv 60+\"]', '[]', '雾虚草囊x 3', NULL, '[]', '[\"吃虎鱼刀\",\"试作斩岩\",\"磐岩结绿\",\"以理服人\",\"黑岩斩刀\",\"雨裁\",\"无工之剑\",\"钺矛\",\"黑岩刺枪\",\"匣里灭辰\",\"宗室猎枪\",\"龙脊长枪\",\"息灾\",\"信使\",\"试作澹月\",\"甲级宝珏\",\"试作金珀\",\"昭心\"]');
INSERT INTO `breakthrough` VALUES (216, 'Secondary', '猎兵祭刀', '2', '/AscensionMaterial/Primary/猎兵祭刀.png', '角色培养素材', '\"某种锋利的合金武具。即便失去了原本的主人，仍反射着令人不安的寒光。\"', '/img/upgrade/material/i_101', '[\"债务处理人掉落\"]', '[\"愚人众·火之债务处理人Lv 0+\"]', '[]', '特工祭刀 = 猎兵祭刀x 3', NULL, '[]', '[\"暗铁剑\",\"黑岩长剑\",\"匣里龙吟\",\"斫峰之刃\",\"石英大剑\",\"千岩古剑\",\"白影剑\",\"白缨枪\",\"流月针\",\"和璞鸢\",\"弹弓\",\"黑岩战弓\",\"弓藏\",\"落霞\",\"陨龙之梦\",\"翡玉法球\",\"黑岩绯玉\",\"匣里日月\"]');
INSERT INTO `breakthrough` VALUES (217, 'Secondary', '特工祭刀', '3', '/AscensionMaterial/Primary/特工祭刀.png', '角色培养素材', '\"利用至冬国优越的技术制造的异形兵器，曾经属于某位资深特工。一般人不经训练，根本无法使用这种奇怪的武具。\"', '/img/upgrade/material/i_102', '[\"合成获得\"]', '[\"愚人众·火之债务处理人Lv 40+\"]', '[]', '猎兵祭刀x 3', '督察长祭刀 = 特工祭刀x 3', '[]', '[\"暗铁剑\",\"黑岩长剑\",\"匣里龙吟\",\"斫峰之刃\",\"石英大剑\",\"千岩古剑\",\"白影剑\",\"白缨枪\",\"流月针\",\"和璞鸢\",\"弹弓\",\"黑岩战弓\",\"弓藏\",\"落霞\",\"陨龙之梦\",\"翡玉法球\",\"黑岩绯玉\",\"匣里日月\"]');
INSERT INTO `breakthrough` VALUES (218, 'Secondary', '督察长祭刀', '4', '/AscensionMaterial/Primary/督察长祭刀.png', '角色培养素材', '\"在故主手中，曾处理过许多「债务」的凶恶武具。世上无人背后长眼，而这种兵器与相关的武艺则是针对这种弱点设计的。\"', '/img/upgrade/material/i_103', '[\"合成获得\"]', '[\"愚人众·火之债务处理人Lv 60+\"]', '[]', '特工祭刀x 3', NULL, '[]', '[\"暗铁剑\",\"黑岩长剑\",\"匣里龙吟\",\"斫峰之刃\",\"石英大剑\",\"千岩古剑\",\"白影剑\",\"白缨枪\",\"流月针\",\"和璞鸢\",\"弹弓\",\"黑岩战弓\",\"弓藏\",\"落霞\",\"陨龙之梦\",\"翡玉法球\",\"黑岩绯玉\",\"匣里日月\"]');
INSERT INTO `breakthrough` VALUES (219, 'Secondary', '脆弱的骨片', '2', '/AscensionMaterial/Primary/脆弱的骨片.png', '角色培养素材', '\"从龙蜥身上找到的某种骨片。<br>尽管十分脆弱，其中似乎蕴含着某种难以言喻的力量。\"', '/img/upgrade/material/i_141', '[\"龙蜥掉落\",\"岩龙蜥掉落\"]', '[\"幼岩龙蜥Lv 0+\",\"岩龙蜥Lv 0+\",\"深海龙蜥·原种Lv 0+\",\"深海龙蜥·啮冰Lv 0+\",\"深海龙蜥·吞雷Lv 0+\"]', '[]', '结实的骨片 = 脆弱的骨片x 3', NULL, '[]', '[\"飞天御剑\",\"铁蜂刺\",\"飞天大御剑\",\"试作古华\",\"螭骨剑\",\"衔珠海皇\",\"黑缨枪\",\"「旗杆」\",\"千岩长枪\",\"试作星镰\",\"贯虹之槊\",\"黑檀弓\",\"钢轮弓\",\"琥珀玥\",\"万国诸海图谱\",\"尘世之锁\"]');
INSERT INTO `breakthrough` VALUES (220, 'Secondary', '结实的骨片', '3', '/AscensionMaterial/Primary/结实的骨片.png', '角色培养素材', '\"出于某种理由，龙蜥十分珍惜的某种生物骨片。<br>看上去有一定的年头。即使是这种不通人言的龙兽，似乎对这种遗骸也拥有某种特殊的憧憬。\"', '/img/upgrade/material/i_142', '[\"合成获得\",\"40级以上岩龙蜥掉落\",\"合成获得\"]', '[\"幼岩龙蜥Lv 40+\",\"岩龙蜥Lv 40+\",\"深海龙蜥·原种Lv 40+\",\"深海龙蜥·啮冰Lv 40+\",\"深海龙蜥·吞雷Lv 40+\"]', '[]', '脆弱的骨片x 3', '石化的骨片 = 结实的骨片x 3', '[]', '[\"飞天御剑\",\"铁蜂刺\",\"飞天大御剑\",\"试作古华\",\"螭骨剑\",\"衔珠海皇\",\"黑缨枪\",\"「旗杆」\",\"千岩长枪\",\"试作星镰\",\"贯虹之槊\",\"黑檀弓\",\"钢轮弓\",\"琥珀玥\",\"万国诸海图谱\",\"尘世之锁\"]');
INSERT INTO `breakthrough` VALUES (221, 'Secondary', '石化的骨片', '4', '/AscensionMaterial/Primary/石化的骨片.png', '角色培养素材', '\"击败龙蜥后有时能找到的石化骨片。<br>龙蜥都梦想着成长为真正的龙。会把古老的化石认作是龙之骨而加以珍惜，或许是因为希冀着龙的永恒与强大吧。\"', '/img/upgrade/material/i_143', '[\"合成获得\",\"60级以上岩龙蜥掉落\",\"合成获得\"]', '[\"幼岩龙蜥Lv 60+\",\"岩龙蜥Lv 60+\",\"深海龙蜥·原种Lv 60+\",\"深海龙蜥·啮冰Lv 60+\",\"深海龙蜥·吞雷Lv 60+\"]', '[]', '结实的骨片x 3', NULL, '[]', '[\"飞天御剑\",\"铁蜂刺\",\"飞天大御剑\",\"试作古华\",\"螭骨剑\",\"衔珠海皇\",\"黑缨枪\",\"「旗杆」\",\"千岩长枪\",\"试作星镰\",\"贯虹之槊\",\"黑檀弓\",\"钢轮弓\",\"琥珀玥\",\"万国诸海图谱\",\"尘世之锁\"]');
INSERT INTO `breakthrough` VALUES (222, 'Secondary', '隐兽指爪', '2', '/AscensionMaterial/Primary/隐兽指爪.png', '角色培养素材', '\"兽境群狼的残留身体组织。<br>按照常理来说，这种与此世不容的事物不应在这种异常造物被驱逐后，残留于世才对。\"', '/img/upgrade/material/i_174', '[\"兽境之狼掉落\",\"兽境猎犬掉落\"]', '[\"嗜岩·兽境幼兽Lv 0+\",\"嗜雷·兽境幼兽Lv 0+\",\"嗜岩·兽境猎犬Lv 0+\",\"嗜雷·兽境猎犬Lv 0+\"]', '[]', '隐兽利爪 = 隐兽指爪x 3', NULL, '[]', '[\"天目影打刀\",\"雾切之回光\",\"桂木斩长正\",\"恶王丸\",\"赤角石溃杵\",\"喜多院十文字\",\"「渔获」\",\"断浪长鳍\",\"薙草之稻光\",\"冬极白星\",\"证誓之明瞳\",\"神乐之真意\"]');
INSERT INTO `breakthrough` VALUES (223, 'Secondary', '隐兽利爪', '3', '/AscensionMaterial/Primary/隐兽利爪.png', '角色培养素材', '\"兽境群狼的身体组织。<br>虽然奇妙，但是和大杰作「腐殖层」杜林相比，「淋溶层」猎犬不值一提。\"', '/img/upgrade/material/i_175', '[\"合成获得\",\"40级以上兽境猎犬掉落\",\"合成获得\"]', '[\"嗜岩·兽境幼兽Lv 40+\",\"嗜雷·兽境幼兽Lv 40+\",\"嗜岩·兽境猎犬Lv 40+\",\"嗜雷·兽境猎犬Lv 40+\"]', '[]', '隐兽指爪x 3', '隐兽鬼爪 = 隐兽利爪x 3', '[]', '[\"天目影打刀\",\"雾切之回光\",\"桂木斩长正\",\"恶王丸\",\"赤角石溃杵\",\"喜多院十文字\",\"「渔获」\",\"断浪长鳍\",\"薙草之稻光\",\"冬极白星\",\"证誓之明瞳\",\"神乐之真意\"]');
INSERT INTO `breakthrough` VALUES (224, 'Secondary', '隐兽鬼爪', '4', '/AscensionMaterial/Primary/隐兽鬼爪.png', '角色培养素材', '\"兽境群狼的利爪。<br>世之奇珍，但是和最高杰作「白垩层」相比，只是寻常之物罢了。\"', '/img/upgrade/material/i_176', '[\"合成获得\",\"60级以上兽境猎犬掉落\",\"合成获得\"]', '[\"嗜岩·兽境幼兽Lv 60+\",\"嗜雷·兽境幼兽Lv 60+\",\"嗜岩·兽境猎犬Lv 60+\",\"嗜雷·兽境猎犬Lv 60+\"]', '[]', '隐兽利爪x 3', NULL, '[]', '[\"恶王丸\",\"赤角石溃杵\",\"断浪长鳍\",\"冬极白星\",\"证誓之明瞳\",\"神乐之真意\"]');
INSERT INTO `breakthrough` VALUES (225, 'Secondary', '黯淡棱镜', '2', '/AscensionMaterial/Primary/黯淡棱镜.png', '角色培养素材', '\"原本是能偏转光线的棱镜，在流水般的年岁中渐渐变得黯淡。\"', '/img/upgrade/material/i_181', '[\"愚人众·冬国仕女掉落\"]', '[\"愚人众·藏镜仕女Lv 0+\"]', '[]', '水晶棱镜 = 黯淡棱镜x 3', NULL, '[]', '[\"破魔之弓\",\"掠食者\",\"曚云之月\",\"飞雷之弦振\",\"白辰之环\",\"不灭月华\"]');
INSERT INTO `breakthrough` VALUES (226, 'Secondary', '水晶棱镜', '3', '/AscensionMaterial/Primary/水晶棱镜.png', '角色培养素材', '\"能够偏转光线的棱镜。<br>利用这种棱镜，可以制造虚幻的影像。从某种意义而言，也因此有着操纵人心的力量。\"', '/img/upgrade/material/i_182', '[\"合成获得\"]', '[\"愚人众·藏镜仕女Lv 40+\"]', '[]', '黯淡棱镜x 3', '偏光棱镜 = 水晶棱镜x 3', '[]', '[\"破魔之弓\",\"掠食者\",\"曚云之月\",\"飞雷之弦振\",\"白辰之环\",\"不灭月华\"]');
INSERT INTO `breakthrough` VALUES (227, 'Secondary', '偏光棱镜', '4', '/AscensionMaterial/Primary/偏光棱镜.png', '角色培养素材', '\"能够偏转光线的棱镜。<br>在黑暗中，棱镜可以将细微的光明散布到更大的范围里。或许也是因为这样的特性，这种棱镜对于内心常常被黑暗笼罩的人，有着令他们稍稍安心的力量。\"', '/img/upgrade/material/i_183', '[\"合成获得\"]', '[\"愚人众·藏镜仕女Lv 60+\"]', '[]', '水晶棱镜x 3', NULL, '[]', '[\"破魔之弓\",\"掠食者\",\"曚云之月\",\"飞雷之弦振\",\"白辰之环\",\"不灭月华\"]');
