INSERT INTO `identityuser` VALUES (5, '甘雨', 'ganyu', 'Ganyu', '循循守月', '月海亭', '甘雨', '璃月', '女', '5', '限定UP', '冰', '弓', '仙麟座', '盛世太平', '[[\"哀叙冰玉碎屑\",\"极寒之核\",\"清心\",\"骗骗花蜜\"],[\"骗骗花蜜\",\"「勤劳」的教导\",\"武炼之魂·孤影\"]]', '2021-01-12', '嘲讽、减抗、伤害提升、锻造返还', '月海亭的秘书，体内流淌着仙兽「麒麟」的血脉。', '林簌', '上田丽奈', '<p>璃月七星的秘书，体内流淌着人类与仙兽的血脉。<br />\n天性优雅娴静，但仙兽「麒麟」温柔的性情与坚定毅重的工作态度毫无冲突。<br />\n毕竟，甘雨坚信自己所做的一切工作都是为了践行与帝君的契约，谋求璃月众生的最大福祉。</p>\n', '甘雨·麟迹', '/人物/InGame/5.png', '/人物/LOGO/05-5-甘雨.png', '/人物/uList/05.png', '/人物/painting/5.png', '/人物/Boutique/5.png', '/人物/head/5.png', '/人物/picturePNG/5.png', '/人物/MpicturePNG/甘雨.png', '/人物/UserAssetsNum/12/台词-CHS.png', '[\"/人物/UserAssetsNum/12/音频1-中1.mp3\",\"/人物/UserAssetsNum/12/音频1-中2.mp3\",\"/人物/UserAssetsNum/12/音频1-中3.mp3\",\"/人物/UserAssetsNum/12/音频2-日1.mp3\",\"/人物/UserAssetsNum/12/音频2-日2.mp3\",\"/人物/UserAssetsNum/12/音频2-日3.mp3\"]', '{\"例会\":\"/人物/Dynamicfigure/例会.png\",\"角色待机\":\"/人物/Dynamicfigure/角色待机.gif\",\"角色待机1\":\"/人物/Dynamicfigure/角色待机1.gif\",\"普通攻击\":\"/人物/Dynamicfigure/普通攻击.gif\",\"重击\":\"/人物/Dynamicfigure/重击.gif\",\"元素战技\":\"/人物/Dynamicfigure/元素战技.gif\",\"元素爆发\":\"/人物/Dynamicfigure/元素爆发.gif\"}');
