INSERT INTO `identityuser` VALUES (22, '优菈', 'eula', 'Eula', '浪沫的旋舞', '西风骑士团', '优菈·劳伦斯', '蒙德', '女', '5', '限定UP', '冰', '双手剑', '浪沫座', '骇浪派', '[[\"哀叙冰玉碎屑\",\"晶凝之华\",\"蒲公英籽\",\"破损的面具\"],[\"破损的面具\",\"「抗争」的教导\",\"龙王之冕\"]]', '2021-05-18', '减抗、炼金返还、自身伤害提升、自身抗打断、物理输出', '古老家族出身的「浪花骑士」，西风骑士团游击小队队长。身为旧贵族后裔却加入了堪称死对头的西风骑士团，该事件至今仍是蒙德一大谜团。', '子音', '佐藤利奈', '<p>优菈&mdash;&mdash;常年在外作战的「浪花骑士」，反叛的旧贵族末裔。<br />\n生于旧日宗室，身负罪恶血脉之人，的确需要独特的处世技巧，才能在偏见的高墙下安然行走。当然，这并不妨碍她与家族决裂，作为卓越的「浪花骑士」，在外游猎蒙德的敌人，完成她那意义独特的「复仇」。</p>\n', '优菈·冰印', '/人物/InGame/22.png', '/人物/LOGO/22-5-优菈.png', '/人物/uList/22.png', '/人物/painting/22.png', '/人物/Boutique/22.png', '/人物/head/22.png', '/人物/picturePNG/22.png', '/人物/MpicturePNG/优菈.png', '/人物/UserAssetsNum/16/台词-简体中文(1).png', '[\"/人物/UserAssetsNum/16/音频1-优菈01.mp3\",\"/人物/UserAssetsNum/16/音频1-优菈02.mp3\",\"/人物/UserAssetsNum/16/音频1-优菈03.mp3\",\"/人物/UserAssetsNum/16/音频2-日-优菈01.mp3\",\"/人物/UserAssetsNum/16/音频2-日-优菈02.mp3\",\"/人物/UserAssetsNum/16/音频2-日-优菈03.mp3\"]', '{\"例会\":\"/人物/Dynamicfigure/例会.png\",\"角色待机1\":\"/人物/Dynamicfigure/角色待机1.gif\",\"角色待机2\":\"/人物/Dynamicfigure/角色待机2.gif\",\"普通攻击\":\"/人物/Dynamicfigure/普通攻击.gif\",\"重击\":\"/人物/Dynamicfigure/重击.gif\",\"元素战技（点按）\":\"/人物/Dynamicfigure/元素战技（点按）.gif\",\"元素战技（长按）\":\"/人物/Dynamicfigure/元素战技（长按）.gif\",\"元素爆发\":\"/人物/Dynamicfigure/元素爆发.gif\"}');
