INSERT INTO `weapons` VALUES (6, '暗铁剑', '单手剑', '3', '39', '元素精通', '31', '过载', '触发超载、超导、感电或雷元素扩散反应后的12秒内，攻击力提高20%。', '其实就是稍微有些黑的铁剑。', '[\"触发超载、超导、感电或雷元素扩散反应后的12秒内，攻击力提高20%。\",\"触发超载、超导、感电或雷元素扩散反应后的12秒内，攻击力提高25%。\",\"触发超载、超导、感电或雷元素扩散反应后的12秒内，攻击力提高30%。\",\"触发超载、超导、感电或雷元素扩散反应后的12秒内，攻击力提高35%。\",\"触发超载、超导、感电或雷元素扩散反应后的12秒内，攻击力提高40%。\"]', '「为什么叔叔要把剑，放在地上呢？」<br>「为了把剑晒黑啊。」<br>「为什么要把剑晒黑呀？」<br>「很好的问题。小朋友，什么是黑的呢？」<br>「乌鸦？」<br>「没错！还有呢？」<br>「嗯……铁锅？」<br>「很好的思路！那铁锅下面是什么呢？」<br>「火！」<br>「火是哪来的呢？」<br>小姑娘想了想，恍然大悟。<br>「木炭！是木炭！木炭是黑的！」<br>「没错，而碳正是让钢铁变得更硬更强的物质。这就是为什么叔叔要晒剑。」<br>说完，叔叔把剑翻了一面。<br>', '{\"Level\":[\"1\",\"5\",\"10\",\"15\",\"20\",\"20+\",\"25\",\"30\",\"35\",\"40\",\"40+\",\"45\",\"50\",\"50+\",\"55\",\"60\",\"60+\",\"65\",\"70\",\"70+\",\"75\",\"80\",\"80+\",\"85\",\"90\"],\"BaseAtk\":[\"39\",\"50\",\"65\",\"79\",\"94\",\"113\",\"127\",\"141\",\"155\",\"169\",\"189\",\"202\",\"216\",\"236\",\"249\",\"263\",\"282\",\"296\",\"309\",\"329\",\"342\",\"355\",\"375\",\"388\",\"401\"],\"none\":[\"31\",\"36\",\"42\",\"48\",\"54\",\"54\",\"60\",\"66\",\"73\",\"79\",\"79\",\"85\",\"91\",\"91\",\"97\",\"104\",\"104\",\"110\",\"116\",\"116\",\"122\",\"128\",\"128\",\"134\",\"141\"],\"AscensionMaterials\":[[],[],[],[],[],[{\"name\":\"2 \",\"urlid\":\"/img/upgrade/weapon/i_511\"},{\"name\":\"2 \",\"urlid\":\"/img/upgrade/material/i_101\"},{\"name\":\"1 \",\"urlid\":\"/img/upgrade/material/i_31\"},{\"name\":\"5000\",\"urlid\":\"/img/currency/i_2001\"}],[],[],[],[],[{\"name\":\"2 \",\"urlid\":\"/img/upgrade/weapon/i_512\"},{\"name\":\"8 \",\"urlid\":\"/img/upgrade/material/i_101\"},{\"name\":\"5 \",\"urlid\":\"/img/upgrade/material/i_31\"},{\"name\":\"10000\",\"urlid\":\"/img/currency/i_2001\"}],[],[],[{\"name\":\"4 \",\"urlid\":\"/img/upgrade/weapon/i_512\"},{\"name\":\"4 \",\"urlid\":\"/img/upgrade/material/i_102\"},{\"name\":\"4 \",\"urlid\":\"/img/upgrade/material/i_32\"},{\"name\":\"15000\",\"urlid\":\"/img/currency/i_2001\"}],[],[],[{\"name\":\"2 \",\"urlid\":\"/img/upgrade/weapon/i_513\"}