INSERT INTO `weapons` VALUES (9, '飞天御剑', '单手剑', '3', '38', '元素充能效率', '11.3', '决心', '施放元素爆发后，提高12%攻击力和移动速度，持续15秒。', '可靠的钢剑，传说著名的「御剑公子」曾试图借助它飞天。', '[\"施放元素爆发后，提高12%攻击力和移动速度，持续15秒。\",\"施放元素爆发后，提高15%攻击力和移动速度，持续15秒。\",\"施放元素爆发后，提高18%攻击力和移动速度，持续15秒。\",\"施放元素爆发后，提高21%攻击力和移动速度，持续15秒。\",\"施放元素爆发后，提高24%攻击力和移动速度，持续15秒。\"]', '剑术高超的御剑公子从绝云间顶一跃而下，<br>不顾耳边狂啸的烈风，公子御剑穿透云层。<br>直到断裂的声音顺着筋骨传入脑中——<br>他才知道，有的事情剑术也无法实现。<br>虽说当掉锃亮的钢剑，换了金创药丸。<br>可御剑公子明白，飞天的伟大征程还未结束！<br>', '{\"Level\":[\"1\",\"5\",\"10\",\"15\",\"20\",\"20+\",\"25\",\"30\",\"35\",\"40\",\"40+\",\"45\",\"50\",\"50+\",\"55\",\"60\",\"60+\",\"65\",\"70\",\"70+\",\"75\",\"80\",\"80+\",\"85\",\"90\"],\"BaseAtk\":[\"38\",\"48\",\"61\",\"73\",\"86\",\"105\",\"117\",\"129\",\"140\",\"151\",\"171\",\"182\",\"193\",\"212\",\"223\",\"234\",\"253\",\"264\",\"274\",\"294\",\"304\",\"314\",\"334\",\"344\",\"354\"],\"none\":[\"11.3\",\"13.2\",\"15.4\",\"17.7\",\"20\",\"20\",\"22.3\",\"24.6\",\"26.9\",\"29.2\",\"29.2\",\"31.5\",\"33.8\",\"33.8\",\"36.1\",\"38.3\",\"38.3\",\"40.6\",\"42.9\",\"42.9\",\"45.2\",\"47.5\",\"47.5\",\"49.8\",\"52.1\"],\"AscensionMaterials\":[[],[],[],[],[],[{\"name\":\"2 \",\"urlid\":\"/img/upgrade/weapon/i_551\"},{\"name\":\"2 \",\"urlid\":\"/img/upgrade/material/i_141\"},{\"name\":\"1 \",\"urlid\":\"/img/upgrade/material/i_111\"},{\"name\":\"5000\",\"urlid\":\"/img/currency/i_2001\"}],[],[],[],[],[{\"name\":\"2 \",\"urlid\":\"/img/upgrade/weapon/i_552\"},{\"name\":\"8 \",\"urlid\":\"/img/upgrade/material/i_141\"},{\"name\":\"5 \",\"urlid\":\"/img/upgrade/material/i_111\"},{\"name\":\"10000\",\"urlid\":\"/img/currency/i_2001\"}],[],[],[{\"name\":\"4 \",\"urlid\":\"/img/upgrade/weapon/i_552\"},{\"name\":\"4 \",\"urlid\":\"/img/upgrade/material/i_142\"},{\"name\":\"4 \",\"urlid\":\"/img/upgrade/material/i_112\"},{\"name\":\"15000\",\"urlid\":\"/img/currency/i_2001\"}],[],[],[{\"name\":\"2 \",\"urlid\":\"/img/upgrade/weapon/i_553\"},{\"name\":\"8 \",\"urlid\":\"/img/upgrade/material/i_142\"},{\"name\":\"6 \",\"urlid\