INSERT INTO `weapons` VALUES (10, '旅行剑', '单手剑', '3', '40', '防御力', '6.4', '旅程', '获得元素晶球或元素微粒时，恢复1%生命值。', '趁手的钢剑，剑柄里藏着剪子、放大镜、火绒等方便旅行的小物件。', '[\"获得元素晶球或元素微粒时，恢复1%生命值。\",\"获得元素晶球或元素微粒时，恢复1.25%生命值。\",\"获得元素晶球或元素微粒时，恢复1.5%生命值。\",\"获得元素晶球或元素微粒时，恢复1.75%生命值。\",\"获得元素晶球或元素微粒时，恢复2%生命值。\"]', '本身是可靠的钢剑，平衡趁手，钢也是坚韧的好钢。<br>最大缺点在于不是全龙骨结构，大大降低了耐用性。<br>取而代之的是在掏空的剑柄里塞了微型水果刀、剪子、火绒等小件。<br>之所以叫旅行剑，就是因为里面有各种旅行道具……？<br>', '{\"Level\":[\"1\",\"5\",\"10\",\"15\",\"20\",\"20+\",\"25\",\"30\",\"35\",\"40\",\"40+\",\"45\",\"50\",\"50+\",\"55\",\"60\",\"60+\",\"65\",\"70\",\"70+\",\"75\",\"80\",\"80+\",\"85\",\"90\"],\"BaseAtk\":[\"40\",\"53\",\"69\",\"86\",\"102\",\"121\",\"138\",\"154\",\"171\",\"187\",\"207\",\"223\",\"239\",\"259\",\"275\",\"292\",\"311\",\"327\",\"344\",\"363\",\"380\",\"396\",\"415\",\"432\",\"448\"],\"none\":[\"6.4\",\"7.4\",\"8.7\",\"10\",\"11.3\",\"11.3\",\"12.5\",\"13.8\",\"15.1\",\"16.4\",\"16.4\",\"17.7\",\"19\",\"19\",\"20.3\",\"21.6\",\"21.6\",\"22.8\",\"24.1\",\"24.1\",\"25.4\",\"26.7\",\"26.7\",\"28\",\"29.3\"],\"AscensionMaterials\":[[],[],[],[],[],[{\"name\":\"2 \",\"urlid\":\"/img/upgrade/weapon/i_541\"},{\"name\":\"2 \",\"urlid\":\"/img/upgrade/material/i_81\"},{\"name\":\"1 \",\"urlid\":\"/img/upgrade/material/i_41\"},{\"name\":\"5000\",\"urlid\":\"/img/currency/i_2001\"}],[],[],[],[],[{\"name\":\"2 \",\"urlid\":\"/img/upgrade/weapon/i_542\"},{\"name\":\"8 \",\"urlid\":\"/img/upgrade/material/i_81\"},{\"name\":\"5 \",\"urlid\":\"/img/upgrade/material/i_41\"},{\"name\":\"10000\",\"urlid\":\"/img/currency/i_2001\"}],[],[],[{\"name\":\"4 \",\"urlid\":\"/img/upgrade/weapon/i_542\"},{\"name\":\"4 \",\"urlid\":\"/img/upgrade/material/i_82\"},{\"name\":\"15000\",\"urlid\":\"/img/currency/i_2001\"},{\"name\":\"4 \",\"urlid\":\"/img/upgrade/material/i_42\"}],[],[],[{\"name\":\"2 \",\"urlid\":\"/img/upgrade/weapon/i_543\"},{\"name\":\"8 \",\"urlid\":\"/img/upgrade/material/i_82\"},{\"name\":\"20000\",\"urlid\":\"/img/currency/i_2001\"},{\"name\":\"6 \",\"urlid\":\"/img