INSERT INTO `weapons` VALUES (35, '以理服人', '双手剑', '3', '39', '攻击力', '7.7', '有话直说', '施放元素战技后，普通攻击和重击命中时会在小范围内额外造成60%攻击力的伤害。该效果持续15秒，伤害每3秒只能触发一次。', '精钢打造的趁手大铁棒子。最好的理论依据。', '[\"施放元素战技后，普通攻击和重击命中时会在小范围内额外造成60%攻击力的伤害。该效果持续15秒，伤害每3秒只能触发一次。\",\"施放元素战技后，普通攻击和重击命中时会在小范围内额外造成75%攻击力的伤害。该效果持续15秒，伤害每3秒只能触发一次。\",\"施放元素战技后，普通攻击和重击命中时会在小范围内额外造成90%攻击力的伤害。该效果持续15秒，伤害每3秒只能触发一次。\",\"施放元素战技后，普通攻击和重击命中时会在小范围内额外造成105%攻击力的伤害。该效果持续15秒，伤害每3秒只能触发一次。\",\"施放元素战技后，普通攻击和重击命中时会在小范围内额外造成120%攻击力的伤害。该效果持续15秒，伤害每3秒只能触发一次。\"]', '在任何争辩中都是最可靠的理论根基，说起话来掷地有声。<br>不论面对的是多么食古不化的对手，以理服人，金石为开。<br>是谓之金石良言。<br>最终在人们的抗议中，被勒令停止制造。<br>是谓之众口铄金。<br>', '{\"Level\":[\"1\",\"5\",\"10\",\"15\",\"20\",\"20+\",\"25\",\"30\",\"35\",\"40\",\"40+\",\"45\",\"50\",\"50+\",\"55\",\"60\",\"60+\",\"65\",\"70\",\"70+\",\"75\",\"80\",\"80+\",\"85\",\"90\"],\"BaseAtk\":[\"39\",\"50\",\"65\",\"79\",\"94\",\"113\",\"127\",\"141\",\"155\",\"169\",\"189\",\"202\",\"216\",\"236\",\"249\",\"263\",\"282\",\"296\",\"309\",\"329\",\"342\",\"355\",\"375\",\"388\",\"401\"],\"none\":[\"7.7\",\"8.9\",\"10.4\",\"12\",\"13.5\",\"13.5\",\"15.1\",\"16.6\",\"18.2\",\"19.7\",\"19.7\",\"21.3\",\"22.8\",\"22.8\",\"24.4\",\"25.9\",\"25.9\",\"27.5\",\"29\",\"29\",\"30.5\",\"32.1\",\"32.1\",\"33.6\",\"35.2\"],\"AscensionMaterials\":[[],[],[],[],[],[{\"name\":\"2 \",\"urlid\":\"/img/upgrade/weapon/i_531\"},{\"name\":\"2 \",\"urlid\":\"/img/upgrade/material/i_91\"},{\"name\":\"1 \",\"urlid\":\"/img/upgrade/material/i_31\"},{\"name\":\"5000\",\"urlid\":\"/img/currency/i_2001\"}],[],[],[],[],[{\"name\":\"2 \",\"urlid\":\"/img/upgrade/weapon/i_532\"},{\"name\":\"8 \",\"urlid\":\"/img/upgrade/material/i_91\"},{\"name\":\"5 \",\"urlid\":\"/img/upgrade/material/i_31\"},{\"name\":\"10000\",\"urlid\":\"/img/currency/i_2001\"}],[],[],[{\"name\":\"4 \",\"urlid\":\"/img/upgrade/weapon/i_532\"},{\"name\":\"4 \",\"urlid\":\"/img/upgrade/material/i_92\"},{\"name\":\"4 \",\"urlid\":\"/img/upgrade/material/i_32\"},{\"name\":\"15000\",\"urlid\":\"/img/currency/i_2001\"}],[],[],