INSERT INTO `weapons` VALUES (36, '铁影阔剑', '双手剑', '3', '39', '生命值', '7.7', '不屈', '生命值低于70%时，重击不会轻易被打断，并提高30%重击伤害。', '这把剑是著名的光之狮艾伦德林的佩剑的仿品。握紧它，感受传说中大英雄的力量吧！注意：幻想请适度。', '[\"生命值低于70%时，重击不会轻易被打断，并提高30%重击伤害。\",\"生命值低于75%时，重击不会轻易被打断，并提高35%重击伤害。\",\"生命值低于80%时，重击不会轻易被打断，并提高40%重击伤害。\",\"生命值低于85%时，重击不会轻易被打断，并提高45%重击伤害。\",\"生命值低于90%时，重击不会轻易被打断，并提高50%重击伤害。\"]', '当幼小的艾伦德林手持北风骑士瑞文伍德大剑的仿品，向着童年幻想中的蒙德旧贵族冲锋时，他未曾想到自己日后将成为威名远扬的「西风的光之狮」。更不知道幼时与他交战的那位鲁斯坦会成为他的得力副手，并在服务蒙德人民二十三年后因公殉职。<br>如今，「光之狮」所持大剑的仿品又落在了另一双年轻的手中。<br>这伟大神秘的宿命传承，又将以何种方式继续？<br>', '{\"Level\":[\"1\",\"5\",\"10\",\"15\",\"20\",\"20+\",\"25\",\"30\",\"35\",\"40\",\"40+\",\"45\",\"50\",\"50+\",\"55\",\"60\",\"60+\",\"65\",\"70\",\"70+\",\"75\",\"80\",\"80+\",\"85\",\"90\"],\"BaseAtk\":[\"39\",\"50\",\"65\",\"79\",\"94\",\"113\",\"127\",\"141\",\"155\",\"169\",\"189\",\"202\",\"216\",\"236\",\"249\",\"263\",\"282\",\"296\",\"309\",\"329\",\"342\",\"355\",\"375\",\"388\",\"401\"],\"none\":[\"7.7\",\"8.9\",\"10.4\",\"12\",\"13.5\",\"13.5\",\"15.1\",\"16.6\",\"18.2\",\"19.7\",\"19.7\",\"21.3\",\"22.8\",\"22.8\",\"24.4\",\"25.9\",\"25.9\",\"27.5\",\"29\",\"29\",\"30.5\",\"32.1\",\"32.1\",\"33.6\",\"35.2\"],\"AscensionMaterials\":[[],[],[],[],[],[{\"name\":\"2 \",\"urlid\":\"/img/upgrade/weapon/i_501\"},{\"name\":\"2 \",\"urlid\":\"/img/upgrade/material/i_61\"},{\"name\":\"1 \",\"urlid\":\"/img/upgrade/material/i_131\"},{\"name\":\"5000\",\"urlid\":\"/img/currency/i_2001\"}],[],[],[],[],[{\"name\":\"2 \",\"urlid\":\"/img/upgrade/weapon/i_502\"},{\"name\":\"8 \",\"urlid\":\"/img/upgrade/material/i_61\"},{\"name\":\"5 \",\"urlid\":\"/img/upgrade/material/i_131\"},{\"name\":\"10000\",\"urlid\":\"/img/currency/i_2001\"}],[],[],[{\"name\":\"4 \",\"urlid\":\"/img/upgrade/weapon/i_502\"},{\"name\":\"4 \",\"urlid\":\"/img/upgrade/material/i_62\"},{\"name\":\"4 \",\"urlid\":\"/img/upgrade/material/i_132\"},{\"name\":\"15000\",\"urlid\":\"/img/currency/i_2001\"}],[],[],[{\"name\":\"2 \",\"urlid\":\"/img/upgrade/weapon/i_503\"},{\"name\":\"8 \",