INSERT INTO `weapons` VALUES (66, '白缨枪', '长柄武器', '3', '39', '暴击率', '5.1', '锐利', '普通攻击造成的伤害提升24%。', '千岩军士兵的制式装备，枪身牢靠，枪尖锋锐，是无比可靠的武器。', '[\"普通攻击造成的伤害提升24%。\",\"普通攻击造成的伤害提升30%。\",\"普通攻击造成的伤害提升36%。\",\"普通攻击造成的伤害提升42%。\",\"普通攻击造成的伤害提升48%。\"]', '千岩军士兵素以擅使长兵闻名天下，<br>其所持白缨长枪由黑岩厂批量生产，<br>这武器历经了千百年来无数战火的考验，<br>白缨纵使在硝烟中污损，仍旧傲然飘扬。<br><br>传说军中曾经有枪法精绝的高手，<br>能在千军之中刺上将而不沾白缨。<br>「但那故事属于持枪的豪侠」<br>「他的兵器本身却并不出奇」<br>老铁匠在学徒脑袋上拍了一掌，<br>要他给新打造的长枪配上白缨。<br>', '{\"Level\":[\"1\",\"5\",\"10\",\"15\",\"20\",\"20+\",\"25\",\"30\",\"35\",\"40\",\"40+\",\"45\",\"50\",\"50+\",\"55\",\"60\",\"60+\",\"65\",\"70\",\"70+\",\"75\",\"80\",\"80+\",\"85\",\"90\"],\"BaseAtk\":[\"39\",\"50\",\"65\",\"79\",\"94\",\"113\",\"127\",\"141\",\"155\",\"169\",\"189\",\"202\",\"216\",\"236\",\"249\",\"263\",\"282\",\"296\",\"309\",\"329\",\"342\",\"355\",\"375\",\"388\",\"401\"],\"none\":[\"5.1\",\"5.9\",\"7\",\"8\",\"9\",\"9\",\"10\",\"11.1\",\"12.1\",\"13.1\",\"13.1\",\"14.2\",\"15.2\",\"15.2\",\"16.2\",\"17.3\",\"17.3\",\"18.3\",\"19.3\",\"19.3\",\"20.3\",\"21.4\",\"21.4\",\"22.4\",\"23.4\"],\"AscensionMaterials\":[[],[],[],[],[],[{\"name\":\"2 \",\"urlid\":\"/img/upgrade/weapon/i_511\"},{\"name\":\"2 \",\"urlid\":\"/img/upgrade/material/i_101\"},{\"name\":\"1 \",\"urlid\":\"/img/upgrade/material/i_111\"},{\"name\":\"5000\",\"urlid\":\"/img/currency/i_2001\"}],[],[],[],[],[{\"name\":\"2 \",\"urlid\":\"/img/upgrade/weapon/i_512\"},{\"name\":\"8 \",\"urlid\":\"/img/upgrade/material/i_101\"},{\"name\":\"5 \",\"urlid\":\"/img/upgrade/material/i_111\"},{\"name\":\"10000\",\"urlid\":\"/img/currency/i_2001\"}],[],[],[{\"name\":\"4 \",\"urlid\":\"/img/upgrade/weapon/i_512\"},{\"name\":\"4 \",\"urlid\":\"/img/upgrade/material/i_102\"},{\"name\":\"4 \",\"urlid\":\"/img/upgrade/material/i_112\"},{\"name\":\"15000\",\"urlid\":\"/img/currency/i_2001\"}],[],[],[{\"name\":\"2 \",\"urlid\":\"/img/upgrade/weapon/i_513\"},{\"name\":\"8 \",\"urlid\":\"/img/upgrade/material/i_102\"},{\"name\":\"6 \",\"urlid\":\"/img/upgrade/material/