INSERT INTO `weapons` VALUES (88, '鸦羽弓', '弓', '3', '40', '元素精通', '20', '踏火止水', '对处于水元素或火元素影响下的敌人，造成的伤害提高12%。', '相传渡鸦是死的使者。将鸦羽装饰在弓臂上，射出的每一箭都宣告着猎物的终末。', '[\"对处于水元素或火元素影响下的敌人，造成的伤害提高12%。\",\"对处于水元素或火元素影响下的敌人，造成的伤害提高15%。\",\"对处于水元素或火元素影响下的敌人，造成的伤害提高18%。\",\"对处于水元素或火元素影响下的敌人，造成的伤害提高21%。\",\"对处于水元素或火元素影响下的敌人，造成的伤害提高24%。\"]', '相传渡鸦是报死的使者。<br>若将鸦羽装饰在弓臂上，<br>振弦之声想必会为猎物宣告死期。<br>至少，那些武器商人是这么说的。<br>', '{\"Level\":[\"1\",\"5\",\"10\",\"15\",\"20\",\"20+\",\"25\",\"30\",\"35\",\"40\",\"40+\",\"45\",\"50\",\"50+\",\"55\",\"60\",\"60+\",\"65\",\"70\",\"70+\",\"75\",\"80\",\"80+\",\"85\",\"90\"],\"BaseAtk\":[\"40\",\"53\",\"69\",\"86\",\"102\",\"121\",\"138\",\"154\",\"171\",\"187\",\"207\",\"223\",\"239\",\"259\",\"275\",\"292\",\"311\",\"327\",\"344\",\"363\",\"380\",\"396\",\"415\",\"432\",\"448\"],\"none\":[\"20\",\"24\",\"28\",\"32\",\"36\",\"36\",\"40\",\"44\",\"48\",\"53\",\"53\",\"57\",\"61\",\"61\",\"65\",\"69\",\"69\",\"73\",\"77\",\"77\",\"81\",\"85\",\"85\",\"90\",\"94\"],\"AscensionMaterials\":[[],[],[],[],[],[{\"name\":\"2 \",\"urlid\":\"/img/upgrade/weapon/i_501\"},{\"name\":\"2 \",\"urlid\":\"/img/upgrade/material/i_61\"},{\"name\":\"1 \",\"urlid\":\"/img/upgrade/material/i_51\"},{\"name\":\"5000\",\"urlid\":\"/img/currency/i_2001\"}],[],[],[],[],[{\"name\":\"2 \",\"urlid\":\"/img/upgrade/weapon/i_502\"},{\"name\":\"8 \",\"urlid\":\"/img/upgrade/material/i_61\"},{\"name\":\"5 \",\"urlid\":\"/img/upgrade/material/i_51\"},{\"name\":\"10000\",\"urlid\":\"/img/currency/i_2001\"}],[],[],[{\"name\":\"4 \",\"urlid\":\"/img/upgrade/weapon/i_502\"},{\"name\":\"4 \",\"urlid\":\"/img/upgrade/material/i_62\"},{\"name\":\"4 \",\"urlid\":\"/img/upgrade/material/i_52\"},{\"name\":\"15000\",\"urlid\":\"/img/currency/i_2001\"}],[],[],[{\"name\":\"2 \",\"urlid\":\"/img/upgrade/weapon/i_503\"},{\"name\":\"8 \",\"urlid\":\"/img/upgrade/material/i_62\"},{\"name\":\"6 \",\"urlid\":\"/img/upgrade/material/i_52\"},{\"name\":\"20000\",\"urlid\":\"/img/currency/i_2001\"}],[],[],[{\"name\":\"4