INSERT INTO `weapons` VALUES (89, '反曲弓', '弓', '3', '38', '生命值', '10.2', '收割', '击败敌人时，恢复8%生命值。', '据说能射下苍鹰的猎弓，但能不能做到主要还得看弓手的技术。', '[\"击败敌人时，恢复8%生命值。\",\"击败敌人时，恢复10%生命值。\",\"击败敌人时，恢复12%生命值。\",\"击败敌人时，恢复14%生命值。\",\"击败敌人时，恢复16%生命值。\"]', '由木材、骨头与动物筋腱组合，精心打造出的反曲弓。<br>在射术高超的猎人手中，能够射下高天上飞翔的鸢鹰。<br>这是猎人的骄傲，也是精湛弓术的证明。<br>只是可怜了无端受害的苍鹰。', '{\"Level\":[\"1\",\"5\",\"10\",\"15\",\"20\",\"20+\",\"25\",\"30\",\"35\",\"40\",\"40+\",\"45\",\"50\",\"50+\",\"55\",\"60\",\"60+\",\"65\",\"70\",\"70+\",\"75\",\"80\",\"80+\",\"85\",\"90\"],\"BaseAtk\":[\"38\",\"48\",\"61\",\"73\",\"86\",\"105\",\"117\",\"129\",\"140\",\"151\",\"171\",\"182\",\"193\",\"212\",\"223\",\"234\",\"253\",\"264\",\"274\",\"294\",\"304\",\"314\",\"334\",\"344\",\"354\"],\"none\":[\"10.2\",\"11.9\",\"13.9\",\"16\",\"18\",\"18\",\"20.1\",\"22.2\",\"24.2\",\"26.3\",\"26.3\",\"28.4\",\"30.4\",\"30.4\",\"32.5\",\"34.6\",\"34.6\",\"36.6\",\"38.7\",\"38.7\",\"40.7\",\"42.8\",\"42.8\",\"44.9\",\"46.9\"],\"AscensionMaterials\":[[],[],[],[],[],[{\"name\":\"2 \",\"urlid\":\"/img/upgrade/weapon/i_541\"},{\"name\":\"2 \",\"urlid\":\"/img/upgrade/material/i_81\"},{\"name\":\"1 \",\"urlid\":\"/img/upgrade/material/i_41\"},{\"name\":\"5000\",\"urlid\":\"/img/currency/i_2001\"}],[],[],[],[],[{\"name\":\"2 \",\"urlid\":\"/img/upgrade/weapon/i_542\"},{\"name\":\"8 \",\"urlid\":\"/img/upgrade/material/i_81\"},{\"name\":\"5 \",\"urlid\":\"/img/upgrade/material/i_41\"},{\"name\":\"10000\",\"urlid\":\"/img/currency/i_2001\"}],[],[],[{\"name\":\"4 \",\"urlid\":\"/img/upgrade/weapon/i_542\"},{\"name\":\"4 \",\"urlid\":\"/img/upgrade/material/i_82\"},{\"name\":\"4 \",\"urlid\":\"/img/upgrade/material/i_42\"},{\"name\":\"15000\",\"urlid\":\"/img/currency/i_2001\"}],[],[],[{\"name\":\"2 \",\"urlid\":\"/img/upgrade/weapon/i_543\"},{\"name\":\"8 \",\"urlid\":\"/img/upgrade/material/i_82\"},{\"name\":\"6 \",\"urlid\":\"/img/upgrade/material/i_42\"},{\"name\":\"20000\",\"urlid\":\"/img/currency/i_2001\"}],[],[],[{\"name\":\"4 \",\"urlid\":\"/img/up