INSERT INTO `weapons` VALUES (114, '翡玉法球', '法器', '3', '40', '元素精通', '20', '激流', '触发蒸发、感电、冰冻或水元素扩散反应后的12秒内，攻击力提高20%。', '由璃月北郊绝云间出产的硬玉雕琢而成的法器。小巧轻盈，经久耐用，江湖人称「翡玉丸」。', '[\"触发蒸发、感电、冰冻或水元素扩散反应后的12秒内，攻击力提高20%。\",\"触发蒸发、感电、冰冻或水元素扩散反应后的12秒内，攻击力提高25%。\",\"触发蒸发、感电、冰冻或水元素扩散反应后的12秒内，攻击力提高30%。\",\"触发蒸发、感电、冰冻或水元素扩散反应后的12秒内，攻击力提高35%。\",\"触发蒸发、感电、冰冻或水元素扩散反应后的12秒内，攻击力提高40%。\"]', '翡玉雕琢而成的轻盈法器，经久耐用，价格低廉，颇受江湖人士欢迎。<br>由于外形小巧精致，雕工精细，通常被璃月人称为「翡玉丸」。<br>不仅可以作为高效的防身法器，佩戴在身上也是不错的首饰。<br>', '{\"Level\":[\"1\",\"5\",\"10\",\"15\",\"20\",\"20+\",\"25\",\"30\",\"35\",\"40\",\"40+\",\"45\",\"50\",\"50+\",\"55\",\"60\",\"60+\",\"65\",\"70\",\"70+\",\"75\",\"80\",\"80+\",\"85\",\"90\"],\"BaseAtk\":[\"40\",\"53\",\"69\",\"86\",\"102\",\"121\",\"138\",\"154\",\"171\",\"187\",\"207\",\"223\",\"239\",\"259\",\"275\",\"292\",\"311\",\"327\",\"344\",\"363\",\"380\",\"396\",\"415\",\"432\",\"448\"],\"none\":[\"20\",\"24\",\"28\",\"32\",\"36\",\"36\",\"40\",\"44\",\"48\",\"53\",\"53\",\"57\",\"61\",\"61\",\"65\",\"69\",\"69\",\"73\",\"77\",\"77\",\"81\",\"85\",\"85\",\"90\",\"94\"],\"AscensionMaterials\":[[],[],[],[],[],[{\"name\":\"2 \",\"urlid\":\"/img/upgrade/weapon/i_511\"},{\"name\":\"2 \",\"urlid\":\"/img/upgrade/material/i_101\"},{\"name\":\"1 \",\"urlid\":\"/img/upgrade/material/i_121\"},{\"name\":\"5000\",\"urlid\":\"/img/currency/i_2001\"}],[],[],[],[],[{\"name\":\"2 \",\"urlid\":\"/img/upgrade/weapon/i_512\"},{\"name\":\"8 \",\"urlid\":\"/img/upgrade/material/i_101\"},{\"name\":\"5 \",\"urlid\":\"/img/upgrade/material/i_121\"},{\"name\":\"10000\",\"urlid\":\"/img/currency/i_2001\"}],[],[],[{\"name\":\"4 \",\"urlid\":\"/img/upgrade/weapon/i_512\"},{\"name\":\"4 \",\"urlid\":\"/img/upgrade/material/i_102\"},{\"name\":\"4 \",\"urlid\":\"/img/upgrade/material/i_122\"},{\"name\":\"15000\",\"urlid\":\"/img/currency/i_2001\"}],[],[],[{\"name\":\"2 \",\"urlid\":\"/img/upgrade/weapon/i_513\"},{\"name\":\"8 \",\"urlid\":\"/img/upgrade/material/i_102\"},{\"name\":\"6 \",\"urlid\":\"/img/upgrade/material/i_122\