INSERT INTO `weapons` VALUES (115, '魔导绪论', '法器', '3', '38', '元素精通', '41', '止水息雷', '对处于水元素或雷元素影响下的敌人，造成的伤害提高12%。', '第十二版。结合时代发展加注了新内容，并修订了第十一版的错别字。', '[\"对处于水元素或雷元素影响下的敌人，造成的伤害提高12%。\",\"对处于水元素或雷元素影响下的敌人，造成的伤害提高15%。\",\"对处于水元素或雷元素影响下的敌人，造成的伤害提高18%。\",\"对处于水元素或雷元素影响下的敌人，造成的伤害提高21%。\",\"对处于水元素或雷元素影响下的敌人，造成的伤害提高24%。\"]', '本版《魔导绪论》在发行之初便引起了很大争议。<br>且不论《第七章：风元素运用原理》与常识相悖，<br>有关水元素与雷元素的基础性内容更是错漏甚多。<br>显然该书在定稿前并未经过同行审阅，遑论试行。<br>但无论如何，它还是当今最权威的魔导入门指南。<br>', '{\"Level\":[\"1\",\"5\",\"10\",\"15\",\"20\",\"20+\",\"25\",\"30\",\"35\",\"40\",\"40+\",\"45\",\"50\",\"50+\",\"55\",\"60\",\"60+\",\"65\",\"70\",\"70+\",\"75\",\"80\",\"80+\",\"85\",\"90\"],\"BaseAtk\":[\"38\",\"48\",\"61\",\"73\",\"86\",\"105\",\"117\",\"129\",\"140\",\"151\",\"171\",\"182\",\"193\",\"212\",\"223\",\"234\",\"253\",\"264\",\"274\",\"294\",\"304\",\"314\",\"334\",\"344\",\"354\"],\"none\":[\"41\",\"47\",\"56\",\"64\",\"72\",\"72\",\"80\",\"89\",\"97\",\"105\",\"105\",\"113\",\"122\",\"122\",\"130\",\"138\",\"138\",\"146\",\"154\",\"154\",\"163\",\"171\",\"171\",\"179\",\"187\"],\"AscensionMaterials\":[[],[],[],[],[],[{\"name\":\"2 \",\"urlid\":\"/img/upgrade/weapon/i_501\"},{\"name\":\"2 \",\"urlid\":\"/img/upgrade/material/i_61\"},{\"name\":\"1 \",\"urlid\":\"/img/upgrade/material/i_21\"},{\"name\":\"5000\",\"urlid\":\"/img/currency/i_2001\"}],[],[],[],[],[{\"name\":\"2 \",\"urlid\":\"/img/upgrade/weapon/i_502\"},{\"name\":\"8 \",\"urlid\":\"/img/upgrade/material/i_61\"},{\"name\":\"5 \",\"urlid\":\"/img/upgrade/material/i_21\"},{\"name\":\"10000\",\"urlid\":\"/img/currency/i_2001\"}],[],[],[{\"name\":\"4 \",\"urlid\":\"/img/upgrade/weapon/i_502\"},{\"name\":\"4 \",\"urlid\":\"/img/upgrade/material/i_62\"},{\"name\":\"4 \",\"urlid\":\"/img/upgrade/material/i_22\"},{\"name\":\"15000\",\"urlid\":\"/img/currency/i_2001\"}],[],[],[{\"name\":\"2 \",\"urlid\":\"/img/upgrade/weapon/i_503\"},{\"name\":\"8 \",\"urlid\":\"/img/upgrade/material/i_62\"},{\"name\":\"6 \",\"urlid\":\"/img/upgrade/material/i_22\"},{\